import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertProjectSchema, insertTimeEntrySchema, insertTaskTemplateSchema } from "@shared/schema";
import { setupAuth } from "./replitAuth";
import Stripe from "stripe";
import { 
  insertTodoSchema, insertNoteSchema, insertVoiceRecordingSchema,
  insertFlashcardDeckSchema, insertFlashcardSchema, insertHabitSchema, insertHabitLogSchema,
  insertFeedbackSchema
} from "@shared/schema";
import { initializeAchievements } from "./init-achievements";
import { EmailService } from "./email-service";

const stripe = process.env.STRIPE_SECRET_KEY ? new Stripe(process.env.STRIPE_SECRET_KEY) : null;

export async function registerRoutes(app: Express): Promise<Server> {
  // Temporary mock auth for demo - replace with proper auth later
  app.use('/api', (req, res, next) => {
    // Mock user session for demo
    req.user = { 
      claims: { sub: '927070657' }, // Free user by default
      isAuthenticated: () => true 
    };
    next();
  });

  // Auth routes
  app.get('/api/auth/user', async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Simple user switching for demo
  app.post('/api/switch-user', async (req: any, res) => {
    const { userId } = req.body;
    if (['927070657', '927070658', '927070659'].includes(userId)) {
      req.session.mockUserId = userId;
      res.json({ success: true });
    } else {
      res.status(400).json({ error: 'Invalid user ID' });
    }
  });

  // Stripe subscription routes
  app.post("/api/create-subscription", async (req: any, res) => {
    if (!stripe) {
      return res.status(503).json({ message: "Payment processing is not available" });
    }
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Check if user already has active subscription
      if (user.stripeSubscriptionId) {
        const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);
        if (subscription.status === 'active') {
          const latestInvoice = subscription.latest_invoice as any;
          return res.json({
            subscriptionId: subscription.id,
            clientSecret: latestInvoice?.payment_intent?.client_secret,
            status: subscription.status
          });
        }
      }

      // Create or get customer
      let customerId = user.stripeCustomerId;
      if (!customerId) {
        const customer = await stripe.customers.create({
          email: user.email || `user_${userId}@toolboxpro.com`,
          metadata: { userId }
        });
        customerId = customer.id;
        await storage.updateUserStripeInfo(userId, customerId, '');
      }

      // Create or get the product and price
      let priceId = process.env.STRIPE_PRICE_ID;

      if (!priceId) {
        // Create product if not exists
        const products = await stripe.products.list({ limit: 1 });
        let product = products.data.find(p => p.name === 'ToolboxPro Pro');

        if (!product) {
          product = await stripe.products.create({
            name: 'ToolboxPro Pro',
            description: 'Access to all premium productivity tools'
          });
        }

        // Create price if not exists
        const prices = await stripe.prices.list({ 
          product: product.id,
          active: true,
          limit: 1
        });

        let price = prices.data.find(p => p.unit_amount === 499 && p.recurring?.interval === 'month');

        if (!price) {
          price = await stripe.prices.create({
            currency: 'usd',
            unit_amount: 499,
            recurring: { interval: 'month' },
            product: product.id
          });
        }

        priceId = price.id;
      }

      // Create subscription
      const subscription = await stripe.subscriptions.create({
        customer: customerId,
        items: [{ price: priceId }],
        payment_behavior: 'default_incomplete',
        payment_settings: {
          save_default_payment_method: 'on_subscription'
        },
        expand: ['latest_invoice.payment_intent'],
      });

      // Update user with subscription
      await storage.updateUserStripeInfo(userId, customerId, subscription.id);

      // Get the client secret from the payment intent
      const latestInvoice = subscription.latest_invoice as any;
      const paymentIntent = latestInvoice?.payment_intent;

      res.json({
        subscriptionId: subscription.id,
        clientSecret: paymentIntent?.client_secret,
        status: subscription.status
      });
    } catch (error: any) {
      console.error('Subscription creation error:', error);
      res.status(500).json({ message: "Error creating subscription: " + error.message });
    }
  });

  // One-time tool purchases
  app.post("/api/purchase-tool", async (req: any, res) => {
    if (!stripe) {
      return res.status(503).json({ message: "Payment processing is not available" });
    }
    try {
      const { toolId, toolName, amount } = req.body;
      const userId = req.user.claims.sub;

      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100), // Convert to cents
        currency: "usd",
        metadata: {
          userId,
          toolId,
          toolName,
          type: 'one-time-purchase'
        },
        description: `One-time purchase: ${toolName}`
      });

      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      console.error('Payment intent creation error:', error);
      res.status(500).json({ message: "Error creating payment intent: " + error.message });
    }
  });

  // Cancel subscription
  app.post("/api/cancel-subscription", async (req: any, res) => {
    if (!stripe) {
      return res.status(503).json({ message: "Payment processing is not available" });
    }
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user?.stripeSubscriptionId) {
        return res.status(404).json({ message: "No active subscription found" });
      }

      const subscription = await stripe.subscriptions.update(user.stripeSubscriptionId, {
        cancel_at_period_end: true
      });

      res.json({ 
        message: "Subscription will be cancelled at the end of the billing period",
        subscription: {
          id: subscription.id,
          cancel_at_period_end: subscription.cancel_at_period_end,
          current_period_end: (subscription as any).current_period_end
        }
      });
    } catch (error: any) {
      console.error('Subscription cancellation error:', error);
      res.status(500).json({ message: "Error cancelling subscription: " + error.message });
    }
  });

  // Resume subscription
  app.post("/api/resume-subscription", async (req: any, res) => {
    if (!stripe) {
      return res.status(503).json({ message: "Payment processing is not available" });
    }
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user?.stripeSubscriptionId) {
        return res.status(404).json({ message: "No subscription found" });
      }

      const subscription = await stripe.subscriptions.update(user.stripeSubscriptionId, {
        cancel_at_period_end: false
      });

      res.json({ 
        message: "Subscription resumed successfully",
        subscription: {
          id: subscription.id,
          cancel_at_period_end: subscription.cancel_at_period_end,
          status: subscription.status
        }
      });
    } catch (error: any) {
      console.error('Subscription resume error:', error);
      res.status(500).json({ message: "Error resuming subscription: " + error.message });
    }
  });

  // Get subscription status
  app.get("/api/subscription-status", async (req: any, res) => {
    if (!stripe) {
      return res.json({ 
        status: 'none', 
        subscription: null,
        isPro: false 
      });
    }
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user?.stripeSubscriptionId) {
        return res.json({ 
          status: 'none', 
          subscription: null,
          isPro: false 
        });
      }

      const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);

      // Update user subscription status in database
      const isActive = subscription.status === 'active';
      await storage.updateUserSubscriptionStatus(userId, isActive ? 'pro' : 'free');

      res.json({
        status: subscription.status,
        isPro: isActive,
        subscription: {
          id: subscription.id,
          status: subscription.status,
          current_period_end: (subscription as any).current_period_end,
          cancel_at_period_end: subscription.cancel_at_period_end,
          canceled_at: (subscription as any).canceled_at
        }
      });
    } catch (error: any) {
      console.error('Subscription status error:', error);
      res.status(500).json({ message: "Error fetching subscription status: " + error.message });
    }
  });

  // Stripe webhook for handling payment events
  app.post("/api/stripe-webhook", async (req, res) => {
    const sig = req.headers['stripe-signature'];
    let event;

    try {
      // Only verify webhook signature if webhook secret is available
      if (process.env.STRIPE_WEBHOOK_SECRET && sig && stripe) {
        event = stripe.webhooks.constructEvent(req.body, sig, process.env.STRIPE_WEBHOOK_SECRET);
      } else {
        // For development/testing, parse the body directly
        event = req.body;
      }
    } catch (err: any) {
      console.error('Webhook signature verification failed:', err.message);
      return res.status(400).send(`Webhook Error: ${err.message}`);
    }

    // Handle the event
    switch (event.type) {
      case 'payment_intent.succeeded':
        const paymentIntent = event.data.object;
        if (paymentIntent.metadata.type === 'one-time-purchase') {
          // Handle successful one-time purchase
          console.log('One-time purchase successful:', paymentIntent.metadata);
        }
        break;

      case 'invoice.payment_succeeded':
        const invoice = event.data.object;
        // Handle successful subscription payment
        console.log('Subscription payment successful:', invoice.subscription);
        break;

      case 'customer.subscription.deleted':
        const subscription = event.data.object;
        // Handle subscription cancellation
        console.log('Subscription cancelled:', subscription.id);
        break;

      default:
        console.log(`Unhandled event type ${event.type}`);
    }

    res.json({ received: true });
  });

  // Revenue tracking endpoint
  app.post("/api/track-revenue", async (req, res) => {
    try {
      const { eventType, amount, metadata, userId } = req.body;

      const revenueEvent = await storage.createRevenueEvent({
        userId: userId || ((req as any).user?.claims?.sub),
        eventType,
        amount: amount || 0,
        metadata
      });

      res.json(revenueEvent);
    } catch (error: any) {
      console.error("Revenue tracking error:", error);
      res.status(500).json({ message: "Error tracking revenue: " + error.message });
    }
  });

  // Support developer endpoint
  app.post("/api/support-developer", async (req, res) => {
    if (!stripe) {
      return res.status(503).json({ message: "Payment processing is not available" });
    }
    try {
      const { type, amount, message } = req.body;
      const userId = ((req as any).user?.claims?.sub);

      // Create Stripe payment intent for support
      const paymentIntent = await stripe.paymentIntents.create({
        amount,
        currency: "usd",
        metadata: {
          type: "support",
          supportType: type,
          userId: userId || "anonymous"
        }
      });

      // Track the support intention
      await storage.createSupportTransaction({
        userId,
        supportType: type,
        amount,
        message: message || `Thank you for supporting ToolboxPro development!`
      });

      // Track revenue event
      await storage.createRevenueEvent({
        userId,
        eventType: "donation",
        amount,
        metadata: { supportType: type }
      });

      console.log("Created payment intent:", paymentIntent.id, "with client_secret:", paymentIntent.client_secret);
      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      console.error("Support processing error:", error);
      res.status(500).json({ message: "Error processing support: " + error.message });
    }
  });

  // Test statistics endpoint
  app.get("/api/test-statistics", async (req, res) => {
    try {
      const stats = await storage.getRevenueStats();
      res.json(stats);
    } catch (error: any) {
      console.error("Statistics error:", error);
      res.status(500).json({ message: "Error fetching statistics: " + error.message });
    }
  });

  // Achievement routes
  app.get("/api/achievements", async (req, res) => {
    try {
      const achievements = await storage.getAchievements();
      res.json(achievements);
    } catch (error: any) {
      console.error("Error fetching achievements:", error);
      res.status(500).json({ message: "Error fetching achievements: " + error.message });
    }
  });

  app.get("/api/user-achievements", async (req, res) => {
    try {
      const userId = ((req as any).user?.claims?.sub);
      const userAchievements = await storage.getUserAchievements(userId);
      res.json(userAchievements);
    } catch (error: any) {
      console.error("Error fetching user achievements:", error);
      res.status(500).json({ message: "Error fetching user achievements: " + error.message });
    }
  });

  app.get("/api/user-stats", async (req, res) => {
    try {
      const userId = ((req as any).user?.claims?.sub);
      const userStats = await storage.getUserStats(userId);
      res.json(userStats);
    } catch (error: any) {
      console.error("Error fetching user stats:", error);
      res.status(500).json({ message: "Error fetching user stats: " + error.message });
    }
  });

  app.post("/api/track-activity", async (req, res) => {
    try {
      const { activity, amount } = req.body;
      const userId = ((req as any).user?.claims?.sub);

      const updatedStats = await storage.incrementUserActivity(userId, activity, amount);
      const newAchievements = await storage.checkAndUnlockAchievements(userId);

      res.json({ 
        stats: updatedStats, 
        newAchievements: newAchievements.length > 0 ? newAchievements : null 
      });
    } catch (error: any) {
      console.error("Error tracking activity:", error);
      res.status(500).json({ message: "Error tracking activity: " + error.message });
    }
  });

  // Initialize achievements route
  app.post("/api/init-achievements", async (req, res) => {
    try {
      await initializeAchievements();
      res.json({ success: true, message: "Achievements initialized successfully" });
    } catch (error: any) {
      console.error("Error initializing achievements:", error);
      res.status(500).json({ message: "Error initializing achievements: " + error.message });
    }
  });

  // Update user test status
  app.post("/api/update-user-test-status", async (req, res) => {
    try {
      const { userId, testGroup, subscriptionTier } = req.body;

      let user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      if (testGroup) {
        user = await storage.updateUserTestGroup(userId, testGroup);
      }

      if (subscriptionTier) {
        user = await storage.updateUserSubscriptionTier(userId, subscriptionTier);
      }

      res.json(user);
    } catch (error: any) {
      console.error("User update error:", error);
      res.status(500).json({ message: "Error updating user: " + error.message });
    }
  });

  // Create test user
  app.post("/api/create-test-user", async (req, res) => {
    try {
      const { email, testGroup, subscriptionTier } = req.body;

      const user = await storage.upsertUser({
        id: `test_${Date.now()}`,
        email,
        testGroup,
        subscriptionTier,
        subscriptionStatus: subscriptionTier
      });

      res.json(user);
    } catch (error: any) {
      console.error("Test user creation error:", error);
      res.status(500).json({ message: "Error creating test user: " + error.message });
    }
  });

  // User routes
  app.get('/api/user', async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.sendStatus(401);
    }
    res.json(req.user);
  });

  // Todo routes
  app.get('/api/todos', async (req: any, res) => {
    const todos = await storage.getTodosByUserId(req.user.claims.sub);
    res.json(todos);
  });

  app.post('/api/todos', async (req: any, res) => {
    try {
      const todoData = insertTodoSchema.parse({ ...req.body, userId: req.user.claims.sub });
      const todo = await storage.createTodo(todoData);
      res.json(todo);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch('/api/todos/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const todo = await storage.updateTodo(id, req.body);
      res.json(todo);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/todos/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteTodo(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Notes routes
  app.get('/api/notes', async (req: any, res) => {
    const notes = await storage.getNotesByUserId(req.user.claims.sub);
    res.json(notes);
  });

  app.post('/api/notes', async (req: any, res) => {
    try {
      const noteData = insertNoteSchema.parse({ ...req.body, userId: req.user.claims.sub });
      const note = await storage.createNote(noteData);
      res.json(note);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch('/api/notes/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const note = await storage.updateNote(id, req.body);
      res.json(note);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/notes/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteNote(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Voice recordings routes
  app.get('/api/voice-recordings', async (req: any, res) => {
    const recordings = await storage.getVoiceRecordingsByUserId(req.user.claims.sub);
    res.json(recordings);
  });

  app.post('/api/voice-recordings', async (req: any, res) => {
    try {
      const recordingData = insertVoiceRecordingSchema.parse({ ...req.body, userId: req.user.claims.sub });
      const recording = await storage.createVoiceRecording(recordingData);
      res.json(recording);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch('/api/voice-recordings/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const updateData = req.body;
      const recording = await storage.updateVoiceRecording(id, updateData);
      res.json(recording);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/voice-recordings/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteVoiceRecording(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Flashcard decks routes
  app.get('/api/flashcard-decks', async (req: any, res) => {
    const decks = await storage.getFlashcardDecksByUserId(req.user.claims.sub);
    res.json(decks);
  });

  app.post('/api/flashcard-decks', async (req: any, res) => {
    try {
      const deckData = insertFlashcardDeckSchema.parse({ ...req.body, userId: req.user.claims.sub });
      const deck = await storage.createFlashcardDeck(deckData);
      res.json(deck);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/flashcard-decks/:id', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    try {
      const id = parseInt(req.params.id);
      await storage.deleteFlashcardDeck(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Flashcards routes
  app.get('/api/flashcards/:deckId', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    const deckId = parseInt(req.params.deckId);
    const flashcards = await storage.getFlashcardsByDeckId(deckId);
    res.json(flashcards);
  });

  app.post('/api/flashcards', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    try {
      const flashcardData = insertFlashcardSchema.parse(req.body);
      const flashcard = await storage.createFlashcard(flashcardData);
      res.json(flashcard);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch('/api/flashcards/:id', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    try {
      const id = parseInt(req.params.id);
      const flashcard = await storage.updateFlashcard(id, req.body);
      res.json(flashcard);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/flashcards/:id', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    try {
      const id = parseInt(req.params.id);
      await storage.deleteFlashcard(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Habits routes
  app.get('/api/habits', async (req: any, res) => {
    const habits = await storage.getHabitsByUserId(req.user.claims.sub);
    res.json(habits);
  });

  app.post('/api/habits', async (req: any, res) => {
    try {
      const habitData = insertHabitSchema.parse({ ...req.body, userId: req.user.claims.sub });
      const habit = await storage.createHabit(habitData);
      res.json(habit);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch('/api/habits/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const habit = await storage.updateHabit(id, req.body);
      res.json(habit);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/habits/:id', async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteHabit(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Habit logs routes
  app.get('/api/habit-logs/:habitId', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    const habitId = parseInt(req.params.habitId);
    const logs = await storage.getHabitLogsByHabitId(habitId);
    res.json(logs);
  });

  app.post('/api/habit-logs', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    try {
      const logData = insertHabitLogSchema.parse(req.body);
      const log = await storage.createHabitLog(logData);
      res.json(log);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.delete('/api/habit-logs/:id', async (req, res) => {
    if (!req.user) return res.sendStatus(401);
    try {
      const id = parseInt(req.params.id);
      await storage.deleteHabitLog(id);
      res.sendStatus(204);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Feedback routes
  app.get('/api/feedback', async (req: any, res) => {
    const feedback = await storage.getFeedbackByUserId(req.user.claims.sub);
    res.json(feedback);
  });

  app.post('/api/feedback', async (req: any, res) => {
    try {
      const feedbackData = insertFeedbackSchema.parse({ ...req.body, userId: req.user.claims.sub });
      const feedback = await storage.createFeedback(feedbackData);

      // Send email notifications asynchronously
      setImmediate(async () => {
        try {
          // Send confirmation email to user if email provided
          if (feedback.email) {
            await EmailService.sendFeedbackConfirmation(
              feedback.email, 
              feedback.type, 
              feedback.title
            );
          }

          // Send notification to admin
          await EmailService.sendAdminNotification(feedback);
        } catch (emailError) {
          console.error('Email notification failed:', emailError);
        }
      });

      res.json(feedback);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Admin feedback management routes
  app.get('/api/admin/feedback', async (req: any, res) => {
    try {
      // Only allow admin access (you can customize this check)
      const isLocalhost = req.hostname === 'localhost' || req.hostname === '127.0.0.1';
      const isReplitDev = req.hostname?.includes('replit.dev');

      if (!isLocalhost && !isReplitDev) {
        return res.status(403).json({ message: 'Admin access only' });
      }

      const allFeedback = await storage.getAllFeedback();
      res.json(allFeedback);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.patch('/api/admin/feedback/:id', async (req: any, res) => {
    try {
      // Only allow admin access
      const isLocalhost = req.hostname === 'localhost' || req.hostname === '127.0.0.1';
      const isReplitDev = req.hostname?.includes('replit.dev');

      if (!isLocalhost && !isReplitDev) {
        return res.status(403).json({ message: 'Admin access only' });
      }

      const id = parseInt(req.params.id);
      const { status, response } = req.body;

      // Get the feedback before updating to compare status
      const oldFeedback = await storage.getFeedbackById(id);
      if (!oldFeedback) {
        return res.status(404).json({ message: 'Feedback not found' });
      }

      // Update feedback
      const updatedFeedback = await storage.updateFeedbackStatus(id, status, response);

      // Send email notification if status changed and user has email
      if (oldFeedback.status !== status && updatedFeedback.email) {
        setImmediate(async () => {
          try {
            await EmailService.sendStatusUpdate(
              updatedFeedback.email!,
              updatedFeedback.title,
              oldFeedback.status || 'pending',
              status,
              response
            );

            // Send resolution follow-up if marked as resolved
            if (status === 'resolved') {
              await EmailService.sendResolutionFollowUp(
                updatedFeedback.email!,
                updatedFeedback.title,
                updatedFeedback.type
              );
            }
          } catch (emailError) {
            console.error('Status update email failed:', emailError);
          }
        });
      }

      res.json(updatedFeedback);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // IQ Test Routes
  app.get("/api/iq-test/sessions", async (req, res) => {
    try {
      const userId = ((req as any).user?.claims?.sub);
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const sessions = await storage.getIqTestSessionsByUserId(userId);
      res.json(sessions);
    } catch (error: any) {
      res.status(500).json({ message: "Error fetching IQ test sessions: " + error.message });
    }
  });

  app.post("/api/iq-test/sessions", async (req, res) => {
    try {
      const userId = ((req as any).user?.claims?.sub);
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const sessionData = {
        ...req.body,
        userId,
      };
      const session = await storage.createIqTestSession(sessionData);
      res.json(session);
    } catch (error: any) {
      res.status(400).json({ message: "Error saving IQ test session: " + error.message });
    }
  });

  app.get("/api/iq-test/stats", async (req, res) => {
    try {
      const userId = ((req as any).user?.claims?.sub);
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const stats = await storage.getIqTestStats(userId);
      res.json(stats);
    } catch (error: any) {
      res.status(500).json({ message: "Error fetching IQ test stats: " + error.message });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, todos, notes, voiceRecordings, flashcardDecks, flashcards, habits, habitLogs, feedback,
  revenueEvents, supportTransactions, achievements, userAchievements, userStats, projects, timeEntries, taskTemplates, iqTestSessions,
  type User, type UpsertUser, type Todo, type InsertTodo, type Note, type InsertNote,
  type VoiceRecording, type InsertVoiceRecording, type FlashcardDeck, type InsertFlashcardDeck,
  type Flashcard, type InsertFlashcard, type Habit, type InsertHabit, type HabitLog, type InsertHabitLog,
  type Feedback, type InsertFeedback, type RevenueEvent, type InsertRevenueEvent,
  type SupportTransaction, type InsertSupportTransaction, type Achievement, type InsertAchievement,
  type UserAchievement, type InsertUserAchievement, type UserStats, type InsertUserStats,
  type Project, type InsertProject, type TimeEntry, type InsertTimeEntry, type TaskTemplate, type InsertTaskTemplate,
  type IqTestSession, type InsertIqTestSession
} from "@shared/schema";
import { db } from "./db";
import { eq, inArray, desc, and, gte, lte } from "drizzle-orm";

export interface IStorage {
  // Users (for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserStripeInfo(id: string, customerId: string, subscriptionId: string): Promise<User>;
  updateUserSubscriptionStatus(id: string, status: string): Promise<User>;
  updateUserTestGroup(id: string, testGroup: string): Promise<User>;
  updateUserSubscriptionTier(id: string, tier: string): Promise<User>;
  addPurchasedTool(id: string, toolId: string): Promise<User>;

  // Revenue tracking
  createRevenueEvent(event: InsertRevenueEvent): Promise<RevenueEvent>;
  getRevenueStats(startDate?: Date, endDate?: Date): Promise<any>;

  // Support transactions
  createSupportTransaction(transaction: InsertSupportTransaction): Promise<SupportTransaction>;
  getSupportTransactions(userId?: string): Promise<SupportTransaction[]>;

  // Todos
  getTodosByUserId(userId: string): Promise<Todo[]>;
  createTodo(todo: InsertTodo): Promise<Todo>;
  updateTodo(id: number, todo: Partial<Todo>): Promise<Todo>;
  deleteTodo(id: number): Promise<void>;

  // Notes
  getNotesByUserId(userId: string): Promise<Note[]>;
  createNote(note: InsertNote): Promise<Note>;
  updateNote(id: number, note: Partial<Note>): Promise<Note>;
  deleteNote(id: number): Promise<void>;

  // Voice Recordings
  getVoiceRecordingsByUserId(userId: string): Promise<VoiceRecording[]>;
  createVoiceRecording(recording: InsertVoiceRecording): Promise<VoiceRecording>;
  updateVoiceRecording(id: number, updateData: Partial<VoiceRecording>): Promise<VoiceRecording>;
  deleteVoiceRecording(id: number): Promise<void>;

  // Flashcard Decks
  getFlashcardDecksByUserId(userId: string): Promise<FlashcardDeck[]>;
  createFlashcardDeck(deck: InsertFlashcardDeck): Promise<FlashcardDeck>;
  deleteFlashcardDeck(id: number): Promise<void>;

  // Flashcards
  getFlashcardsByDeckId(deckId: number): Promise<Flashcard[]>;
  createFlashcard(flashcard: InsertFlashcard): Promise<Flashcard>;
  updateFlashcard(id: number, flashcard: Partial<Flashcard>): Promise<Flashcard>;
  deleteFlashcard(id: number): Promise<void>;

  // Habits
  getHabitsByUserId(userId: string): Promise<Habit[]>;
  createHabit(habit: InsertHabit): Promise<Habit>;
  updateHabit(id: number, habit: Partial<Habit>): Promise<Habit>;
  deleteHabit(id: number): Promise<void>;

  // Habit Logs
  getHabitLogsByHabitId(habitId: number): Promise<HabitLog[]>;
  getHabitLogsByUserId(userId: string): Promise<HabitLog[]>;
  createHabitLog(log: InsertHabitLog): Promise<HabitLog>;
  deleteHabitLog(id: number): Promise<void>;

  // Feedback
  getFeedbackByUserId(userId: string): Promise<Feedback[]>;
  createFeedback(feedback: InsertFeedback): Promise<Feedback>;
  getAllFeedback(): Promise<Feedback[]>;
  getFeedbackById(id: number): Promise<Feedback | undefined>;
  updateFeedbackStatus(id: number, status: string, response?: string): Promise<Feedback>;

  // Achievements
  getAchievements(): Promise<Achievement[]>;
  createAchievement(achievement: InsertAchievement): Promise<Achievement>;
  getUserAchievements(userId: string): Promise<UserAchievement[]>;
  unlockAchievement(userId: string, achievementId: number): Promise<UserAchievement>;
  checkAndUnlockAchievements(userId: string): Promise<UserAchievement[]>;

  // User Stats
  getUserStats(userId: string): Promise<UserStats>;
  updateUserStats(userId: string, stats: Partial<UserStats>): Promise<UserStats>;
  incrementUserActivity(userId: string, activity: string, amount?: number): Promise<UserStats>;

  // Project Timer
  getProjectsByUserId(userId: string): Promise<Project[]>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: number, project: Partial<Project>): Promise<Project>;
  deleteProject(id: number): Promise<void>;

  getTimeEntriesByUserId(userId: string, filters?: any): Promise<TimeEntry[]>;
  getTimeEntriesByProjectId(projectId: number): Promise<TimeEntry[]>;
  createTimeEntry(entry: InsertTimeEntry): Promise<TimeEntry>;
  updateTimeEntry(id: number, entry: Partial<TimeEntry>): Promise<TimeEntry>;
  deleteTimeEntry(id: number): Promise<void>;
  getActiveTimeEntry(userId: string): Promise<TimeEntry | undefined>;

  getTaskTemplatesByUserId(userId: string): Promise<TaskTemplate[]>;
  createTaskTemplate(template: InsertTaskTemplate): Promise<TaskTemplate>;
  getRecentTasks(userId: string, limit?: number): Promise<TaskTemplate[]>;

  // IQ Test Sessions
  getIqTestSessionsByUserId(userId: string): Promise<IqTestSession[]>;
  createIqTestSession(session: InsertIqTestSession): Promise<IqTestSession>;
  getIqTestStats(userId: string): Promise<any>;
}

export class DatabaseStorage implements IStorage {
  // Users
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserStripeInfo(id: string, customerId: string, subscriptionId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        stripeCustomerId: customerId,
        stripeSubscriptionId: subscriptionId,
        subscriptionStatus: "pro"
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async updateUserSubscriptionStatus(id: string, status: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ subscriptionStatus: status })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async updateUserTestGroup(id: string, testGroup: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ 
        testGroup,
        updatedAt: new Date()
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async updateUserSubscriptionTier(id: string, tier: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ 
        subscriptionTier: tier,
        subscriptionStatus: tier,
        updatedAt: new Date()
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async addPurchasedTool(id: string, toolId: string): Promise<User> {
    const user = await this.getUser(id);
    if (!user) throw new Error("User not found");

    const purchasedTools = user.purchasedTools || [];
    if (!purchasedTools.includes(toolId)) {
      purchasedTools.push(toolId);
    }

    const [updatedUser] = await db
      .update(users)
      .set({ 
        purchasedTools,
        updatedAt: new Date()
      })
      .where(eq(users.id, id))
      .returning();
    return updatedUser;
  }

  // Revenue tracking
  async createRevenueEvent(insertEvent: InsertRevenueEvent): Promise<RevenueEvent> {
    const [event] = await db
      .insert(revenueEvents)
      .values(insertEvent)
      .returning();
    return event;
  }

  async getRevenueStats(startDate?: Date, endDate?: Date): Promise<any> {
    const events = await db.select().from(revenueEvents);
    const allUsers = await db.select().from(users);

    const totalRevenue = events.reduce((sum, event) => sum + (event.amount || 0), 0);
    const paidUsers = allUsers.filter(u => u.subscriptionStatus !== "free");

    return {
      totalRevenue: totalRevenue / 100,
      totalUsers: allUsers.length,
      conversionRate: allUsers.length > 0 ? (paidUsers.length / allUsers.length * 100) : 0,
      revenueToday: events
        .filter(e => e.createdAt && e.createdAt.toDateString() === new Date().toDateString())
        .reduce((sum, event) => sum + (event.amount || 0), 0) / 100,
      groupStats: this.calculateGroupStats(allUsers, events)
    };
  }

  private calculateGroupStats(users: User[], events: RevenueEvent[]) {
    const groups = ["control", "variant_a", "variant_b", "pricing_test"];
    const stats: any = {};

    groups.forEach(group => {
      const groupUsers = users.filter(u => u.testGroup === group);
      const groupEvents = events.filter(e => 
        groupUsers.some(u => u.id === e.userId)
      );
      const groupRevenue = groupEvents.reduce((sum, event) => sum + (event.amount || 0), 0);
      const paidUsers = groupUsers.filter(u => u.subscriptionStatus !== "free");

      stats[group] = {
        users: groupUsers.length,
        revenue: groupRevenue / 100,
        conversion: groupUsers.length > 0 ? (paidUsers.length / groupUsers.length * 100) : 0
      };
    });

    return stats;
  }

  // Support transactions
  async createSupportTransaction(insertTransaction: InsertSupportTransaction): Promise<SupportTransaction> {
    const [transaction] = await db
      .insert(supportTransactions)
      .values(insertTransaction)
      .returning();
    return transaction;
  }

  async getSupportTransactions(userId?: string): Promise<SupportTransaction[]> {
    if (userId) {
      return await db.select().from(supportTransactions).where(eq(supportTransactions.userId, userId));
    }
    return await db.select().from(supportTransactions);
  }

  // Todos
  async getTodosByUserId(userId: string): Promise<Todo[]> {
    return await db.select().from(todos).where(eq(todos.userId, userId));
  }

  async createTodo(insertTodo: InsertTodo): Promise<Todo> {
    const [todo] = await db
      .insert(todos)
      .values(insertTodo)
      .returning();
    return todo;
  }

  async updateTodo(id: number, todoUpdate: Partial<Todo>): Promise<Todo> {
    const [todo] = await db
      .update(todos)
      .set({ ...todoUpdate, updatedAt: new Date() })
      .where(eq(todos.id, id))
      .returning();
    return todo;
  }

  async deleteTodo(id: number): Promise<void> {
    await db.delete(todos).where(eq(todos.id, id));
  }

  // Notes
  async getNotesByUserId(userId: string): Promise<Note[]> {
    return await db.select().from(notes).where(eq(notes.userId, userId));
  }

  async createNote(insertNote: InsertNote): Promise<Note> {
    const [note] = await db
      .insert(notes)
      .values(insertNote)
      .returning();
    return note;
  }

  async updateNote(id: number, noteUpdate: Partial<Note>): Promise<Note> {
    const [note] = await db
      .update(notes)
      .set({ ...noteUpdate, updatedAt: new Date() })
      .where(eq(notes.id, id))
      .returning();
    return note;
  }

  async deleteNote(id: number): Promise<void> {
    await db.delete(notes).where(eq(notes.id, id));
  }

  // Voice Recordings
  async getVoiceRecordingsByUserId(userId: string): Promise<VoiceRecording[]> {
    return await db.select().from(voiceRecordings).where(eq(voiceRecordings.userId, userId));
  }

  async createVoiceRecording(insertRecording: InsertVoiceRecording): Promise<VoiceRecording> {
    const [recording] = await db
      .insert(voiceRecordings)
      .values(insertRecording)
      .returning();
    return recording;
  }

  async updateVoiceRecording(id: number, updateData: Partial<VoiceRecording>): Promise<VoiceRecording> {
    const [recording] = await db
      .update(voiceRecordings)
      .set({ ...updateData, updatedAt: new Date() })
      .where(eq(voiceRecordings.id, id))
      .returning();
    return recording;
  }

  async deleteVoiceRecording(id: number): Promise<void> {
    await db.delete(voiceRecordings).where(eq(voiceRecordings.id, id));
  }

  // Flashcard Decks
  async getFlashcardDecksByUserId(userId: string): Promise<FlashcardDeck[]> {
    return await db.select().from(flashcardDecks).where(eq(flashcardDecks.userId, userId));
  }

  async createFlashcardDeck(insertDeck: InsertFlashcardDeck): Promise<FlashcardDeck> {
    const [deck] = await db
      .insert(flashcardDecks)
      .values(insertDeck)
      .returning();
    return deck;
  }

  async deleteFlashcardDeck(id: number): Promise<void> {
    // Delete all flashcards in this deck first
    await db.delete(flashcards).where(eq(flashcards.deckId, id));
    await db.delete(flashcardDecks).where(eq(flashcardDecks.id, id));
  }

  // Flashcards
  async getFlashcardsByDeckId(deckId: number): Promise<Flashcard[]> {
    return await db.select().from(flashcards).where(eq(flashcards.deckId, deckId));
  }

  async createFlashcard(insertFlashcard: InsertFlashcard): Promise<Flashcard> {
    const [flashcard] = await db
      .insert(flashcards)
      .values(insertFlashcard)
      .returning();
    return flashcard;
  }

  async updateFlashcard(id: number, flashcardUpdate: Partial<Flashcard>): Promise<Flashcard> {
    const [flashcard] = await db
      .update(flashcards)
      .set(flashcardUpdate)
      .where(eq(flashcards.id, id))
      .returning();
    return flashcard;
  }

  async deleteFlashcard(id: number): Promise<void> {
    await db.delete(flashcards).where(eq(flashcards.id, id));
  }

  // Habits
  async getHabitsByUserId(userId: string): Promise<Habit[]> {
    return await db.select().from(habits).where(eq(habits.userId, userId));
  }

  async createHabit(insertHabit: InsertHabit): Promise<Habit> {
    const [habit] = await db
      .insert(habits)
      .values(insertHabit)
      .returning();
    return habit;
  }

  async updateHabit(id: number, habitUpdate: Partial<Habit>): Promise<Habit> {
    const [habit] = await db
      .update(habits)
      .set(habitUpdate)
      .where(eq(habits.id, id))
      .returning();
    return habit;
  }

  async deleteHabit(id: number): Promise<void> {
    // Delete all habit logs for this habit first
    await db.delete(habitLogs).where(eq(habitLogs.habitId, id));
    await db.delete(habits).where(eq(habits.id, id));
  }

  // Habit Logs
  async getHabitLogsByHabitId(habitId: number): Promise<HabitLog[]> {
    return await db.select().from(habitLogs).where(eq(habitLogs.habitId, habitId));
  }

  async getHabitLogsByUserId(userId: string): Promise<HabitLog[]> {
    const userHabits = await this.getHabitsByUserId(userId);
    const habitIds = userHabits.map(habit => habit.id);
    if (habitIds.length === 0) return [];

    return await db.select().from(habitLogs).where(inArray(habitLogs.habitId, habitIds));
  }

  async createHabitLog(insertLog: InsertHabitLog): Promise<HabitLog> {
    const [log] = await db
      .insert(habitLogs)
      .values(insertLog)
      .returning();
    return log;
  }

  async deleteHabitLog(id: number): Promise<void> {
    await db.delete(habitLogs).where(eq(habitLogs.id, id));
  }

  // Feedback
  async getFeedbackByUserId(userId: string): Promise<Feedback[]> {
    return await db.select().from(feedback).where(eq(feedback.userId, userId));
  }

  async createFeedback(insertFeedback: InsertFeedback): Promise<Feedback> {
    const [feedbackEntry] = await db
      .insert(feedback)
      .values(insertFeedback)
      .returning();
    return feedbackEntry;
  }

  async getAllFeedback(): Promise<Feedback[]> {
    return await db.select().from(feedback).orderBy(desc(feedback.createdAt));
  }

  async getFeedbackById(id: number): Promise<Feedback | undefined> {
    const [feedbackEntry] = await db.select().from(feedback).where(eq(feedback.id, id));
    return feedbackEntry;
  }

  async updateFeedbackStatus(id: number, status: string, response?: string): Promise<Feedback> {
    const updateData: any = { status, updatedAt: new Date() };
    if (response) {
      updateData.response = response;
    }

    const [updatedFeedback] = await db
      .update(feedback)
      .set(updateData)
      .where(eq(feedback.id, id))
      .returning();

    return updatedFeedback;
  }

  // Achievements
  async getAchievements(): Promise<Achievement[]> {
    return await db.select().from(achievements).where(eq(achievements.isActive, true));
  }

  async createAchievement(insertAchievement: InsertAchievement): Promise<Achievement> {
    const [achievement] = await db
      .insert(achievements)
      .values(insertAchievement)
      .returning();
    return achievement;
  }

  async getUserAchievements(userId: string): Promise<UserAchievement[]> {
    return await db
      .select()
      .from(userAchievements)
      .where(eq(userAchievements.userId, userId));
  }

  async unlockAchievement(userId: string, achievementId: number): Promise<UserAchievement> {
    const [userAchievement] = await db
      .insert(userAchievements)
      .values({
        userId,
        achievementId,
        isCompleted: true
      })
      .returning();
    return userAchievement;
  }

  async checkAndUnlockAchievements(userId: string): Promise<UserAchievement[]> {
    const userStats = await this.getUserStats(userId);
    const allAchievements = await this.getAchievements();
    const userAchievements = await this.getUserAchievements(userId);
    const unlockedAchievementIds = userAchievements.map(ua => ua.achievementId);
    const newUnlocks: UserAchievement[] = [];

    for (const achievement of allAchievements) {
      if (unlockedAchievementIds.includes(achievement.id)) continue;

      const requirement = achievement.requirement as any;
      let shouldUnlock = false;

      switch (achievement.key) {
        case 'first_todo':
          shouldUnlock = (userStats.todosCompleted || 0) >= 1;
          break;
        case 'todo_master':
          shouldUnlock = (userStats.todosCompleted || 0) >= 10;
          break;
        case 'note_taker':
          shouldUnlock = (userStats.notesCreated || 0) >= 5;
          break;
        case 'habit_builder':
          shouldUnlock = (userStats.habitsTracked || 0) >= 1;
          break;
        case 'consistency_king':
          shouldUnlock = (userStats.streakDays || 0) >= 7;
          break;
        case 'tool_explorer':
          shouldUnlock = (userStats.toolsUsed || []).length >= 5;
          break;
        case 'focus_master':
          shouldUnlock = (userStats.totalFocusTime || 0) >= 60;
          break;
      }

      if (shouldUnlock) {
        const newAchievement = await this.unlockAchievement(userId, achievement.id);
        newUnlocks.push(newAchievement);

        // Award points
        await this.incrementUserActivity(userId, 'points', achievement.points ?? 10);
      }
    }

    return newUnlocks;
  }

  // User Stats
  async getUserStats(userId: string): Promise<UserStats> {
    const [existingStats] = await db
      .select()
      .from(userStats)
      .where(eq(userStats.userId, userId));

    if (existingStats) {
      return existingStats;
    }

    // Create new stats for user
    const [newStats] = await db
      .insert(userStats)
      .values({ userId })
      .returning();
    return newStats;
  }

  async updateUserStats(userId: string, statsUpdate: Partial<UserStats>): Promise<UserStats> {
    const [updatedStats] = await db
      .update(userStats)
      .set({ ...statsUpdate, updatedAt: new Date() })
      .where(eq(userStats.userId, userId))
      .returning();
    return updatedStats;
  }

  async incrementUserActivity(userId: string, activity: string, amount: number = 1): Promise<UserStats> {
    const currentStats = await this.getUserStats(userId);
    const updates: Partial<UserStats> = {};

    switch (activity) {
      case 'todos_completed':
        updates.todosCompleted = (currentStats.todosCompleted || 0) + amount;
        updates.experiencePoints = (currentStats.experiencePoints || 0) + 5;
        break;
      case 'notes_created':
        updates.notesCreated = (currentStats.notesCreated || 0) + amount;
        updates.experiencePoints = (currentStats.experiencePoints || 0) + 3;
        break;
      case 'habits_tracked':
        updates.habitsTracked = (currentStats.habitsTracked || 0) + amount;
        updates.experiencePoints = (currentStats.experiencePoints || 0) + 2;
        break;
      case 'flashcards_studied':
        updates.flashcardsStudied = (currentStats.flashcardsStudied || 0) + amount;
        updates.experiencePoints = (currentStats.experiencePoints || 0) + 1;
        break;
      case 'pomodoro_sessions':
        updates.pomodoroSessions = (currentStats.pomodoroSessions || 0) + amount;
        updates.totalFocusTime = (currentStats.totalFocusTime || 0) + 25;
        updates.experiencePoints = (currentStats.experiencePoints || 0) + 10;
        break;
      case 'tool_used':
        const toolsUsed = currentStats.toolsUsed || [];
        if (!toolsUsed.includes(activity)) {
          updates.toolsUsed = [...toolsUsed, activity];
        }
        break;
      case 'points':
        updates.totalPoints = (currentStats.totalPoints || 0) + amount;
        break;
    }

    // Calculate level based on experience points
    const newXP = updates.experiencePoints || currentStats.experiencePoints || 0;
    const newLevel = Math.floor(newXP / 100) + 1;
    if (newLevel > (currentStats.level || 1)) {
      updates.level = newLevel;
    }

    updates.lastActiveDate = new Date();

    return await this.updateUserStats(userId, updates);
  }

  // Project Timer Methods
  async getProjectsByUserId(userId: string): Promise<Project[]> {
    return await db.select().from(projects).where(eq(projects.userId, userId)).orderBy(projects.updatedAt);
  }

  async createProject(insertProject: InsertProject): Promise<Project> {
    const [project] = await db.insert(projects).values(insertProject).returning();
    return project;
  }

  async updateProject(id: number, projectUpdate: Partial<Project>): Promise<Project> {
    const [project] = await db
      .update(projects)
      .set({ ...projectUpdate, updatedAt: new Date() })
      .where(eq(projects.id, id))
      .returning();
    return project;
  }

  async deleteProject(id: number): Promise<void> {
    await db.delete(projects).where(eq(projects.id, id));
  }

  async getTimeEntriesByUserId(userId: string, filters?: any): Promise<TimeEntry[]> {
    let whereConditions = [eq(timeEntries.userId, userId)];

    if (filters?.projectId) {
      whereConditions.push(eq(timeEntries.projectId, filters.projectId));
    }

    if (filters?.startDate && filters?.endDate) {
      whereConditions.push(
        gte(timeEntries.startTime, new Date(filters.startDate)),
        lte(timeEntries.startTime, new Date(filters.endDate))
      );
    }

    return await db.select().from(timeEntries)
      .where(and(...whereConditions))
      .orderBy(desc(timeEntries.startTime));
  }

  async getTimeEntriesByProjectId(projectId: number): Promise<TimeEntry[]> {
    return await db.select().from(timeEntries)
      .where(eq(timeEntries.projectId, projectId))
      .orderBy(desc(timeEntries.startTime));
  }

  async createTimeEntry(insertEntry: InsertTimeEntry): Promise<TimeEntry> {
    const [entry] = await db.insert(timeEntries).values(insertEntry).returning();
    return entry;
  }

  async updateTimeEntry(id: number, entryUpdate: Partial<TimeEntry>): Promise<TimeEntry> {
    const [entry] = await db
      .update(timeEntries)
      .set({ ...entryUpdate, updatedAt: new Date() })
      .where(eq(timeEntries.id, id))
      .returning();
    return entry;
  }

  async deleteTimeEntry(id: number): Promise<void> {
    await db.delete(timeEntries).where(eq(timeEntries.id, id));
  }

  async getActiveTimeEntry(userId: string): Promise<TimeEntry | undefined> {
    const [entry] = await db.select().from(timeEntries)
      .where(and(eq(timeEntries.userId, userId), eq(timeEntries.isActive, true)))
      .limit(1);
    return entry;
  }

  async getTaskTemplatesByUserId(userId: string): Promise<TaskTemplate[]> {
    return await db.select().from(taskTemplates)
      .where(eq(taskTemplates.userId, userId))
      .orderBy(desc(taskTemplates.usageCount));
  }

  async createTaskTemplate(insertTemplate: InsertTaskTemplate): Promise<TaskTemplate> {
    const [template] = await db.insert(taskTemplates).values(insertTemplate).returning();
    return template;
  }

  async getRecentTasks(userId: string, limit: number = 5): Promise<TaskTemplate[]> {
    return await db.select().from(taskTemplates)
      .where(eq(taskTemplates.userId, userId))
      .orderBy(desc(taskTemplates.usageCount))
      .limit(limit);
  }

  // IQ Test Sessions
  async getIqTestSessionsByUserId(userId: string): Promise<IqTestSession[]> {
    const sessions = await db
      .select()
      .from(iqTestSessions)
      .where(eq(iqTestSessions.userId, userId))
      .orderBy(desc(iqTestSessions.completedAt));
    return sessions;
  }

  async createIqTestSession(insertSession: InsertIqTestSession): Promise<IqTestSession> {
    const [session] = await db
      .insert(iqTestSessions)
      .values(insertSession)
      .returning();
    return session;
  }

  async getIqTestStats(userId: string): Promise<any> {
    const sessions = await this.getIqTestSessionsByUserId(userId);

    if (sessions.length === 0) {
      return {
        totalTests: 0,
        averageScore: 0,
        bestScore: 0,
        averageAccuracy: 0,
        levelProgression: {},
        categoryStrengths: {},
        recentTrend: []
      };
    }

    const totalTests = sessions.length;
    const averageScore = Math.round(sessions.reduce((sum, s) => sum + s.iqScore, 0) / totalTests);
    const bestScore = Math.max(...sessions.map(s => s.iqScore));
    const averageAccuracy = Math.round(sessions.reduce((sum, s) => sum + (s.correctAnswers / s.questionCount * 100), 0) / totalTests);

    // Level progression
    const levelProgression = sessions.reduce((acc, session) => {
      acc[session.testLevel] = (acc[session.testLevel] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    // Category strengths (average from recent sessions)
    const recentSessions = sessions.slice(0, 5);
    const categoryStrengths = recentSessions.reduce((acc, session) => {
      if (session.categoryScores) {
        const scores = session.categoryScores as Record<string, number>;
        Object.entries(scores).forEach(([category, score]) => {
          if (!acc[category]) acc[category] = [];
          acc[category].push(score);
        });
      }
      return acc;
    }, {} as Record<string, number[]>);

    // Average category scores
    const avgCategoryStrengths: Record<string, number> = {};
    Object.keys(categoryStrengths).forEach(category => {
      const scores = categoryStrengths[category];
      avgCategoryStrengths[category] = Math.round(scores.reduce((sum, score) => sum + score, 0) / scores.length);
    });

    // Recent trend (last 10 sessions)
    const recentTrend = sessions.slice(0, 10).reverse().map(session => ({
      date: session.completedAt,
      score: session.iqScore,
      level: session.testLevel
    }));

    return {
      totalTests,
      averageScore,
      bestScore,
      averageAccuracy,
      levelProgression,
      categoryStrengths: avgCategoryStrengths,
      recentTrend
    };
  }
}

export const storage = new DatabaseStorage();

