@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  /* Custom scrollbar styles */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-muted;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-border rounded-md;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-muted-foreground;
  }

  /* Focus styles */
  .focus-visible {
    @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
  }

  /* Text selection */
  ::selection {
    @apply bg-primary/20 text-primary-foreground;
  }

  /* Custom animations */
  @keyframes fade-in {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slide-in {
    from {
      transform: translateX(-100%);
    }
    to {
      transform: translateX(0);
    }
  }

  @keyframes pulse-ring {
    0% {
      transform: scale(0.8);
      opacity: 1;
    }
    100% {
      transform: scale(2);
      opacity: 0;
    }
  }

  .animate-fade-in {
    animation: fade-in 0.3s ease-out;
  }

  .animate-slide-in {
    animation: slide-in 0.3s ease-out;
  }

  .animate-pulse-ring {
    animation: pulse-ring 2s cubic-bezier(0.455, 0.03, 0.515, 0.955) infinite;
  }

  /* Utility classes for text truncation */
  .line-clamp-1 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 1;
  }

  .line-clamp-2 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  .line-clamp-3 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 3;
  }

  .line-clamp-4 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 4;
  }

  /* Glass morphism effect */
  .glass {
    @apply bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm border border-white/20 dark:border-slate-800/20;
  }

  /* Gradient text */
  .gradient-text {
    @apply bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent;
  }

  /* Custom shadows */
  .shadow-glow {
    box-shadow: 0 0 20px rgba(37, 99, 235, 0.3);
  }

  .shadow-glow-secondary {
    box-shadow: 0 0 20px rgba(5, 150, 105, 0.3);
  }

  /* Loading states */
  .skeleton {
    @apply animate-pulse bg-muted rounded;
  }

  /* PWA specific styles */
  @media (display-mode: standalone) {
    body {
      padding-top: env(safe-area-inset-top);
      padding-bottom: env(safe-area-inset-bottom);
      padding-left: env(safe-area-inset-left);
      padding-right: env(safe-area-inset-right);
    }
  }

  /* Print styles */
  @media print {
    .no-print {
      display: none !important;
    }

    body {
      @apply text-black bg-white;
    }

    .print-break-before {
      page-break-before: always;
    }

    .print-break-after {
      page-break-after: always;
    }

    .print-break-inside-avoid {
      page-break-inside: avoid;
    }
  }

  /* Accessibility improvements */
  @media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
    }
  }

  /* High contrast mode */
  @media (prefers-contrast: high) {
    :root {
      --border: 0 0% 0%;
      --ring: 0 0% 0%;
    }

    .dark {
      --border: 0 0% 100%;
      --ring: 0 0% 100%;
    }
  }

  /* Focus indicators for keyboard navigation */
  .keyboard-focus:focus-visible {
    @apply outline-2 outline-primary outline-offset-2;
  }

  /* Custom button variants */
  .btn-gradient {
    @apply bg-gradient-to-r from-primary to-secondary text-primary-foreground;
  }

  .btn-gradient:hover {
    @apply from-primary/90 to-secondary/90;
  }

  /* Status indicators */
  .status-online {
    @apply bg-green-500;
  }

  .status-offline {
    @apply bg-red-500;
  }

  .status-away {
    @apply bg-yellow-500;
  }

  /* Custom spacing for mobile */
  @media (max-width: 640px) {
    .mobile-spacing {
      @apply px-4 py-2;
    }

    .mobile-text-sm {
      @apply text-sm;
    }
  }
}

/* Component-specific styles */
@layer components {
  /* Custom card hover effects */
  .card-hover {
    @apply transition-all duration-300 hover:shadow-lg hover:scale-[1.02];
  }

  /* Custom input focus styles */
  .input-focus {
    @apply focus:ring-2 focus:ring-primary focus:border-primary transition-colors;
  }

  /* Custom navigation styles */
  .nav-link {
    @apply text-muted-foreground hover:text-foreground transition-colors duration-200;
  }

  .nav-link.active {
    @apply text-primary font-medium;
  }

  /* Custom table styles */
  .table-row {
    @apply border-b border-border hover:bg-muted/50 transition-colors;
  }

  /* Loading spinner */
  .spinner {
    @apply animate-spin rounded-full border-2 border-muted border-t-primary;
  }

  /* Custom checkbox styles */
  .checkbox-gradient {
    @apply bg-gradient-to-r from-primary to-secondary;
  }

  /* Tool card styles */
  .tool-card {
    @apply bg-card border border-border rounded-lg p-6 hover:shadow-md transition-all duration-200;
  }

  .tool-card:hover {
    @apply border-primary/20 shadow-glow;
  }

  /* Notification styles */
  .notification-success {
    @apply bg-green-50 border-green-200 text-green-800 dark:bg-green-900/20 dark:border-green-800 dark:text-green-300;
  }

  .notification-error {
    @apply bg-red-50 border-red-200 text-red-800 dark:bg-red-900/20 dark:border-red-800 dark:text-red-300;
  }

  .notification-warning {
    @apply bg-yellow-50 border-yellow-200 text-yellow-800 dark:bg-yellow-900/20 dark:border-yellow-800 dark:text-yellow-300;
  }

  .notification-info {
    @apply bg-blue-50 border-blue-200 text-blue-800 dark:bg-blue-900/20 dark:border-blue-800 dark:text-blue-300;
  }
}

