const DONATION_OPTIONS = [
  {
    id: "coffee",
    name: "Buy me a coffee",
    price: 3,
    description: "Support development with a coffee ‚òï",
    icon: Coffee,
    popular: false
  },
  {
    id: "snack",
    name: "Coffee & a snack",
    price: 5,
    description: "Keep me fueled with coffee and snacks üç™",
    icon: Cookie,
    popular: true
  },
  {
    id: "meal",
    name: "Buy me lunch",
    price: 8,
    description: "Support a proper meal while coding ü•™",
    icon: Heart,
    popular: false
  },
  {
