  const supportMutation = useMutation({
    mutationFn: async (support: { type: string; amount: number; message?: string }) => {
      const response = await apiRequest("POST", "/api/support-developer", support);
      return response.json();
    },
    onSuccess: (data: any) => {
      console.log("Donation response:", data);
      if (data.clientSecret) {
        onClose();
        window.location.href = `/checkout?client_secret=${data.clientSecret}`;
      } else {
        toast({
          title: "Error",
          description: "Failed to create payment session. Please try again.",
          variant: "destructive"
        });
      }
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to process donation. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleDonation = (option: typeof DONATION_OPTIONS[0]) => {
    supportMutation.mutate({
      type: option.id,
      amount: option.price * 100, // Convert to cents
      message: `Thank you for supporting ToolboxPro development!`
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-center text-2xl font-bold text-gray-900 dark:text-white">
