import { useQuery } from '@tanstack/react-query';
import { useSubscriptionStore } from '@/lib/stores';
import { useEffect } from 'react';

export interface User {
  id: number;
  email: string;
  username: string;
  subscriptionStatus: 'free' | 'pro' | 'lifetime';
  stripeCustomerId?: string;
  stripeSubscriptionId?: string;
}

export function useSubscription() {
  const {
    isSubscribed,
    subscriptionType,
    subscriptionExpiry,
    features,
    setSubscription,
    hasFeature,
  } = useSubscriptionStore();

  // Fetch user data to get subscription status
  const { data: user, isLoading } = useQuery<User>({
    queryKey: ['/api/user'],
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  // Update subscription store when user data changes
  useEffect(() => {
    if (user?.subscriptionStatus) {
      setSubscription(user.subscriptionStatus);
    }
  }, [user?.subscriptionStatus, setSubscription]);

  const checkFeatureAccess = (feature: string): { hasAccess: boolean; reason?: string } => {
    if (hasFeature(feature)) {
      return { hasAccess: true };
    }

    // Check if subscription has expired
    if (subscriptionExpiry && new Date() > subscriptionExpiry) {
      return {
        hasAccess: false,
        reason: 'Your subscription has expired. Please renew to continue using premium features.',
      };
    }

    return {
      hasAccess: false,
      reason: 'This feature requires a Pro subscription. Upgrade to unlock all premium features.',
    };
  };

  const getPremiumFeatures = () => [
    'voice-recorder',
    'flashcards',
    'pomodoro',
    'habit-tracker',
    'cloud-sync',
    'advanced-analytics',
    'export-data',
    'priority-support',
  ];

  const getFeatureList = () => {
    const allFeatures = {
      'todos': {
        name: 'To-Do Lists',
        description: 'Create and manage tasks with priorities and due dates',
        premium: false,
      },
      'notes': {
        name: 'Notes',
        description: 'Rich text note-taking with folders and search',
        premium: false,
      },
      'calculator': {
        name: 'Calculator',
        description: 'Basic and scientific calculator functions',
        premium: false,
      },
      'timer': {
        name: 'Timer',
        description: 'Countdown timers for various tasks',
        premium: false,
      },
      'unit-converter': {
        name: 'Unit Converter',
        description: 'Convert between different units of measurement',
        premium: false,
      },
      'world-clock': {
        name: 'World Clock',
        description: 'Track multiple time zones',
        premium: false,
      },
      'voice-recorder': {
        name: 'Voice Recorder',
        description: 'Record and manage audio memos',
        premium: true,
      },
      'flashcards': {
        name: 'Flashcards',
        description: 'Study with spaced repetition algorithm',
        premium: true,
      },
      'pomodoro': {
        name: 'Pomodoro Timer',
        description: 'Focus sessions with break intervals',
        premium: true,
      },
      'habit-tracker': {
        name: 'Habit Tracker',
        description: 'Build and track positive habits',
        premium: true,
      },
      'cloud-sync': {
        name: 'Cloud Sync',
        description: 'Sync data across all your devices',
        premium: true,
      },
    };

    return allFeatures;
  };

  const getSubscriptionInfo = () => {
    const info = {
      type: subscriptionType,
      isActive: isSubscribed,
      expiry: subscriptionExpiry,
      daysRemaining: subscriptionExpiry 
        ? Math.max(0, Math.ceil((subscriptionExpiry.getTime() - Date.now()) / (1000 * 60 * 60 * 24)))
        : null,
    };

    return info;
  };

  const canUpgrade = () => {
    return subscriptionType === 'free';
  };

  const needsRenewal = () => {
    if (!subscriptionExpiry || subscriptionType === 'lifetime') return false;

    const daysRemaining = Math.ceil((subscriptionExpiry.getTime() - Date.now()) / (1000 * 60 * 60 * 24));
    return daysRemaining <= 7; // Show renewal notice if 7 days or less remaining
  };

  const getUpgradeUrl = () => {
    return '/subscribe';
  };

  const getManageUrl = () => {
    // In a real app, this would link to Stripe customer portal
    return '/subscribe';
  };

  return {
    // Subscription status
    isSubscribed,
    subscriptionType,
    subscriptionExpiry,
    isLoading,
    user,

    // Feature access
    hasFeature,
    checkFeatureAccess,
    features,

    // Feature information
    getPremiumFeatures,
    getFeatureList,

    // Subscription management
    getSubscriptionInfo,
    canUpgrade,
    needsRenewal,
    getUpgradeUrl,
    getManageUrl,

    // Utility functions
    isPro: subscriptionType === 'pro',
    isLifetime: subscriptionType === 'lifetime',
    isFree: subscriptionType === 'free',
  };
}

// Hook for checking if a specific feature is available
export function useFeatureAccess(feature: string) {
  return {
    hasAccess: true, // All features are now free
    reason: 'available',
    isAvailable: true,
  };
}

// Hook for premium feature gate
export function usePremiumFeature(feature: string) {
  return {
    hasAccess: true, // All features are now free
    reason: 'available',
    requireUpgrade: () => {}, // No upgrade needed
    upgradeUrl: null,
  };
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

