client/src/lib/nlp-parser.ts.content:
import * as chrono from 'chrono-node';

export interface ParsedTask {
  title: string;
  description?: string;
  dueDate?: Date;
  reminderDate?: Date;
  priority: 'low' | 'medium' | 'high';
  urgency: 'low' | 'medium' | 'high';
  importance: 'low' | 'medium' | 'high';
  tags: string[];
  labels: string[];
  category?: string;
  estimatedDuration?: number;
  isRecurring: boolean;
  recurringPattern?: any;
  originalText: string;
}

export function parseNaturalLanguageTask(input: string): ParsedTask {
  const originalText = input;
  let cleanedInput = input;

  // Extract tags (#hashtag) and labels (@mention)
  const tags: string[] = [];
  const labels: string[] = [];

  // Extract hashtags
  const hashtagMatches = input.match(/#[\w]+/g);
  if (hashtagMatches) {
    tags.push(...hashtagMatches.map(tag => tag.substring(1)));
    cleanedInput = cleanedInput.replace(/#[\w]+/g, '').trim();
  }

  // Extract @mentions as labels
  const mentionMatches = input.match(/@[\w]+/g);
  if (mentionMatches) {
    labels.push(...mentionMatches.map(label => label.substring(1)));
    cleanedInput = cleanedInput.replace(/@[\w]+/g, '').trim();
  }

  // Parse dates and times
  const parsedDates = chrono.parse(input);
  let dueDate: Date | undefined;
  let reminderDate: Date | undefined;

  if (parsedDates.length > 0) {
    dueDate = parsedDates[0].start.date();

    // Check for reminder keywords
    if (input.toLowerCase().includes('remind') && parsedDates.length > 1) {
      reminderDate = parsedDates[1].start.date();
    } else if (input.toLowerCase().includes('remind')) {
      // Set reminder 30 minutes before due date
      reminderDate = new Date(dueDate.getTime() - 30 * 60 * 1000);
    }

    // Remove date text from cleaned input
    cleanedInput = cleanedInput.replace(parsedDates[0].text, '').trim();
  }

  // Determine priority from keywords
  let priority: 'low' | 'medium' | 'high' = 'medium';
  let urgency: 'low' | 'medium' | 'high' = 'medium';
  let importance: 'low' | 'medium' | 'high' = 'medium';

  const lowPriorityKeywords = ['maybe', 'sometime', 'when possible', 'low priority'];
  const highPriorityKeywords = ['urgent', 'asap', 'important', 'critical', 'high priority', 'emergency'];

  const inputLower = input.toLowerCase();

  if (highPriorityKeywords.some(keyword => inputLower.includes(keyword))) {
    priority = 'high';
    urgency = 'high';
    importance = 'high';
  } else if (lowPriorityKeywords.some(keyword => inputLower.includes(keyword))) {
    priority = 'low';
    urgency = 'low';
    importance = 'low';
  }

  // Extract duration estimates
  let estimatedDuration: number | undefined;
  const durationMatches = input.match(/(\d+)\s*(minute|min|hour|hr)s?/i);
  if (durationMatches) {
    const value = parseInt(durationMatches[1]);
    const unit = durationMatches[2].toLowerCase();
    estimatedDuration = unit.startsWith('hour') || unit.startsWith('hr') ? value * 60 : value;
    cleanedInput = cleanedInput.replace(durationMatches[0], '').trim();
  }

  // Detect recurring patterns
  let isRecurring = false;
  let recurringPattern: any = undefined;

  const recurringKeywords = ['daily', 'weekly', 'monthly', 'yearly', 'every'];
  if (recurringKeywords.some(keyword => inputLower.includes(keyword))) {
    isRecurring = true;

    if (inputLower.includes('daily') || inputLower.includes('every day')) {
      recurringPattern = { type: 'daily', interval: 1 };
    } else if (inputLower.includes('weekly') || inputLower.includes('every week')) {
      recurringPattern = { type: 'weekly', interval: 1 };
    } else if (inputLower.includes('monthly') || inputLower.includes('every month')) {
      recurringPattern = { type: 'monthly', interval: 1 };
    } else if (inputLower.includes('every other')) {
      if (inputLower.includes('day')) {
        recurringPattern = { type: 'daily', interval: 2 };
      } else if (inputLower.includes('week')) {
        recurringPattern = { type: 'weekly', interval: 2 };
      }
    }
  }

  // Determine category based on keywords
  let category: string | undefined;
  const workKeywords = ['meeting', 'call', 'project', 'work', 'office', 'client'];
  const personalKeywords = ['home', 'family', 'personal', 'buy', 'grocery'];
  const healthKeywords = ['doctor', 'gym', 'exercise', 'health', 'appointment'];

  if (workKeywords.some(keyword => inputLower.includes(keyword))) {
    category = 'work';
  } else if (personalKeywords.some(keyword => inputLower.includes(keyword))) {
    category = 'personal';
  } else if (healthKeywords.some(keyword => inputLower.includes(keyword))) {
    category = 'health';
  }

  // Clean up the title
  let title = cleanedInput;

  // Remove priority keywords from title
  const allPriorityKeywords = [...lowPriorityKeywords, ...highPriorityKeywords];
  allPriorityKeywords.forEach(keyword => {
    title = title.replace(new RegExp(keyword, 'gi'), '').trim();
  });

  // Remove recurring keywords from title
  recurringKeywords.forEach(keyword => {
    title = title.replace(new RegExp(`\\b${keyword}\\b`, 'gi'), '').trim();
  });

  // Clean up extra spaces
  title = title.replace(/\s+/g, ' ').trim();

  return {
    title: title || 'New Task',
    priority,
    urgency,
    importance,
    tags,
    labels,
    category,
    dueDate,
    reminderDate,
    estimatedDuration,
    isRecurring,
    recurringPattern,
    originalText
  };
}

export function formatRecurringPattern(pattern: any): string {
  if (!pattern) return '';

  const { type, interval } = pattern;

  if (interval === 1) {
    return `Every ${type.slice(0, -2)}`;
  } else {
    return `Every ${interval} ${type}`;
  }
}

client/src/lib/queryClient.ts.content:
import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey[0] as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

client/src/lib/recurring-patterns.ts.content:
export interface RecurringPattern {
  type: 'daily' | 'weekly' | 'monthly' | 'yearly' | 'custom';
  interval: number;
  daysOfWeek?: number[]; // 0 = Sunday, 1 = Monday, etc.
  dayOfMonth?: number;
  weekOfMonth?: number; // 1 = first week, 2 = second week, etc.
  monthOfYear?: number; // 1 = January, 2 = February, etc.
  endType?: 'never' | 'after' | 'on';
  endCount?: number;
  endDate?: Date;
}

export function parseRecurringText(text: string): RecurringPattern | null {
  const lowerText = text.toLowerCase();

  // Daily patterns
  if (lowerText.includes('daily') || lowerText.includes('every day')) {
    return { type: 'daily', interval: 1 };
  }

  if (lowerText.includes('every other day')) {
    return { type: 'daily', interval: 2 };
  }

  // Weekly patterns
  if (lowerText.includes('weekly') || lowerText.includes('every week')) {
    return { type: 'weekly', interval: 1 };
  }

  if (lowerText.includes('every other week') || lowerText.includes('biweekly')) {
    return { type: 'weekly', interval: 2 };
  }

  // Specific day patterns
  const dayPatterns = {
    'monday': 1, 'tuesday': 2, 'wednesday': 3, 'thursday': 4,
    'friday': 5, 'saturday': 6, 'sunday': 0
  };

  for (const [day, dayNum] of Object.entries(dayPatterns)) {
    if (lowerText.includes(`every ${day}`)) {
      return { type: 'weekly', interval: 1, daysOfWeek: [dayNum] };
    }
    if (lowerText.includes(`every other ${day}`)) {
      return { type: 'weekly', interval: 2, daysOfWeek: [dayNum] };
    }
  }

  // Monthly patterns
  if (lowerText.includes('monthly') || lowerText.includes('every month')) {
    return { type: 'monthly', interval: 1 };
  }

  if (lowerText.includes('first monday of the month') || lowerText.includes('first monday of each month')) {
    return { type: 'monthly', interval: 1, daysOfWeek: [1], weekOfMonth: 1 };
  }

  if (lowerText.includes('last friday of the month') || lowerText.includes('last friday of each month')) {
    return { type: 'monthly', interval: 1, daysOfWeek: [5], weekOfMonth: -1 };
  }

  // After X completions
  const afterMatch = lowerText.match(/after (\d+) completion/);
  if (afterMatch) {
    return { type: 'custom', interval: 1, endType: 'after', endCount: parseInt(afterMatch[1]) };
  }

  return null;
}

export function formatRecurringPattern(pattern: RecurringPattern): string {
  if (!pattern) return '';

  const { type, interval, daysOfWeek, weekOfMonth, endType, endCount } = pattern;

  let result = '';

  switch (type) {
    case 'daily':
      result = interval === 1 ? 'Daily' : `Every ${interval} days`;
      break;

    case 'weekly':
      if (daysOfWeek && daysOfWeek.length === 1) {
        const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const dayName = days[daysOfWeek[0]];
        result = interval === 1 ? `Every ${dayName}` : `Every ${interval} weeks on ${dayName}`;
      } else {
        result = interval === 1 ? 'Weekly' : `Every ${interval} weeks`;
      }
      break;

    case 'monthly':
      if (daysOfWeek && weekOfMonth) {
        const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const dayName = days[daysOfWeek[0]];
        const weekNames = ['', 'First', 'Second', 'Third', 'Fourth'];
        const weekName = weekOfMonth === -1 ? 'Last' : weekNames[weekOfMonth];
        result = `${weekName} ${dayName} of each month`;
      } else {
        result = interval === 1 ? 'Monthly' : `Every ${interval} months`;
      }
      break;

    case 'yearly':
      result = interval === 1 ? 'Yearly' : `Every ${interval} years`;
      break;

    case 'custom':
      if (endType === 'after' && endCount) {
        result = `After ${endCount} completions`;
      } else {
        result = 'Custom pattern';
      }
      break;
  }

  if (endType === 'after' && endCount && type !== 'custom') {
    result += ` (${endCount} times)`;
  }

  return result;
}

export function getNextDueDate(pattern: RecurringPattern, lastDate: Date): Date {
  const next = new Date(lastDate);

  switch (pattern.type) {
    case 'daily':
      next.setDate(next.getDate() + pattern.interval);
      break;

    case 'weekly':
      next.setDate(next.getDate() + (7 * pattern.interval));
      break;

    case 'monthly':
      next.setMonth(next.getMonth() + pattern.interval);
      break;

    case 'yearly':
      next.setFullYear(next.getFullYear() + pattern.interval);
      break;
  }

  return next;
}

