// Client-side storage utilities for PWA offline functionality

export interface StorageItem {
  key: string;
  value: any;
  timestamp: number;
  expiresAt?: number;
}

class LocalStorageManager {
  private prefix = 'toolboxpro_';

  set(key: string, value: any, expiresInMinutes?: number): void {
    try {
      const item: StorageItem = {
        key,
        value,
        timestamp: Date.now(),
        expiresAt: expiresInMinutes ? Date.now() + (expiresInMinutes * 60 * 1000) : undefined,
      };

      localStorage.setItem(this.prefix + key, JSON.stringify(item));
    } catch (error) {
      console.error('Failed to store item:', error);
    }
  }

  get<T>(key: string): T | null {
    try {
      const stored = localStorage.getItem(this.prefix + key);
      if (!stored) return null;

      const item: StorageItem = JSON.parse(stored);

      // Check if item has expired
      if (item.expiresAt && Date.now() > item.expiresAt) {
        this.remove(key);
        return null;
      }

      return item.value as T;
    } catch (error) {
      console.error('Failed to retrieve item:', error);
      return null;
    }
  }

  remove(key: string): void {
    try {
      localStorage.removeItem(this.prefix + key);
    } catch (error) {
      console.error('Failed to remove item:', error);
    }
  }

  clear(): void {
    try {
      const keys = Object.keys(localStorage).filter(key => key.startsWith(this.prefix));
      keys.forEach(key => localStorage.removeItem(key));
    } catch (error) {
      console.error('Failed to clear storage:', error);
    }
  }

  // Get all keys with the prefix
  getAllKeys(): string[] {
    try {
      return Object.keys(localStorage)
        .filter(key => key.startsWith(this.prefix))
        .map(key => key.replace(this.prefix, ''));
    } catch (error) {
      console.error('Failed to get keys:', error);
      return [];
    }
  }

  // Check if storage is available
  isAvailable(): boolean {
    try {
      const test = '__storage_test__';
      localStorage.setItem(test, 'test');
      localStorage.removeItem(test);
      return true;
    } catch {
      return false;
    }
  }

  // Get storage usage info
  getUsageInfo(): { used: number; available: number; percentage: number } {
    try {
      let used = 0;
      for (let key in localStorage) {
        if (localStorage.hasOwnProperty(key) && key.startsWith(this.prefix)) {
          used += localStorage[key].length;
        }
      }

      // Estimate available storage (5MB is typical limit)
      const available = 5 * 1024 * 1024; // 5MB in bytes
      const percentage = (used / available) * 100;

      return { used, available, percentage };
    } catch (error) {
      console.error('Failed to get usage info:', error);
      return { used: 0, available: 0, percentage: 0 };
    }
  }
}

class CacheManager {
  private prefix = 'toolboxpro_cache_';

  async set(key: string, data: any, expiresInMinutes: number = 60): Promise<void> {
    try {
      const cacheItem = {
        data,
        timestamp: Date.now(),
        expiresAt: Date.now() + (expiresInMinutes * 60 * 1000),
      };

      localStorage.setItem(this.prefix + key, JSON.stringify(cacheItem));
    } catch (error) {
      console.error('Failed to cache data:', error);
    }
  }

  async get<T>(key: string): Promise<T | null> {
    try {
      const cached = localStorage.getItem(this.prefix + key);
      if (!cached) return null;

      const cacheItem = JSON.parse(cached);

      // Check if cache has expired
      if (Date.now() > cacheItem.expiresAt) {
        this.remove(key);
        return null;
      }

      return cacheItem.data as T;
    } catch (error) {
      console.error('Failed to retrieve cached data:', error);
      return null;
    }
  }

  async remove(key: string): Promise<void> {
    try {
      localStorage.removeItem(this.prefix + key);
    } catch (error) {
      console.error('Failed to remove cached data:', error);
    }
  }

  async clear(): Promise<void> {
    try {
      const keys = Object.keys(localStorage).filter(key => key.startsWith(this.prefix));
      keys.forEach(key => localStorage.removeItem(key));
    } catch (error) {
      console.error('Failed to clear cache:', error);
    }
  }

  // Clean expired cache entries
  async cleanExpired(): Promise<void> {
    try {
      const keys = Object.keys(localStorage).filter(key => key.startsWith(this.prefix));
      const now = Date.now();

      keys.forEach(key => {
        try {
          const cached = localStorage.getItem(key);
          if (cached) {
            const cacheItem = JSON.parse(cached);
            if (now > cacheItem.expiresAt) {
              localStorage.removeItem(key);
            }
          }
        } catch {
          // If we can't parse it, remove it
          localStorage.removeItem(key);
        }
      });
    } catch (error) {
      console.error('Failed to clean expired cache:', error);
    }
  }
}

class OfflineQueueManager {
  private queueKey = 'offline_queue';

  addRequest(request: {
    url: string;
    method: string;
    data?: any;
    timestamp: number;
  }): void {
    try {
      const queue = this.getQueue();
      queue.push(request);
      localStorage.setItem(this.queueKey, JSON.stringify(queue));
    } catch (error) {
      console.error('Failed to add request to offline queue:', error);
    }
  }

  getQueue(): any[] {
    try {
      const stored = localStorage.getItem(this.queueKey);
      return stored ? JSON.parse(stored) : [];
    } catch (error) {
      console.error('Failed to get offline queue:', error);
      return [];
    }
  }

  removeRequest(index: number): void {
    try {
      const queue = this.getQueue();
      queue.splice(index, 1);
      localStorage.setItem(this.queueKey, JSON.stringify(queue));
    } catch (error) {
      console.error('Failed to remove request from offline queue:', error);
    }
  }

  clearQueue(): void {
    try {
      localStorage.removeItem(this.queueKey);
    } catch (error) {
      console.error('Failed to clear offline queue:', error);
    }
  }

  async processQueue(): Promise<void> {
    const queue = this.getQueue();

    for (let i = queue.length - 1; i >= 0; i--) {
      const request = queue[i];

      try {
        const response = await fetch(request.url, {
          method: request.method,
          headers: request.data ? { 'Content-Type': 'application/json' } : {},
          body: request.data ? JSON.stringify(request.data) : undefined,
          credentials: 'include',
        });

        if (response.ok) {
          this.removeRequest(i);
        }
      } catch (error) {
        console.error('Failed to process queued request:', error);
        // Keep the request in queue for next attempt
      }
    }
  }
}

// Export singleton instances
export const storage = new LocalStorageManager();
export const cache = new CacheManager();
export const offlineQueue = new OfflineQueueManager();

// Utility functions
export const isOnline = (): boolean => navigator.onLine;

export const waitForOnline = (): Promise<void> => {
  return new Promise((resolve) => {
    if (isOnline()) {
      resolve();
    } else {
      const handleOnline = () => {
        window.removeEventListener('online', handleOnline);
        resolve();
      };
      window.addEventListener('online', handleOnline);
    }
  });
};

// Initialize offline queue processing when online
window.addEventListener('online', () => {
  offlineQueue.processQueue();
});

// Clean expired cache on startup
cache.cleanExpired();

client/src/lib/stores.ts.content:
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

// Theme Store
interface ThemeState {
  theme: 'light' | 'dark' | 'system';
  setTheme: (theme: 'light' | 'dark' | 'system') => void;
}

export const useThemeStore = create<ThemeState>()(
  persist(
    (set) => ({
      theme: 'system',
      setTheme: (theme) => {
        set({ theme });

        // Apply theme to document
        const root = window.document.documentElement;
        root.classList.remove('light', 'dark');

        if (theme === 'system') {
          const systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
          root.classList.add(systemTheme);
        } else {
          root.classList.add(theme);
        }
      },
    }),
    {
      name: 'theme-storage',
    }
  )
);

// User Preferences Store
interface UserPreferencesState {
  notifications: boolean;
  soundEnabled: boolean;
  defaultPomodoroTime: number;
  defaultBreakTime: number;
  weekStartsOn: 0 | 1; // 0 = Sunday, 1 = Monday
  timeFormat: '12h' | '24h';
  setNotifications: (enabled: boolean) => void;
  setSoundEnabled: (enabled: boolean) => void;
  setDefaultPomodoroTime: (minutes: number) => void;
  setDefaultBreakTime: (minutes: number) => void;
  setWeekStartsOn: (day: 0 | 1) => void;
  setTimeFormat: (format: '12h' | '24h') => void;
}

export const useUserPreferencesStore = create<UserPreferencesState>()(
  persist(
    (set) => ({
      notifications: true,
      soundEnabled: true,
      defaultPomodoroTime: 25,
      defaultBreakTime: 5,
      weekStartsOn: 1,
      timeFormat: '24h',
      setNotifications: (notifications) => set({ notifications }),
      setSoundEnabled: (soundEnabled) => set({ soundEnabled }),
      setDefaultPomodoroTime: (defaultPomodoroTime) => set({ defaultPomodoroTime }),
      setDefaultBreakTime: (defaultBreakTime) => set({ defaultBreakTime }),
      setWeekStartsOn: (weekStartsOn) => set({ weekStartsOn }),
      setTimeFormat: (timeFormat) => set({ timeFormat }),
    }),
    {
      name: 'user-preferences-storage',
    }
  )
);

// App State Store
interface AppStateState {
  isOnline: boolean;
  lastSync: Date | null;
  pendingSyncs: number;
  isLoading: boolean;
  setOnlineStatus: (isOnline: boolean) => void;
  setLastSync: (date: Date) => void;
  setPendingSyncs: (count: number) => void;
  setIsLoading: (isLoading: boolean) => void;
}

export const useAppStateStore = create<AppStateState>((set) => ({
  isOnline: navigator.onLine,
  lastSync: null,
  pendingSyncs: 0,
  isLoading: false,
  setOnlineStatus: (isOnline) => set({ isOnline }),
  setLastSync: (lastSync) => set({ lastSync }),
  setPendingSyncs: (pendingSyncs) => set({ pendingSyncs }),
  setIsLoading: (isLoading) => set({ isLoading }),
}));

// Tool State Store (for managing tool-specific state)
interface ToolStateState {
  lastUsedTool: string | null;
  toolPreferences: Record<string, any>;
  setLastUsedTool: (toolId: string) => void;
  setToolPreference: (toolId: string, preferences: any) => void;
  getToolPreference: (toolId: string) => any;
}

export const useToolStateStore = create<ToolStateState>()(
  persist(
    (set, get) => ({
      lastUsedTool: null,
      toolPreferences: {},
      setLastUsedTool: (lastUsedTool) => set({ lastUsedTool }),
      setToolPreference: (toolId, preferences) => {
        const { toolPreferences } = get();
        set({
          toolPreferences: {
            ...toolPreferences,
            [toolId]: { ...toolPreferences[toolId], ...preferences },
          },
        });
      },
      getToolPreference: (toolId) => {
        const { toolPreferences } = get();
        return toolPreferences[toolId] || {};
      },
    }),
    {
      name: 'tool-state-storage',
    }
  )
);

// Subscription Store
interface SubscriptionState {
  isSubscribed: boolean;
  subscriptionType: 'free' | 'pro' | 'lifetime';
  subscriptionExpiry: Date | null;
  features: string[];
  setSubscription: (type: 'free' | 'pro' | 'lifetime', expiry?: Date) => void;
  hasFeature: (feature: string) => boolean;
}

export const useSubscriptionStore = create<SubscriptionState>()(
  persist(
    (set, get) => ({
      isSubscribed: false,
      subscriptionType: 'free',
      subscriptionExpiry: null,
      features: ['todos', 'notes', 'calculator', 'timer', 'unit-converter', 'world-clock'],
      setSubscription: (subscriptionType, subscriptionExpiry) => {
        const features = subscriptionType === 'free' 
          ? ['todos', 'notes', 'calculator', 'timer', 'unit-converter', 'world-clock']
          : [
              'todos', 'notes', 'calculator', 'timer', 'unit-converter', 'world-clock',
              'voice-recorder', 'flashcards', 'pomodoro', 'habit-tracker', 'cloud-sync'
            ];

        set({
          isSubscribed: subscriptionType !== 'free',
          subscriptionType,
          subscriptionExpiry: subscriptionExpiry || null,
          features,
        });
      },
      hasFeature: (feature) => {
        const { features } = get();
        return features.includes(feature);
      },
    }),
    {
      name: 'subscription-storage',
    }
  )
);

// Initialize online status listener
window.addEventListener('online', () => {
  useAppStateStore.getState().setOnlineStatus(true);
});

window.addEventListener('offline', () => {
  useAppStateStore.getState().setOnlineStatus(false);
});

// Initialize theme on app start
const { theme, setTheme } = useThemeStore.getState();
setTheme(theme);

// Listen for system theme changes
if (theme === 'system') {
  window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', (e) => {
    if (useThemeStore.getState().theme === 'system') {
      const root = window.document.documentElement;
      root.classList.remove('light', 'dark');
      root.classList.add(e.matches ? 'dark' : 'light');
    }
  });
}

