import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation } from "@tanstack/react-query";
import { Elements, PaymentElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { Link } from "wouter";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Navbar from "@/components/navbar";
import { Coffee, Heart, Star, ArrowLeft } from "lucide-react";

if (!import.meta.env.VITE_STRIPE_PUBLIC_KEY) {
  throw new Error('Missing required Stripe key: VITE_STRIPE_PUBLIC_KEY');
}
const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY);

const supportSchema = z.object({
  amount: z.number().min(1, "Amount must be at least $1").max(1000, "Amount cannot exceed $1000"),
  message: z.string().optional(),
});

type SupportFormData = z.infer<typeof supportSchema>;

const presetAmounts = [
  { value: 3, label: "$3", description: "Buy me a coffee", icon: Coffee, popular: false },
  { value: 5, label: "$5", description: "Support development", icon: Heart, popular: true },
  { value: 10, label: "$10", description: "Fuel innovation", icon: Star, popular: false },
];

const CheckoutForm = ({ amount, message }: { amount: number; message: string }) => {
  const stripe = useStripe();
  const elements = useElements();
  const { toast } = useToast();
  const [isProcessing, setIsProcessing] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setIsProcessing(true);

    const { error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        return_url: `${window.location.origin}/support-success`,
      },
    });

    setIsProcessing(false);

    if (error) {
      toast({
        title: "Payment Failed",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-4">
        <div className="text-center">
          <h3 className="text-lg font-semibold">Complete Your Support</h3>
          <p className="text-muted-foreground">
            Supporting ${amount} for ToolboxPro development
          </p>
          {message && (
            <div className="mt-2 p-3 bg-muted rounded-lg">
              <p className="text-sm italic">"{message}"</p>
            </div>
          )}
        </div>

        <Separator />

        <PaymentElement />
      </div>

      <Button 
        type="submit" 
        className="w-full" 
        disabled={!stripe || isProcessing}
        size="lg"
      >
        {isProcessing ? "Processing..." : `Support with $${amount}`}
      </Button>

      <p className="text-xs text-center text-muted-foreground">
        Powered by Stripe. Your payment information is secure and encrypted.
      </p>
    </form>
  );
};

export default function SupportPage() {
  const [selectedAmount, setSelectedAmount] = useState<number | null>(null);
  const [customAmount, setCustomAmount] = useState("");
  const [message, setMessage] = useState("");
  const [clientSecret, setClientSecret] = useState("");
  const [step, setStep] = useState<"select" | "payment">("select");
  const { toast } = useToast();

  const form = useForm<SupportFormData>({
    resolver: zodResolver(supportSchema),
    defaultValues: {
      amount: 5,
      message: "",
    },
  });

  const createPaymentMutation = useMutation({
    mutationFn: async (data: { amount: number; message: string }) => {
      const response = await apiRequest("POST", "/api/support-developer", {
        type: "donation",
        amount: data.amount * 100, // Convert to cents
        message: data.message,
      });
      return response.json();
    },
    onSuccess: (data) => {
      setClientSecret(data.clientSecret);
      setStep("payment");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Failed to create payment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleAmountSelect = (amount: number) => {
    setSelectedAmount(amount);
    setCustomAmount("");
    form.setValue("amount", amount);
  };

  const handleCustomAmountChange = (value: string) => {
    setCustomAmount(value);
    setSelectedAmount(null);
    const numValue = parseFloat(value);
    if (!isNaN(numValue)) {
      form.setValue("amount", numValue);
    }
  };

  const handleContinue = () => {
    const amount = selectedAmount || parseFloat(customAmount);
    if (amount && amount >= 1) {
      createPaymentMutation.mutate({
        amount,
        message,
      });
    }
  };

  const finalAmount = selectedAmount || parseFloat(customAmount) || 0;

  if (step === "payment" && clientSecret) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800">
        <Navbar onBack={() => setStep("select")} title="Complete Payment" />

        <div className="container mx-auto px-4 py-8">
          <div className="max-w-md mx-auto">
            <Card>
              <CardContent className="p-6">
                <Elements stripe={stripePromise} options={{ clientSecret }}>
                  <CheckoutForm amount={finalAmount} message={message} />
                </Elements>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800">
      <Navbar onBack={() => window.history.back()} title="Support ToolboxPro" />

      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          {/* Header */}
          <div className="text-center mb-8">
            <div className="mb-4">
              <Coffee className="h-16 w-16 mx-auto text-orange-500" />
            </div>
            <h1 className="text-3xl font-bold mb-2">Support ToolboxPro</h1>
            <p className="text-lg text-muted-foreground mb-4">
              Help keep ToolboxPro free and continuously improving
            </p>
            <div className="bg-white/50 dark:bg-gray-800/50 rounded-lg p-4 backdrop-blur-sm">
              <p className="text-sm text-muted-foreground">
                ToolboxPro is completely free to use. Your support helps cover development costs 
                and allows us to add new features and tools for everyone.
              </p>
            </div>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>Choose Your Support Amount</CardTitle>
              <CardDescription>
                Select a preset amount or enter your own preferred amount
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Preset Amounts */}
              <div className="space-y-3">
                <label className="text-sm font-medium">Popular amounts</label>
                <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                  {presetAmounts.map((preset) => {
                    const IconComponent = preset.icon;
                    return (
                      <Card
                        key={preset.value}
                        className={`cursor-pointer transition-all hover:shadow-md ${
                          selectedAmount === preset.value
                            ? "ring-2 ring-primary border-primary"
                            : "hover:border-primary/50"
                        }`}
                        onClick={() => handleAmountSelect(preset.value)}
                      >
                        <CardContent className="p-4 text-center">
                          <div className="flex items-center justify-center space-x-2 mb-2">
                            <IconComponent className="h-5 w-5" />
                            <span className="text-lg font-semibold">{preset.label}</span>
                            {preset.popular && (
                              <Badge variant="secondary" className="text-xs">Popular</Badge>
                            )}
                          </div>
                          <p className="text-sm text-muted-foreground">{preset.description}</p>
                        </CardContent>
                      </Card>
                    );
                  })}
                </div>
              </div>

              <Separator />

              {/* Custom Amount */}
              <div className="space-y-3">
                <label className="text-sm font-medium">Or enter custom amount</label>
                <div className="relative">
                  <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground">$</span>
                  <Input
                    type="number"
                    placeholder="0.00"
                    min="1"
                    max="1000"
                    step="0.01"
                    value={customAmount}
                    onChange={(e) => handleCustomAmountChange(e.target.value)}
                    className="pl-8"
                  />
                </div>
              </div>

              <Separator />

              {/* Optional Message */}
              <div className="space-y-3">
                <label className="text-sm font-medium">Optional message (public)</label>
                <Textarea
                  placeholder="Say something nice... (optional)"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  maxLength={200}
                />
                <p className="text-xs text-muted-foreground">
                  {message.length}/200 characters
                </p>
              </div>

              {/* Continue Button */}
              <Button
                onClick={handleContinue}
                disabled={!finalAmount || finalAmount < 1 || createPaymentMutation.isPending}
                size="lg"
                className="w-full"
              >
                {createPaymentMutation.isPending
                  ? "Setting up payment..."
                  : `Continue with $${finalAmount.toFixed(2)}`
                }
              </Button>

              <div className="text-center">
                <Link href="/">
                  <Button variant="ghost" size="sm">
                    <ArrowLeft className="h-4 w-4 mr-2" />
                    Back to ToolboxPro
                  </Button>
                </Link>
              </div>
            </CardContent>
          </Card>

          {/* Features List */}
          <div className="mt-8 text-center">
            <h3 className="text-lg font-semibold mb-4">Your support helps us:</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-muted-foreground">
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                <span>Keep all tools completely free</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                <span>Add new productivity features</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
                <span>Improve performance and reliability</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-orange-500 rounded-full"></div>
                <span>Cover hosting and development costs</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/tools/brainstormin.content:
import React, { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Lightbulb, 
  Plus, 
  Trash2, 
  Star, 
  Download, 
  Shuffle,
  Target,
  Users,
  Clock,
  Brain
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Idea {
  id: string;
  text: string;
  category: string;
  priority: 'low' | 'medium' | 'high';
  starred: boolean;
  timestamp: Date;
  tags: string[];
}

interface BrainstormSession {
  id: string;
  title: string;
  topic: string;
  ideas: Idea[];
  createdAt: Date;
  mode: 'individual' | 'group';
}

const brainstormingPrompts = [
  "What if we removed all limitations?",
  "How might we solve this differently?",
  "What would a child suggest?",
  "What's the opposite approach?",
  "How can we combine existing solutions?",
  "What if we had unlimited resources?",
  "What would happen if we did nothing?",
  "How can we make this 10x better?",
  "What assumptions are we making?",
  "What would our competitors do?"
];

const ideaCategories = [
  'Feature', 'Improvement', 'Problem', 'Solution', 
  'Marketing', 'Business', 'Technical', 'Creative', 'Other'
];

export default function BrainstormingTool() {
  const [sessions, setSessions] = useState<BrainstormSession[]>([]);
  const [currentSession, setCurrentSession] = useState<BrainstormSession | null>(null);
  const [newSessionTitle, setNewSessionTitle] = useState('');
  const [newSessionTopic, setNewSessionTopic] = useState('');
  const [newIdea, setNewIdea] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('Other');
  const [currentPrompt, setCurrentPrompt] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [timerActive, setTimerActive] = useState(false);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes default
  const { toast } = useToast();

  useEffect(() => {
    const savedSessions = localStorage.getItem('brainstorming_sessions');
    if (savedSessions) {
      const parsed = JSON.parse(savedSessions);
      setSessions(parsed.map((s: any) => ({
        ...s,
        createdAt: new Date(s.createdAt),
        ideas: s.ideas.map((i: any) => ({
          ...i,
          timestamp: new Date(i.timestamp)
        }))
      })));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('brainstorming_sessions', JSON.stringify(sessions));
  }, [sessions]);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (timerActive && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            setTimerActive(false);
            toast({
              title: "Time's Up!",
              description: "Your brainstorming session has ended. Great work!"
            });
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [timerActive, timeLeft, toast]);

  const createSession = () => {
    if (!newSessionTitle.trim() || !newSessionTopic.trim()) return;

    const session: BrainstormSession = {
      id: Date.now().toString(),
      title: newSessionTitle,
      topic: newSessionTopic,
      ideas: [],
      createdAt: new Date(),
      mode: 'individual'
    };

    setSessions(prev => [session, ...prev]);
    setCurrentSession(session);
    setNewSessionTitle('');
    setNewSessionTopic('');
    generateRandomPrompt();

    toast({
      title: "Session Created",
      description: `Started brainstorming session: ${newSessionTitle}`
    });
  };

  const addIdea = () => {
    if (!newIdea.trim() || !currentSession) return;

    const idea: Idea = {
      id: Date.now().toString(),
      text: newIdea,
      category: selectedCategory,
      priority: 'medium',
      starred: false,
      timestamp: new Date(),
      tags: []
    };

    const updatedSession = {
      ...currentSession,
      ideas: [...currentSession.ideas, idea]
    };

    setCurrentSession(updatedSession);
    setSessions(prev => prev.map(s => s.id === currentSession.id ? updatedSession : s));
    setNewIdea('');

    toast({
      title: "Idea Added",
      description: "Your idea has been captured!"
    });
  };

  const toggleStar = (ideaId: string) => {
    if (!currentSession) return;

    const updatedSession = {
      ...currentSession,
      ideas: currentSession.ideas.map(idea => 
        idea.id === ideaId ? { ...idea, starred: !idea.starred } : idea
      )
    };

    setCurrentSession(updatedSession);
    setSessions(prev => prev.map(s => s.id === currentSession.id ? updatedSession : s));
  };

  const deleteIdea = (ideaId: string) => {
    if (!currentSession) return;

    const updatedSession = {
      ...currentSession,
      ideas: currentSession.ideas.filter(idea => idea.id !== ideaId)
    };

    setCurrentSession(updatedSession);
    setSessions(prev => prev.map(s => s.id === currentSession.id ? updatedSession : s));
  };

  const updatePriority = (ideaId: string, priority: 'low' | 'medium' | 'high') => {
    if (!currentSession) return;

    const updatedSession = {
      ...currentSession,
      ideas: currentSession.ideas.map(idea => 
        idea.id === ideaId ? { ...idea, priority } : idea
      )
    };

    setCurrentSession(updatedSession);
    setSessions(prev => prev.map(s => s.id === currentSession.id ? updatedSession : s));
  };

  const generateRandomPrompt = () => {
    const randomPrompt = brainstormingPrompts[Math.floor(Math.random() * brainstormingPrompts.length)];
    setCurrentPrompt(randomPrompt);
  };

  const exportSession = () => {
    if (!currentSession) return;

    const exportData = {
      title: currentSession.title,
      topic: currentSession.topic,
      ideas: currentSession.ideas.map(idea => ({
        text: idea.text,
        category: idea.category,
        priority: idea.priority,
        starred: idea.starred
      }))
    };

    const dataStr = JSON.stringify(exportData, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `${currentSession.title.replace(/\s+/g, '_')}_ideas.json`;
    link.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Session Exported",
      description: "Your brainstorming session has been downloaded as JSON."
    });
  };

  const startTimer = (minutes: number) => {
    setTimeLeft(minutes * 60);
    setTimerActive(true);
    toast({
      title: "Timer Started",
      description: `${minutes}-minute brainstorming session started!`
    });
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'destructive';
      case 'medium': return 'default';
      case 'low': return 'secondary';
      default: return 'secondary';
    }
  };

  const filteredIdeas = currentSession?.ideas.filter(idea => 
    filterCategory === 'all' || idea.category === filterCategory
  ) || [];

  return (
    <div className="space-y-6">
      {!currentSession ? (
        /* Session Creation */
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Brain className="w-5 h-5" />
              Start New Brainstorming Session
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="title">Session Title</Label>
              <Input
                id="title"
                value={newSessionTitle}
                onChange={(e) => setNewSessionTitle(e.target.value)}
                placeholder="e.g., Product Feature Ideas"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="topic">Topic/Challenge</Label>
              <Textarea
                id="topic"
                value={newSessionTopic}
                onChange={(e) => setNewSessionTopic(e.target.value)}
                placeholder="Describe what you want to brainstorm about..."
                rows={3}
              />
            </div>
            <Button onClick={createSession} disabled={!newSessionTitle.trim() || !newSessionTopic.trim()}>
              <Plus className="w-4 h-4 mr-2" />
              Start Session
            </Button>
          </CardContent>
        </Card>
      ) : (
        /* Active Session */
        <div className="space-y-6">
          {/* Session Header */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <div>
                  <h2 className="text-xl font-bold">{currentSession.title}</h2>
                  <p className="text-sm text-muted-foreground mt-1">{currentSession.topic}</p>
                </div>
                <div className="flex gap-2">
                  {timerActive && (
                    <Badge variant="default" className="px-3 py-1">
                      <Clock className="w-4 h-4 mr-1" />
                      {formatTime(timeLeft)}
                    </Badge>
                  )}
                  <Button variant="outline" size="sm" onClick={exportSession}>
                    <Download className="w-4 h-4" />
                  </Button>
                  <Button variant="outline" size="sm" onClick={() => setCurrentSession(null)}>
                    Back to Sessions
                  </Button>
                </div>
              </CardTitle>
            </CardHeader>
          </Card>

          <Tabs defaultValue="ideate" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="ideate">Ideate</TabsTrigger>
              <TabsTrigger value="organize">Organize</TabsTrigger>
              <TabsTrigger value="timer">Timer</TabsTrigger>
            </TabsList>

            <TabsContent value="ideate" className="space-y-4">
              {/* Prompt Card */}
              {currentPrompt && (
                <Card className="bg-primary/5 border-primary/20">
                  <CardContent className="pt-6">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <Lightbulb className="w-5 h-5 text-primary" />
                        <span className="font-medium">Thinking Prompt:</span>
                      </div>
                      <Button variant="ghost" size="sm" onClick={generateRandomPrompt}>
                        <Shuffle className="w-4 h-4" />
                      </Button>
                    </div>
                    <p className="mt-2 text-primary font-medium">{currentPrompt}</p>
                  </CardContent>
                </Card>
              )}

              {/* Add Idea */}
              <Card>
                <CardContent className="pt-6 space-y-4">
                  <div className="flex gap-2">
                    <Textarea
                      value={newIdea}
                      onChange={(e) => setNewIdea(e.target.value)}
                      placeholder="Capture your idea here..."
                      rows={2}
                      className="flex-1"
                      onKeyPress={(e) => {
                        if (e.key === 'Enter' && !e.shiftKey) {
                          e.preventDefault();
                          addIdea();
                        }
                      }}
                    />
                    <div className="space-y-2">
                      <select
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                        className="px-3 py-2 border rounded-md"
                      >
                        {ideaCategories.map(cat => (
                          <option key={cat} value={cat}>{cat}</option>
                        ))}
                      </select>
                      <Button onClick={addIdea} disabled={!newIdea.trim()}>
                        <Plus className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Ideas List */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>Ideas ({currentSession.ideas.length})</span>
                    <select
                      value={filterCategory}
                      onChange={(e) => setFilterCategory(e.target.value)}
                      className="px-3 py-1 border rounded text-sm"
                    >
                      <option value="all">All Categories</option>
                      {ideaCategories.map(cat => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {filteredIdeas.length === 0 ? (
                      <p className="text-center text-muted-foreground py-8">
                        No ideas yet. Start capturing your thoughts above!
                      </p>
                    ) : (
                      filteredIdeas.map(idea => (
                        <div key={idea.id} className="p-4 border rounded-lg space-y-2">
                          <div className="flex items-start justify-between">
                            <p className="flex-1">{idea.text}</p>
                            <div className="flex gap-1 ml-2">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => toggleStar(idea.id)}
                                className={idea.starred ? "text-yellow-500" : ""}
                              >
                                <Star className={`w-4 h-4 ${idea.starred ? 'fill-current' : ''}`} />
                              </Button>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => deleteIdea(idea.id)}
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>
                          <div className="flex items-center gap-2">
                            <Badge variant="outline">{idea.category}</Badge>
                            <select
                              value={idea.priority}
                              onChange={(e) => updatePriority(idea.id, e.target.value as any)}
                              className="px-2 py-1 text-xs border rounded"
                            >
                              <option value="low">Low</option>
                              <option value="medium">Medium</option>
                              <option value="high">High</option>
                            </select>
                            <Badge variant={getPriorityColor(idea.priority)}>
                              {idea.priority}
                            </Badge>
                          </div>
                        </div>
                      ))
                    )}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="organize" className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {['high', 'medium', 'low'].map(priority => (
                  <Card key={priority}>
                    <CardHeader>
                      <CardTitle className="capitalize flex items-center gap-2">
                        <Target className="w-4 h-4" />
                        {priority} Priority
                        <Badge variant={getPriorityColor(priority)}>
                          {currentSession.ideas.filter(i => i.priority === priority).length}
                        </Badge>
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-2">
                        {currentSession.ideas
                          .filter(idea => idea.priority === priority)
                          .map(idea => (
                            <div key={idea.id} className="p-2 bg-muted rounded text-sm">
                              <p>{idea.text}</p>
                              <div className="flex items-center gap-1 mt-1">
                                <Badge variant="outline" className="text-xs">
                                  {idea.category}
                                </Badge>
                                {idea.starred && (
                                  <Star className="w-3 h-3 text-yellow-500 fill-current" />
                                )}
                              </div>
                            </div>
                          ))}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </TabsContent>

            <TabsContent value="timer" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Clock className="w-5 h-5" />
                    Brainstorming Timer
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  {timerActive ? (
                    <div className="text-center space-y-4">
                      <div className="text-4xl font-mono font-bold">
                        {formatTime(timeLeft)}
                      </div>
                      <Button 
                        onClick={() => setTimerActive(false)}
                        variant="outline"
                      >
                        Stop Timer
                      </Button>
                    </div>
                  ) : (
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-2">
                      {[5, 10, 15, 25].map(minutes => (
                        <Button
                          key={minutes}
                          onClick={() => startTimer(minutes)}
                          variant="outline"
                        >
                          {minutes} min
                        </Button>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      )}

      {/* Previous Sessions */}
      {!currentSession && sessions.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Previous Sessions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {sessions.map(session => (
                <div
                  key={session.id}
                  className="p-4 border rounded-lg cursor-pointer hover:bg-muted/50"
                  onClick={() => setCurrentSession(session)}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <h4 className="font-semibold">{session.title}</h4>
                      <p className="text-sm text-muted-foreground">{session.topic}</p>
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge variant="outline">
                        {session.ideas.length} ideas
                      </Badge>
                      <Badge variant="secondary">
                        {session.ideas.filter(i => i.starred).length} starred
                      </Badge>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/tools/calculator.t.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Trash2 } from "lucide-react";

type CalculatorMode = "basic" | "scientific";

export default function CalculatorTool() {
  const [display, setDisplay] = useState("0");
  const [equation, setEquation] = useState("");
  const [history, setHistory] = useState<string[]>([]);
  const [mode, setMode] = useState<CalculatorMode>("basic");

  const clearDisplay = () => {
    setDisplay("0");
    setEquation("");
  };

  const clearHistory = () => {
    setHistory([]);
  };

  const deleteLast = () => {
    if (display.length > 1 && display !== "0") {
      setDisplay(display.slice(0, -1));
    } else {
      setDisplay("0");
    }
  };

  const inputNumber = (num: string) => {
    if (display === "0" || display === "Error") {
      setDisplay(num);
    } else {
      setDisplay(display + num);
    }
  };

  const inputOperator = (op: string) => {
    if (display !== "Error") {
      const newEquation = equation + display + " " + op + " ";
      setEquation(newEquation);
      setDisplay("0");
    }
  };

  const inputFunction = (func: string) => {
    try {
      const num = parseFloat(display);
      let result: number;

      switch (func) {
        case "sin":
          result = Math.sin(num * Math.PI / 180);
          break;
        case "cos":
          result = Math.cos(num * Math.PI / 180);
          break;
        case "tan":
          result = Math.tan(num * Math.PI / 180);
          break;
        case "log":
          result = Math.log10(num);
          break;
        case "ln":
          result = Math.log(num);
          break;
        case "sqrt":
          result = Math.sqrt(num);
          break;
        case "square":
          result = num * num;
          break;
        case "1/x":
          result = 1 / num;
          break;
        default:
          return;
      }

      if (isNaN(result) || !isFinite(result)) {
        setDisplay("Error");
      } else {
        setDisplay(result.toString());
      }
    } catch {
      setDisplay("Error");
    }
  };

  const calculate = () => {
    try {
      const fullEquation = equation + display;
      if (fullEquation.trim() === display) return;

      // Replace display operators with JavaScript operators
      const jsEquation = fullEquation
        .replace(/√ó/g, "*")
        .replace(/√∑/g, "/")
        .replace(/\^/g, "**");

      const result = eval(jsEquation);

      if (isNaN(result) || !isFinite(result)) {
        setDisplay("Error");
      } else {
        const resultStr = result.toString();
        setDisplay(resultStr);
        setHistory([...history, `${fullEquation} = ${resultStr}`]);
      }
      setEquation("");
    } catch {
      setDisplay("Error");
      setEquation("");
    }
  };

  const inputDecimal = () => {
    if (!display.includes(".") && display !== "Error") {
      setDisplay(display + ".");
    }
  };

  const basicButtons = [
    { label: "C", onClick: clearDisplay, className: "bg-red-100 text-red-700 hover:bg-red-200" },
    { label: "‚å´", onClick: deleteLast, className: "bg-gray-100 text-gray-700 hover:bg-gray-200" },
    { label: "√∑", onClick: () => inputOperator("√∑"), className: "bg-blue-100 text-blue-700 hover:bg-blue-200" },
    { label: "√ó", onClick: () => inputOperator("√ó"), className: "bg-blue-100 text-blue-700 hover:bg-blue-200" },

    { label: "7", onClick: () => inputNumber("7") },
    { label: "8", onClick: () => inputNumber("8") },
    { label: "9", onClick: () => inputNumber("9") },
    { label: "-", onClick: () => inputOperator("-"), className: "bg-blue-100 text-blue-700 hover:bg-blue-200" },

    { label: "4", onClick: () => inputNumber("4") },
    { label: "5", onClick: () => inputNumber("5") },
    { label: "6", onClick: () => inputNumber("6") },
    { label: "+", onClick: () => inputOperator("+"), className: "bg-blue-100 text-blue-700 hover:bg-blue-200" },

    { label: "1", onClick: () => inputNumber("1") },
    { label: "2", onClick: () => inputNumber("2") },
    { label: "3", onClick: () => inputNumber("3") },
    { label: "=", onClick: calculate, className: "bg-green-100 text-green-700 hover:bg-green-200 row-span-2" },

    { label: "0", onClick: () => inputNumber("0"), className: "col-span-2" },
    { label: ".", onClick: inputDecimal },
  ];

  const scientificButtons = [
    { label: "sin", onClick: () => inputFunction("sin") },
    { label: "cos", onClick: () => inputFunction("cos") },
    { label: "tan", onClick: () => inputFunction("tan") },
    { label: "log", onClick: () => inputFunction("log") },

    { label: "ln", onClick: () => inputFunction("ln") },
    { label: "‚àö", onClick: () => inputFunction("sqrt") },
    { label: "x¬≤", onClick: () => inputFunction("square") },
    { label: "1/x", onClick: () => inputFunction("1/x") },

    { label: "œÄ", onClick: () => inputNumber(Math.PI.toString()) },
    { label: "e", onClick: () => inputNumber(Math.E.toString()) },
    { label: "(", onClick: () => inputOperator("(") },
    { label: ")", onClick: () => inputOperator(")") },
  ];

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Calculator</h2>
          <p className="text-slate-600">Perform calculations with basic and scientific functions</p>
        </div>
      </div>

      <div className="grid lg:grid-cols-3 gap-6">
        {/* Calculator */}
        <div className="lg:col-span-2">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>Calculator</CardTitle>
                <div className="flex space-x-2">
                  <Button
                    variant={mode === "basic" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setMode("basic")}
                  >
                    Basic
                  </Button>
                  <Button
                    variant={mode === "scientific" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setMode("scientific")}
                  >
                    Scientific
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              {/* Display */}
              <div className="mb-6">
                {equation && (
                  <div className="text-sm text-slate-500 mb-1 min-h-5">{equation}</div>
                )}
                <div className="bg-slate-100 p-4 rounded-lg text-right">
                  <div className="text-3xl font-mono text-slate-900 truncate">
                    {display}
                  </div>
                </div>
              </div>

              {/* Scientific Functions (if in scientific mode) */}
              {mode === "scientific" && (
                <div className="grid grid-cols-4 gap-2 mb-4">
                  {scientificButtons.map((btn, index) => (
                    <Button
                      key={index}
                      variant="outline"
                      size="sm"
                      onClick={btn.onClick}
                      className="h-10 text-sm bg-purple-50 text-purple-700 hover:bg-purple-100"
                    >
                      {btn.label}
                    </Button>
                  ))}
                </div>
              )}

              {/* Main Calculator Grid */}
              <div className="grid grid-cols-4 gap-2">
                {basicButtons.map((btn, index) => (
                  <Button
                    key={index}
                    onClick={btn.onClick}
                    className={`h-12 text-lg font-semibold ${btn.className || "bg-slate-50 text-slate-900 hover:bg-slate-100"}`}
                    variant="outline"
                    style={btn.label === "=" ? { gridRow: "span 2" } : btn.label === "0" ? { gridColumn: "span 2" } : {}}
                  >
                    {btn.label}
                  </Button>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* History */}
        <div>
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center space-x-2">
                  <span>History</span>
                  <Badge variant="secondary">{history.length}</Badge>
                </CardTitle>
                {history.length > 0 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={clearHistory}
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                )}
              </div>
            </CardHeader>
            <CardContent>
              {history.length === 0 ? (
                <div className="text-center py-8 text-slate-500 text-sm">
                  No calculations yet
                </div>
              ) : (
                <div className="space-y-2 max-h-96 overflow-y-auto">
                  {history.slice().reverse().map((calc, index) => (
                    <div
                      key={index}
                      className="p-2 bg-slate-50 rounded text-sm font-mono cursor-pointer hover:bg-slate-100"
                      onClick={() => {
                        const result = calc.split(" = ")[1];
                        setDisplay(result);
                      }}
                    >
                      {calc}
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

