  },
  {
    text: "The way to get started is to quit talking and begin doing.",
    author: "Walt Disney",
    category: "Productivity"
  },
  {
    text: "Don't let yesterday take up too much of today.",
    author: "Will Rogers",
    category: "Life"
  },
  {
    text: "You learn more from failure than from success. Don't let it stop you. Failure builds character.",
    author: "Unknown",
    category: "Motivation"
  },
  {
    text: "If you are working on something that you really care about, you don't have to be pushed. The vision pulls you.",
    author: "Steve Jobs",
    category: "Productivity"
  },
  {
    text: "Happiness is not something ready made. It comes from your own actions.",
    author: "Dalai Lama",
    category: "Happiness"
  },
  {
    text: "The only impossible journey is the one you never begin.",
    author: "Tony Robbins",
    category: "Inspiration"
  },
  {
    text: "Life is what happens to you while you're busy making other plans.",
    author: "John Lennon",
    category: "Life"
  },
  {
    text: "The greatest glory in living lies not in never falling, but in rising every time we fall.",
    author: "Nelson Mandela",
    category: "Wisdom"
  },
  {
    text: "Your time is limited, don't waste it living someone else's life.",
    author: "Steve Jobs",
    category: "Life"
  },
  {
    text: "Creativity is intelligence having fun.",
    author: "Albert Einstein",
    category: "Creativity"
  }
];

export default function DailyQuotes() {
  const [todaysQuote, setTodaysQuote] = useState<DailyQuote | null>(null);
  const [favoriteQuotes, setFavoriteQuotes] = useState<DailyQuote[]>([]);
  const [customQuotes, setCustomQuotes] = useState<CustomQuote[]>([]);
  const [newQuoteText, setNewQuoteText] = useState('');
  const [newQuoteAuthor, setNewQuoteAuthor] = useState('');
  const [newQuoteCategory, setNewQuoteCategory] = useState('Inspiration');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [quotesHistory, setQuotesHistory] = useState<DailyQuote[]>([]);
  const { toast } = useToast();

  useEffect(() => {
    // Load saved data
    const savedFavorites = localStorage.getItem('dailyQuotes_favorites');
    if (savedFavorites) {
      setFavoriteQuotes(JSON.parse(savedFavorites));
    }

    const savedCustom = localStorage.getItem('dailyQuotes_custom');
    if (savedCustom) {
      const parsed = JSON.parse(savedCustom);
      setCustomQuotes(parsed.map((q: any) => ({
        ...q,
        createdAt: new Date(q.createdAt)
      })));
    }

    const savedHistory = localStorage.getItem('dailyQuotes_history');
    if (savedHistory) {
      setQuotesHistory(JSON.parse(savedHistory));
    }

    // Generate today's quote
    generateTodaysQuote();
  }, []);

  useEffect(() => {
    localStorage.setItem('dailyQuotes_favorites', JSON.stringify(favoriteQuotes));
  }, [favoriteQuotes]);

  useEffect(() => {
    localStorage.setItem('dailyQuotes_custom', JSON.stringify(customQuotes));
  }, [customQuotes]);

  useEffect(() => {
    localStorage.setItem('dailyQuotes_history', JSON.stringify(quotesHistory));
  }, [quotesHistory]);

  const generateTodaysQuote = () => {
    const today = new Date().toDateString();

    // Check if we already have today's quote
    const existingQuote = quotesHistory.find(q => q.date === today);
    if (existingQuote) {
      setTodaysQuote(existingQuote);
      return;
    }

    // Generate new quote for today
    const allQuotes = [...inspirationalQuotes, ...customQuotes.map(q => ({
      text: q.text,
      author: q.author,
      category: q.category
    }))];

    const randomQuote = allQuotes[Math.floor(Math.random() * allQuotes.length)];

    const dailyQuote: DailyQuote = {
      id: Date.now().toString(),
      text: randomQuote.text,
      author: randomQuote.author,
      category: randomQuote.category,
      date: today,
      favorite: false,
      shared: false
    };

    setTodaysQuote(dailyQuote);
    setQuotesHistory(prev => [dailyQuote, ...prev.slice(0, 29)]); // Keep last 30 days
  };

  const generateNewQuote = () => {
    const allQuotes = [...inspirationalQuotes, ...customQuotes.map(q => ({
      text: q.text,
      author: q.author,
      category: q.category
    }))];

    let randomQuote;
    do {
      randomQuote = allQuotes[Math.floor(Math.random() * allQuotes.length)];
    } while (todaysQuote && randomQuote.text === todaysQuote.text);

    const newQuote: DailyQuote = {
      id: Date.now().toString(),
      text: randomQuote.text,
      author: randomQuote.author,
      category: randomQuote.category,
      date: new Date().toDateString(),
      favorite: false,
      shared: false
    };

    setTodaysQuote(newQuote);
    toast({
      title: "New Quote Generated",
      description: "Here's a fresh dose of inspiration!"
    });
  };

  const toggleFavorite = (quote: DailyQuote) => {
    const updatedQuote = { ...quote, favorite: !quote.favorite };

    if (updatedQuote.favorite) {
      setFavoriteQuotes(prev => [updatedQuote, ...prev]);
      toast({
        title: "Added to Favorites",
        description: "Quote saved to your favorites collection."
      });
    } else {
      setFavoriteQuotes(prev => prev.filter(q => q.id !== quote.id));
      toast({
        title: "Removed from Favorites",
        description: "Quote removed from your favorites."
      });
    }

    if (todaysQuote?.id === quote.id) {
      setTodaysQuote(updatedQuote);
    }

    setQuotesHistory(prev => prev.map(q => q.id === quote.id ? updatedQuote : q));
  };

  const shareQuote = async (quote: DailyQuote) => {
    const shareText = `"${quote.text}" - ${quote.author}`;

    if (navigator.share) {
      try {
        await navigator.share({
          title: 'Daily Quote',
          text: shareText
        });
        toast({
          title: "Quote Shared",
          description: "Quote shared successfully!"
        });
      } catch (err) {
        copyToClipboard(shareText);
      }
    } else {
      copyToClipboard(shareText);
    }
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      toast({
        title: "Copied to Clipboard",
