  const [timeLeft, setTimeLeft] = useState(600);
  const [isActive, setIsActive] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);
  const [testQuestions, setTestQuestions] = useState<Question[]>([]);
  const [showExplanations, setShowExplanations] = useState(false);
  const [startTime, setStartTime] = useState<Date | null>(null);

  const queryClient = useQueryClient();

  // Fetch test sessions for progress tracking
  const { data: testSessions = [], isLoading: sessionsLoading } = useQuery({
    queryKey: ['/api/iq-test/sessions'],
    queryFn: () => apiRequest('GET', '/api/iq-test/sessions').then(res => res.json())
  });

  // Fetch IQ test statistics
  const { data: testStats, isLoading: statsLoading } = useQuery({
    queryKey: ['/api/iq-test/stats'],
    queryFn: () => apiRequest('GET', '/api/iq-test/stats').then(res => res.json())
  });

  // Save test session mutation
  const saveSessionMutation = useMutation({
    mutationFn: (sessionData: any) => 
      apiRequest('POST', '/api/iq-test/sessions', sessionData).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/iq-test/sessions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/iq-test/stats'] });
    }
  });

  useEffect(() => {
    let interval: NodeJS.Timeout | null = null;
    if (isActive && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft(timeLeft => timeLeft - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      setIsActive(false);
      calculateResults();
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isActive, timeLeft]);

  const generateTestQuestions = (config: TestConfig): Question[] => {
    const filteredQuestions = allQuestions.filter(q => 
      q.level === config.level || 
      (config.level === 'standard' && ['basic', 'standard'].includes(q.level)) ||
      (config.level === 'professional' && ['standard', 'professional'].includes(q.level)) ||
      (config.level === 'genius' && ['professional', 'genius'].includes(q.level))
    );

    // Shuffle and take required number
    const shuffled = [...filteredQuestions].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, Math.min(config.questionCount, filteredQuestions.length));
  };

  const startTest = (level?: string) => {
    const config = level ? testConfigs[level] : customConfig;
    const questions = generateTestQuestions(config);

    setTestQuestions(questions);
    setIsActive(true);
    setCurrentQuestion(0);
    setSelectedAnswers([]);
    setShowResults(false);
    setTimeLeft(config.timeLimit);
    setShowExplanations(false);
    setStartTime(new Date());
    setCurrentView('test');
  };

  const selectAnswer = (answerIndex: number) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[currentQuestion] = answerIndex;
    setSelectedAnswers(newAnswers);
  };

  const nextQuestion = () => {
    if (currentQuestion < testQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setIsActive(false);
      calculateResults();
    }
  };

  const previousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const calculateResults = async () => {
    let correct = 0;
    const categoryScores: Record<string, { correct: number; total: number }> = {};

    selectedAnswers.forEach((answer, index) => {
      if (index < testQuestions.length) {
        const question = testQuestions[index];
        const category = question.category;

        if (!categoryScores[category]) {
          categoryScores[category] = { correct: 0, total: 0 };
        }
        categoryScores[category].total++;

        if (answer === question.correct) {
          correct++;
          categoryScores[category].correct++;
        }
      }
    });

    const finalCategoryScores: Record<string, number> = {};
    Object.entries(categoryScores).forEach(([category, scores]) => {
      finalCategoryScores[category] = Math.round((scores.correct / scores.total) * 100);
    });

    setScore(correct);
    setShowResults(true);
    setCurrentView('results');

    // Save session to database
    const currentLevel = testQuestions[0]?.level || 'standard';
    const iqScore = getIQScore(correct, testQuestions.length, currentLevel);
    const totalTime = startTime ? Math.round((Date.now() - startTime.getTime()) / 1000) : 0;

    try {
      await saveSessionMutation.mutateAsync({
        testLevel: currentLevel,
        questionCount: testQuestions.length,
        correctAnswers: correct,
        totalTime,
        iqScore,
        categoryScores: finalCategoryScores,
        questionsData: {
          questions: testQuestions.map(q => q.id),
          answers: selectedAnswers
        }
      });
    } catch (error) {
      console.error('Failed to save test session:', error);
    }
  };

  const getIQScore = (correct: number, totalQuestions: number, level: string) => {
    const percentage = (correct / totalQuestions) * 100;
    const levelMultiplier = {
      basic: 0.8,
      standard: 1.0,
      professional: 1.2,
      genius: 1.5
    }[level] || 1.0;

    let baseScore = 100;
    if (percentage >= 90) baseScore = 140;
    else if (percentage >= 80) baseScore = 130;
    else if (percentage >= 70) baseScore = 120;
    else if (percentage >= 60) baseScore = 110;
    else if (percentage >= 50) baseScore = 100;
    else if (percentage >= 40) baseScore = 90;
    else baseScore = 80;

    return Math.round(baseScore * levelMultiplier);
  };

  const getPerformanceLevel = (iqScore: number) => {
    if (iqScore >= 160) return { level: "Genius", color: "text-purple-600", icon: Star };
    if (iqScore >= 140) return { level: "Very Superior", color: "text-blue-600", icon: Award };
    if (iqScore >= 120) return { level: "Superior", color: "text-green-600", icon: Trophy };
    if (iqScore >= 110) return { level: "High Average", color: "text-yellow-600", icon: Target };
    if (iqScore >= 90) return { level: "Average", color: "text-gray-600", icon: Brain };
    return { level: "Below Average", color: "text-red-600", icon: Brain };
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const resetTest = () => {
    setCurrentView('menu');
    setIsActive(false);
    setShowResults(false);
    setCurrentQuestion(0);
    setSelectedAnswers([]);
    setTestQuestions([]);
    setScore(0);
    setStartTime(null);
  };

  // Main Menu View
  if (currentView === 'menu') {
    return (
      <div className="max-w-4xl mx-auto space-y-6">
        <Card className="text-center">
          <CardHeader>
            <CardTitle className="flex items-center justify-center gap-2">
              <Brain className="h-6 w-6 text-purple-600" />
              Enhanced IQ Assessment Suite
            </CardTitle>
            <p className="text-muted-foreground">
              Comprehensive cognitive assessment with progress tracking and detailed analytics
            </p>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(testConfigs).map(([key, config]) => (
                <Card key={key} className="cursor-pointer hover:shadow-md transition-shadow"
                      onClick={() => startTest(key)}>
                  <CardContent className="p-4 text-center">
                    <div className="capitalize text-lg font-semibold">{key} Level</div>
                    <div className="text-sm text-muted-foreground">
                      {config.questionCount} questions ‚Ä¢ {Math.round(config.timeLimit / 60)} minutes
                    </div>
                    <div className="mt-2 text-xs">
                      {config.categories.slice(0, 3).join(', ')}
                      {config.categories.length > 3 && ` +${config.categories.length - 3} more`}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>

            <div className="flex flex-wrap gap-2 justify-center">
              <Button onClick={() => setCurrentView('config')} variant="outline">
                <Settings className="h-4 w-4 mr-2" />
                Custom Test
              </Button>
              <Button onClick={() => setCurrentView('stats')} variant="outline">
                <BarChart3 className="h-4 w-4 mr-2" />
                Progress & Stats
              </Button>
            </div>

            {/* Quick Stats Overview */}
            {testStats && testStats.totalTests > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle className="text-sm">Your Progress</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-3 gap-4 text-center text-sm">
                    <div>
                      <div className="text-lg font-semibold">{testStats.totalTests}</div>
                      <div className="text-muted-foreground">Tests Taken</div>
                    </div>
                    <div>
                      <div className="text-lg font-semibold">{testStats.bestScore}</div>
                      <div className="text-muted-foreground">Best Score</div>
                    </div>
                    <div>
                      <div className="text-lg font-semibold">{testStats.averageAccuracy}%</div>
                      <div className="text-muted-foreground">Avg Accuracy</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}
          </CardContent>
        </Card>
      </div>
    );
  }

  // Test Configuration View
  if (currentView === 'config') {
    return (
      <div className="max-w-2xl mx-auto space-y-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Settings className="h-5 w-5" />
              Custom Test Configuration
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <label className="text-sm font-medium">Test Level</label>
              <Select value={customConfig.level} onValueChange={(value: any) => 
                setCustomConfig({...customConfig, level: value})
              }>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {Object.keys(testConfigs).map(level => (
                    <SelectItem key={level} value={level}>
                      {level.charAt(0).toUpperCase() + level.slice(1)}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <label className="text-sm font-medium">Number of Questions: {customConfig.questionCount}</label>
              <input
                type="range"
                min="5"
                max="25"
                value={customConfig.questionCount}
                onChange={(e) => setCustomConfig({...customConfig, questionCount: parseInt(e.target.value)})}
                className="w-full"
              />
            </div>

            <div>
              <label className="text-sm font-medium">Time Limit: {Math.round(customConfig.timeLimit / 60)} minutes</label>
              <input
                type="range"
                min="300"
                max="1800"
                step="300"
                value={customConfig.timeLimit}
                onChange={(e) => setCustomConfig({...customConfig, timeLimit: parseInt(e.target.value)})}
                className="w-full"
              />
            </div>

            <div className="flex gap-2">
              <Button onClick={() => startTest()} className="flex-1">
                <Zap className="h-4 w-4 mr-2" />
                Start Custom Test
              </Button>
              <Button onClick={() => setCurrentView('menu')} variant="outline">
                Back
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Statistics View
  if (currentView === 'stats') {
    return (
      <div className="max-w-4xl mx-auto space-y-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="h-5 w-5" />
              Progress & Statistics
            </CardTitle>
          </CardHeader>
          <CardContent>
            {statsLoading ? (
              <div className="text-center py-8">Loading statistics...</div>
            ) : testStats && testStats.totalTests > 0 ? (
              <Tabs defaultValue="overview" className="w-full">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="overview">Overview</TabsTrigger>
                  <TabsTrigger value="progress">Progress</TabsTrigger>
                  <TabsTrigger value="categories">Categories</TabsTrigger>
                </TabsList>

                <TabsContent value="overview" className="space-y-4">
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <Card>
                      <CardContent className="p-4 text-center">
                        <div className="text-2xl font-bold">{testStats.totalTests}</div>
                        <div className="text-sm text-muted-foreground">Total Tests</div>
                      </CardContent>
                    </Card>
                    <Card>
                      <CardContent className="p-4 text-center">
                        <div className="text-2xl font-bold">{testStats.averageScore}</div>
                        <div className="text-sm text-muted-foreground">Avg Score</div>
                      </CardContent>
                    </Card>
                    <Card>
                      <CardContent className="p-4 text-center">
                        <div className="text-2xl font-bold">{testStats.bestScore}</div>
                        <div className="text-sm text-muted-foreground">Best Score</div>
                      </CardContent>
                    </Card>
                    <Card>
                      <CardContent className="p-4 text-center">
                        <div className="text-2xl font-bold">{testStats.averageAccuracy}%</div>
                        <div className="text-sm text-muted-foreground">Avg Accuracy</div>
                      </CardContent>
                    </Card>
                  </div>

                  <Card>
                    <CardHeader>
                      <CardTitle className="text-sm">Level Progression</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-2">
                        {Object.entries(testStats.levelProgression).map(([level, count]) => (
                          <div key={level} className="flex justify-between items-center">
                            <span className="capitalize">{level}</span>
                            <div className="flex items-center gap-2">
                              <span>{count} tests</span>
                              <div className="w-24 bg-gray-200 rounded-full h-2">
                                <div 
                                  className="bg-blue-600 h-2 rounded-full" 
                                  style={{ width: `${(count / testStats.totalTests) * 100}%` }}
                                ></div>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="progress" className="space-y-4">
                  {testStats.recentTrend.length > 0 && (
                    <Card>
                      <CardHeader>
                        <CardTitle className="text-sm">Recent Performance Trend</CardTitle>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2">
                          {testStats.recentTrend.map((session, index) => (
                            <div key={index} className="flex justify-between items-center text-sm">
                              <span>{new Date(session.date).toLocaleDateString()}</span>
                              <div className="flex items-center gap-2">
                                <Badge variant="outline">{session.level}</Badge>
                                <span className="font-semibold">{session.score}</span>
                              </div>
                            </div>
                          ))}
                        </div>
                      </CardContent>
                    </Card>
                  )}
                </TabsContent>

                <TabsContent value="categories" className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-sm">Category Strengths</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        {Object.entries(testStats.categoryStrengths).map(([category, score]) => (
                          <div key={category} className="space-y-1">
                            <div className="flex justify-between text-sm">
                              <span>{category}</span>
                              <span>{score}%</span>
                            </div>
                            <Progress value={score} className="h-2" />
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            ) : (
              <div className="text-center py-8">
                <Brain className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
                <div className="text-lg font-medium">No test data yet</div>
                <div className="text-muted-foreground">Take your first test to see progress tracking</div>
              </div>
            )}
          </CardContent>
        </Card>

        <div className="text-center">
          <Button onClick={() => setCurrentView('menu')} variant="outline">
            Back to Menu
          </Button>
        </div>
      </div>
    );
  }

  // Test View
  if (currentView === 'test' && testQuestions.length > 0) {
    const currentQ = testQuestions[currentQuestion];
    const progress = ((currentQuestion + 1) / testQuestions.length) * 100;

    return (
      <div className="max-w-2xl mx-auto space-y-6">
        <Card>
          <CardHeader>
            <div className="flex justify-between items-center">
              <CardTitle className="text-lg">
                Question {currentQuestion + 1} of {testQuestions.length}
              </CardTitle>
              <div className="flex items-center gap-4 text-sm">
                <Badge variant="outline">{currentQ.category}</Badge>
                <div className="flex items-center gap-1">
                  <Clock className="h-4 w-4" />
                  {formatTime(timeLeft)}
                </div>
              </div>
            </div>
            <Progress value={progress} className="h-2" />
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="text-lg font-medium">{currentQ.question}</div>

            <div className="space-y-2">
              {currentQ.options.map((option, index) => (
                <Button
                  key={index}
                  variant={selectedAnswers[currentQuestion] === index ? "default" : "outline"}
                  className="w-full text-left justify-start h-auto p-4"
                  onClick={() => selectAnswer(index)}
                >
                  <span className="mr-2 font-bold">{String.fromCharCode(65 + index)}.</span>
                  {option}
                </Button>
              ))}
            </div>

            <div className="flex justify-between">
              <Button 
                onClick={previousQuestion} 
                disabled={currentQuestion === 0}
                variant="outline"
              >
                Previous
              </Button>
              <Button 
                onClick={nextQuestion}
                disabled={selectedAnswers[currentQuestion] === undefined}
              >
                {currentQuestion === testQuestions.length - 1 ? 'Finish Test' : 'Next'}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Results View
  if (currentView === 'results' && showResults) {
    const currentLevel = testQuestions[0]?.level || 'standard';
    const iqScore = getIQScore(score, testQuestions.length, currentLevel);
    const performance = getPerformanceLevel(iqScore);
    const Icon = performance.icon;

    return (
      <div className="max-w-4xl mx-auto space-y-6">
        <Card className="text-center">
          <CardHeader>
            <CardTitle className="flex items-center justify-center gap-2">
              <Icon className={`h-6 w-6 ${performance.color}`} />
              IQ Test Results
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="text-center">
              <div className={`text-6xl font-bold ${performance.color}`}>{iqScore}</div>
              <div className="text-xl text-muted-foreground">Estimated IQ Score</div>
              <div className={`text-lg font-semibold ${performance.color}`}>{performance.level}</div>
            </div>

            <div className="grid grid-cols-3 gap-4 text-center">
              <div>
                <div className="text-2xl font-semibold">{score}/{testQuestions.length}</div>
                <div className="text-sm text-muted-foreground">Correct Answers</div>
              </div>
              <div>
                <div className="text-2xl font-semibold">{Math.round((score / testQuestions.length) * 100)}%</div>
                <div className="text-sm text-muted-foreground">Accuracy</div>
              </div>
              <div>
                <div className="text-2xl font-semibold capitalize">{currentLevel}</div>
                <div className="text-sm text-muted-foreground">Test Level</div>
              </div>
            </div>

            <Tabs defaultValue="summary" className="w-full">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="summary">Summary</TabsTrigger>
                <TabsTrigger value="detailed">Detailed Review</TabsTrigger>
              </TabsList>

              <TabsContent value="summary" className="space-y-4">
                <div className="space-y-2">
                  <div className="text-sm font-medium">Performance by Category:</div>
                  {Array.from(new Set(testQuestions.map(q => q.category))).map(category => {
                    const categoryQuestions = testQuestions.filter(q => q.category === category);
                    const categoryCorrect = categoryQuestions.filter((q, index) => {
                      const originalIndex = testQuestions.indexOf(q);
                      return selectedAnswers[originalIndex] === q.correct;
                    }).length;
                    const percentage = Math.round((categoryCorrect / categoryQuestions.length) * 100);
                    return (
                      <div key={category} className="flex justify-between items-center text-sm">
                        <span>{category}</span>
                        <div className="flex items-center gap-2">
                          <span>{categoryCorrect}/{categoryQuestions.length}</span>
                          <div className="w-16 bg-gray-200 rounded-full h-2">
                            <div 
                              className="bg-blue-600 h-2 rounded-full" 
                              style={{ width: `${percentage}%` }}
                            ></div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </TabsContent>

              <TabsContent value="detailed" className="space-y-4">
                <div className="space-y-4 max-h-96 overflow-y-auto">
                  {testQuestions.map((question, index) => {
                    const userAnswer = selectedAnswers[index];
                    const isCorrect = userAnswer === question.correct;
                    return (
                      <div key={question.id} className="border rounded-lg p-4 space-y-2">
                        <div className="flex items-center gap-2">
                          <Badge variant={isCorrect ? "default" : "destructive"}>
                            Q{index + 1}
                          </Badge>
                          <Badge variant="outline">{question.category}</Badge>
                          <Badge variant="secondary">{question.difficulty}</Badge>
                        </div>
                        <div className="text-sm font-medium">{question.question}</div>
                        <div className="text-xs space-y-1">
                          <div className={isCorrect ? "text-green-600" : "text-red-600"}>
                            Your answer: {question.options[userAnswer] || "Not answered"}
                          </div>
                          {!isCorrect && (
                            <div className="text-green-600">
                              Correct answer: {question.options[question.correct]}
                            </div>
                          )}
                          {question.explanation && (
                            <div className="text-muted-foreground italic">
                              {question.explanation}
                            </div>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </TabsContent>
            </Tabs>

            <div className="flex gap-2 justify-center">
              <Button onClick={resetTest}>
                <RotateCcw className="h-4 w-4 mr-2" />
                Take Another Test
              </Button>
              <Button onClick={() => setCurrentView('stats')} variant="outline">
                <BarChart3 className="h-4 w-4 mr-2" />
                View All Stats
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto">
      <Card>
        <CardContent className="p-8 text-center">
          <Brain className="h-12 w-12 mx-auto text-purple-600 mb-4" />
          <div className="text-lg font-medium">Enhanced IQ Tester</div>
          <div className="text-muted-foreground">Loading assessment suite...</div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Clock, Brain, Target, Trophy, RotateCcw, Settings, Zap, Star, Award, TrendingUp, Calendar, BarChart3, Globe } from "lucide-react";

interface Question {
  id: number;
  question: string;
  options: string[];
  correct: number;
  category: string;
  difficulty: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  level: 'basic' | 'standard' | 'professional' | 'genius';
  explanation?: string;
}

interface TestConfig {
  level: 'basic' | 'standard' | 'professional' | 'genius';
  questionCount: number;
  timeLimit: number;
  categories: string[];
}

const allQuestions: Question[] = [
  // Basic Level Questions
  {
    id: 1,
    question: "What number comes next: 1, 2, 3, 4, ?",
    options: ["5", "6", "7", "8"],
    correct: 0,
    category: "Pattern Recognition",
    difficulty: 'beginner',
    level: 'basic',
    explanation: "This is a simple counting sequence, so 5 comes next."
  },
  {
    id: 2,
    question: "Which shape is different?",
    options: ["Circle", "Square", "Triangle", "Apple"],
