type NoteFormData = z.infer<typeof insertNoteSchema>;

interface RichTextEditorProps {
  content: string;
  onChange: (content: string) => void;
  placeholder?: string;
}

function RichTextEditor({ content, onChange, placeholder }: RichTextEditorProps) {
  const editor = useEditor({
    extensions: [
      StarterKit,
      TiptapImage.configure({
        inline: true,
        allowBase64: true,
      }),
      TiptapLink.configure({
        openOnClick: false,
      }),
      TaskList,
      TaskItem.configure({
        nested: true,
      }),
    ],
    content,
    onUpdate: ({ editor }) => {
      onChange(editor.getHTML());
    },
    editorProps: {
      attributes: {
        class: 'prose prose-sm sm:prose lg:prose-lg xl:prose-2xl mx-auto focus:outline-none min-h-[200px] p-4',
      },
    },
  });

  const addImage = () => {
    const url = window.prompt('Enter image URL:');
    if (url) {
      editor?.chain().focus().setImage({ src: url }).run();
    }
  };

  const addLink = () => {
    const url = window.prompt('Enter URL:');
    if (url) {
      editor?.chain().focus().setLink({ href: url }).run();
    }
  };

  const addTable = () => {
    // Simple HTML table insertion
    const tableHTML = `
      <table>
        <tr><th>Header 1</th><th>Header 2</th><th>Header 3</th></tr>
        <tr><td>Cell 1</td><td>Cell 2</td><td>Cell 3</td></tr>
        <tr><td>Cell 4</td><td>Cell 5</td><td>Cell 6</td></tr>
      </table>
    `;
    editor?.commands.insertContent(tableHTML);
  };

  if (!editor) {
    return <div className="h-64 bg-gray-100 dark:bg-gray-800 rounded animate-pulse" />;
  }

  return (
    <div className="border rounded-lg">
      {/* Toolbar */}
      <div className="border-b p-2 flex flex-wrap gap-1">
        <div className="flex items-center gap-1 border-r pr-2 mr-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleBold().run()}
            className={editor.isActive('bold') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Bold className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleItalic().run()}
            className={editor.isActive('italic') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Italic className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleStrike().run()}
            className={editor.isActive('strike') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Strikethrough className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center gap-1 border-r pr-2 mr-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()}
            className={editor.isActive('heading', { level: 1 }) ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Heading1 className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleHeading({ level: 2 }).run()}
            className={editor.isActive('heading', { level: 2 }) ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Heading2 className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleHeading({ level: 3 }).run()}
            className={editor.isActive('heading', { level: 3 }) ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Heading3 className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center gap-1 border-r pr-2 mr-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleBulletList().run()}
            className={editor.isActive('bulletList') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <List className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleOrderedList().run()}
            className={editor.isActive('orderedList') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <ListOrdered className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleTaskList().run()}
            className={editor.isActive('taskList') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <FileText className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center gap-1 border-r pr-2 mr-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleBlockquote().run()}
            className={editor.isActive('blockquote') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Quote className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleCodeBlock().run()}
            className={editor.isActive('codeBlock') ? 'bg-gray-200 dark:bg-gray-700' : ''}
          >
            <Code className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center gap-1">
          <Button variant="ghost" size="sm" onClick={addImage}>
            <ImageIcon className="h-4 w-4" />
          </Button>
          <Button variant="ghost" size="sm" onClick={addLink}>
            <LinkIcon className="h-4 w-4" />
          </Button>
          <Button variant="ghost" size="sm" onClick={addTable}>
            <TableIcon className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Editor Content */}
      <EditorContent 
        editor={editor} 
        className="min-h-[200px] max-h-[600px] overflow-y-auto"
      />
    </div>
  );
}

interface MarkdownEditorProps {
  content: string;
  onChange: (content: string) => void;
  showPreview?: boolean;
}

function MarkdownEditor({ content, onChange, showPreview = false }: MarkdownEditorProps) {
  return (
    <div className={`grid ${showPreview ? 'grid-cols-2' : 'grid-cols-1'} gap-4 h-96`}>
      <div className="border rounded-lg">
        <div className="border-b p-2 bg-gray-50 dark:bg-gray-800">
          <span className="text-sm font-medium">Markdown</span>
        </div>
        <Textarea
          value={content}
          onChange={(e) => onChange(e.target.value)}
          placeholder="# Your note title

Write your content in **Markdown**...

- List item 1
- List item 2

```javascript
console.log('Code blocks supported');
```"
          className="h-full border-0 resize-none font-mono text-sm"
        />
      </div>

      {showPreview && (
        <div className="border rounded-lg">
          <div className="border-b p-2 bg-gray-50 dark:bg-gray-800">
            <span className="text-sm font-medium">Preview</span>
          </div>
          <ScrollArea className="h-full p-4">
            <div className="prose prose-sm dark:prose-invert max-w-none">
              <ReactMarkdown
                remarkPlugins={[remarkGfm]}
                rehypePlugins={[rehypeHighlight]}
              >
                {content || '*Preview will appear here...*'}
              </ReactMarkdown>
            </div>
          </ScrollArea>
        </div>
      )}
    </div>
  );
}

export default function EnhancedNotesTool() {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingNote, setEditingNote] = useState<Note | null>(null);
  const [selectedFolder, setSelectedFolder] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState<'list' | 'grid'>('list');
  const [editorMode, setEditorMode] = useState<'rich' | 'markdown'>('rich');
  const [showMarkdownPreview, setShowMarkdownPreview] = useState(false);
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [showArchived, setShowArchived] = useState(false);
  const [showTemplates, setShowTemplates] = useState(false);

  const form = useForm<NoteFormData>({
    resolver: zodResolver(insertNoteSchema),
    defaultValues: {
      title: "",
      content: "",
      markdownContent: "",
      contentType: "rich",
      folder: "General",
      tags: [],
      smartTags: [],
      linkedNotes: [],
      attachments: {},
      metadata: {},
      outline: {},
      isFavorite: false,
      isArchived: false,
      wordCount: 0,
      readingTime: 0,
    },
  });

  const { data: notes = [], isLoading } = useQuery({
    queryKey: ["/api/notes"],
  });

  const { data: folders = [] } = useQuery({
    queryKey: ["/api/note-folders"],
  });

  const { data: templates = [] } = useQuery({
    queryKey: ["/api/note-templates"],
  });

  const createNoteMutation = useMutation({
    mutationFn: (data: NoteFormData) => apiRequest("POST", "/api/notes", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
      setIsDialogOpen(false);
      form.reset();
      toast({ title: "Note created successfully" });
    },
  });

  const updateNoteMutation = useMutation({
    mutationFn: ({ id, ...data }: Partial<Note> & { id: number }) =>
      apiRequest("PATCH", `/api/notes/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
      setEditingNote(null);
      toast({ title: "Note updated successfully" });
    },
  });

  const deleteNoteMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/notes/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
      toast({ title: "Note deleted successfully" });
    },
  });

  const filteredNotes = useMemo(() => {
    let filtered = notes.filter((note: Note) => {
      if (!showArchived && note.isArchived) return false;
      if (selectedFolder !== 'all' && note.folder !== selectedFolder) return false;
      if (selectedTags.length > 0 && !selectedTags.some(tag => note.tags?.includes(tag))) return false;
      if (searchQuery) {
        const query = searchQuery.toLowerCase();
        return note.title.toLowerCase().includes(query) || 
               note.content.toLowerCase().includes(query) ||
               note.tags?.some(tag => tag.toLowerCase().includes(query));
      }
      return true;
    });

    return filtered.sort((a: Note, b: Note) => {
      if (a.isFavorite && !b.isFavorite) return -1;
      if (!a.isFavorite && b.isFavorite) return 1;
      return new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime();
    });
  }, [notes, selectedFolder, selectedTags, searchQuery, showArchived]);

  const allTags = useMemo(() => {
    const tags = new Set<string>();
    notes.forEach((note: Note) => {
      note.tags?.forEach(tag => tags.add(tag));
      note.smartTags?.forEach(tag => tags.add(tag));
    });
    return Array.from(tags);
  }, [notes]);

  const allFolders = useMemo(() => {
    const folderSet = new Set(notes.map((note: Note) => note.folder).filter(Boolean));
    return Array.from(folderSet);
  }, [notes]);

  const calculateReadingTime = (content: string) => {
    const wordsPerMinute = 200;
    const words = content.split(/\s+/).length;
    return Math.ceil(words / wordsPerMinute);
  };

  const generateOutline = (content: string) => {
    const headingRegex = /<h([1-6]).*?>(.*?)<\/h[1-6]>/g;
    const outline: Array<{level: number, text: string, id: string}> = [];
    let match;

    while ((match = headingRegex.exec(content)) !== null) {
      outline.push({
        level: parseInt(match[1]),
        text: match[2].replace(/<[^>]*>/g, ''),
        id: match[2].toLowerCase().replace(/[^a-z0-9]/g, '-')
      });
    }

    return outline;
  };

  const onSubmit = (data: NoteFormData) => {
    const content = editorMode === 'rich' ? data.content : data.markdownContent || '';
    const wordCount = content.split(/\s+/).length;
    const readingTime = calculateReadingTime(content);
    const outline = generateOutline(content);

    const noteData = {
      ...data,
      contentType: editorMode,
      wordCount,
      readingTime,
      outline,
    };

    if (editingNote) {
      updateNoteMutation.mutate({ ...noteData, id: editingNote.id });
    } else {
      createNoteMutation.mutate(noteData);
    }
  };

  const handleTemplateSelect = (template: NoteTemplate) => {
    form.setValue('title', template.name);
    form.setValue('content', template.content || '');
    form.setValue('markdownContent', template.markdownContent || '');
    form.setValue('contentType', template.contentType || 'rich');
    form.setValue('tags', template.tags || []);
    setEditorMode((template.contentType as 'rich' | 'markdown') || 'rich');
    setShowTemplates(false);
  };

  if (isLoading) {
    return (
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="h-8 bg-gray-200 rounded animate-pulse"></div>
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          <div className="space-y-3">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-10 bg-gray-200 rounded animate-pulse"></div>
            ))}
          </div>
          <div className="lg:col-span-3 space-y-3">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-32 bg-gray-200 rounded animate-pulse"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span className="flex items-center gap-2">
              <BookOpen className="h-5 w-5" />
              Enhanced Notes
            </span>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowArchived(!showArchived)}
              >
                <Archive className="h-4 w-4 mr-2" />
                {showArchived ? 'Hide Archived' : 'Show Archived'}
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowTemplates(true)}
              >
                <FileText className="h-4 w-4 mr-2" />
                Templates
              </Button>
              <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogTrigger asChild>
                  <Button>
                    <Plus className="h-4 w-4 mr-2" />
                    New Note
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle className="flex items-center justify-between">
                      {editingNote ? 'Edit Note' : 'Create New Note'}
                      <div className="flex items-center gap-2">
                        <span className="text-sm text-gray-500">Mode:</span>
                        <Tabs value={editorMode} onValueChange={(v) => setEditorMode(v as 'rich' | 'markdown')}>
                          <TabsList className="grid w-full grid-cols-2">
                            <TabsTrigger value="rich">Rich Text</TabsTrigger>
                            <TabsTrigger value="markdown">Markdown</TabsTrigger>
                          </TabsList>
                        </Tabs>
                        {editorMode === 'markdown' && (
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setShowMarkdownPreview(!showMarkdownPreview)}
                          >
                            {showMarkdownPreview ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                          </Button>
                        )}
                      </div>
                    </DialogTitle>
                  </DialogHeader>
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="title"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Title</FormLabel>
                              <FormControl>
                                <Input {...field} placeholder="Note title" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="folder"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Folder</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger>
                                    <SelectValue />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  {allFolders.map((folder) => (
                                    <SelectItem key={folder} value={folder}>{folder}</SelectItem>
                                  ))}
                                </SelectContent>
                              </Select>
                            </FormItem>
                          )}
                        />
                      </div>

                      {editorMode === 'rich' ? (
                        <FormField
                          control={form.control}
                          name="content"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Content</FormLabel>
                              <FormControl>
                                <RichTextEditor
                                  content={field.value || ''}
                                  onChange={field.onChange}
                                  placeholder="Start writing your note..."
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      ) : (
                        <FormField
                          control={form.control}
                          name="markdownContent"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Content (Markdown)</FormLabel>
                              <FormControl>
                                <MarkdownEditor
                                  content={field.value || ''}
                                  onChange={field.onChange}
                                  showPreview={showMarkdownPreview}
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      )}

                      <FormField
                        control={form.control}
                        name="tags"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Tags</FormLabel>
                            <FormControl>
                              <Input
                                placeholder="Enter tags separated by commas"
                                value={field.value?.join(', ') || ''}
                                onChange={(e) => field.onChange(e.target.value.split(',').map(tag => tag.trim()).filter(Boolean))}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="flex items-center gap-4">
                        <FormField
                          control={form.control}
                          name="isFavorite"
                          render={({ field }) => (
                            <FormItem className="flex items-center space-x-2">
                              <FormControl>
                                <Switch
                                  checked={field.value}
                                  onCheckedChange={field.onChange}
                                />
                              </FormControl>
                              <FormLabel className="text-sm">Favorite</FormLabel>
                            </FormItem>
                          )}
                        />
                      </div>

                      <Button type="submit" className="w-full">
                        {editingNote ? 'Update Note' : 'Create Note'}
                      </Button>
                    </form>
                  </Form>
                </DialogContent>
              </Dialog>
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap items-center gap-4">
            <div className="flex-1 min-w-64">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search notes..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>

            <Select value={selectedFolder} onValueChange={setSelectedFolder}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="All Folders" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Folders</SelectItem>
                {allFolders.map((folder) => (
                  <SelectItem key={folder} value={folder}>{folder}</SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setViewMode(viewMode === 'list' ? 'grid' : 'list')}
            >
              {viewMode === 'list' ? <Grid className="h-4 w-4" /> : <List className="h-4 w-4" />}
            </Button>
          </div>

          {/* Tags Filter */}
          {allTags.length > 0 && (
            <div className="mt-4 flex flex-wrap gap-2">
              <span className="text-sm text-gray-600 dark:text-gray-400 mr-2">Filter by tags:</span>
              {allTags.map((tag) => (
                <Badge
                  key={tag}
                  variant={selectedTags.includes(tag) ? "default" : "outline"}
                  className="cursor-pointer"
                  onClick={() => {
                    setSelectedTags(prev =>
                      prev.includes(tag)
                        ? prev.filter(t => t !== tag)
                        : [...prev, tag]
                    );
                  }}
                >
                  #{tag}
                </Badge>
              ))}
              {selectedTags.length > 0 && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setSelectedTags([])}
                  className="h-6 px-2"
                >
                  Clear
                </Button>
              )}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Quick Capture & Web Clipper */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
        <QuickCapture />
        <WebClipper />
      </div>

      {/* Notes Grid/List */}
      <div className={viewMode === 'grid' ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4' : 'space-y-4'}>
        {filteredNotes.map((note: Note) => (
          <Card key={note.id} className="group hover:shadow-md transition-shadow">
            <CardContent className="p-4">
              <div className="flex items-start justify-between mb-2">
                <div className="flex-1 min-w-0">
                  <h3 className="font-medium truncate flex items-center gap-2">
                    {note.isFavorite && <Star className="h-4 w-4 text-yellow-500" />}
                    {note.title}
                  </h3>
                  <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                    {note.folder} ‚Ä¢ {format(new Date(note.updatedAt), 'MMM d, yyyy')}
                  </p>
                </div>
                <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => {
                      setEditingNote(note);
                      form.reset({
                        title: note.title,
                        content: note.content,
                        markdownContent: note.markdownContent || '',
                        contentType: note.contentType || 'rich',
                        folder: note.folder,
                        tags: note.tags || [],
                        isFavorite: note.isFavorite,
                      });
                      setEditorMode((note.contentType as 'rich' | 'markdown') || 'rich');
                      setIsDialogOpen(true);
                    }}
                  >
                    <Edit3 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
