                  <div className="space-y-2 text-sm">
                    {project.clientName && (
                      <div className="flex items-center gap-2">
                        <User className="h-3 w-3" />
                        {project.clientName}
                      </div>
                    )}

                    <div className="flex items-center gap-2">
                      <Clock className="h-3 w-3" />
                      Total: 0h 0m
                    </div>

                    {project.isBillable && (
                      <div className="flex items-center gap-2">
                        <DollarSign className="h-3 w-3" />
                        ${(project.billableRate / 100).toFixed(2)}/hour
