import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { 
  Mic, MicOff, Play, Pause, Square, Trash2, Download, 
  Clock, FileAudio, Share2, Copy, Mail, MessageSquare,
  FileText, Languages, Volume2, Edit3, Eye, EyeOff,
  Twitter, Facebook, Linkedin, Phone, Send, Globe
} from "lucide-react";
import { format } from "date-fns";

// Supported languages for transcription
const SUPPORTED_LANGUAGES = [
  { code: "en-US", name: "English (US)" },
  { code: "en-GB", name: "English (UK)" },
  { code: "es-ES", name: "Spanish (Spain)" },
  { code: "es-MX", name: "Spanish (Mexico)" },
  { code: "fr-FR", name: "French" },
  { code: "de-DE", name: "German" },
  { code: "it-IT", name: "Italian" },
  { code: "pt-BR", name: "Portuguese (Brazil)" },
  { code: "ru-RU", name: "Russian" },
  { code: "ja-JP", name: "Japanese" },
  { code: "ko-KR", name: "Korean" },
  { code: "zh-CN", name: "Chinese (Simplified)" },
  { code: "ar-SA", name: "Arabic" },
  { code: "hi-IN", name: "Hindi" },
  { code: "nl-NL", name: "Dutch" },
  { code: "sv-SE", name: "Swedish" },
  { code: "da-DK", name: "Danish" },
  { code: "no-NO", name: "Norwegian" },
  { code: "pl-PL", name: "Polish" },
  { code: "tr-TR", name: "Turkish" }
];

const recordingSchema = z.object({
  title: z.string().min(1, "Title is required"),
  transcriptLanguage: z.string().default("en-US"),
});

type RecordingFormData = z.infer<typeof recordingSchema>;

interface VoiceRecording {
  id: number;
  userId: string;
  title: string;
  duration: number;
  blob: string;
  transcript?: string;
  transcriptLanguage: string;
  summary?: string;
  tags: string[];
  createdAt: string;
  updatedAt: string;
}

export default function EnhancedVoiceRecorderTool() {
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [duration, setDuration] = useState(0);
  const [currentRecording, setCurrentRecording] = useState<Blob | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [playingId, setPlayingId] = useState<number | null>(null);
  const [selectedLanguage, setSelectedLanguage] = useState("en-US");
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [liveTranscript, setLiveTranscript] = useState("");
  const [showTranscript, setShowTranscript] = useState<{ [key: number]: boolean }>({});
  const [shareDialog, setShareDialog] = useState<{ open: boolean; recording: VoiceRecording | null }>({
    open: false,
    recording: null
  });

  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const recognitionRef = useRef<any>(null);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const { toast } = useToast();

  const form = useForm<RecordingFormData>({
    resolver: zodResolver(recordingSchema),
    defaultValues: {
      title: "",
      transcriptLanguage: "en-US",
    },
  });

  // Query for fetching recordings
  const { data: recordings = [], refetch } = useQuery({
    queryKey: ['/api/voice-recordings'],
    queryFn: () => apiRequest("GET", "/api/voice-recordings").then(res => res.json())
  });

  // Mutation for creating recordings
  const createRecordingMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/voice-recordings", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/voice-recordings'] });
      setIsDialogOpen(false);
      form.reset();
      setCurrentRecording(null);
      setLiveTranscript("");
      toast({
        title: "Recording Saved",
        description: "Your voice recording has been saved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save recording. Please try again.",
        variant: "destructive",
      });
    }
  });

  // Mutation for updating transcripts
  const updateTranscriptMutation = useMutation({
    mutationFn: async ({ id, transcript, summary }: { id: number; transcript: string; summary?: string }) => {
      const response = await apiRequest("PATCH", `/api/voice-recordings/${id}`, { transcript, summary });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/voice-recordings'] });
      toast({
        title: "Transcript Updated",
        description: "Transcript has been generated successfully.",
      });
    }
  });

  // Mutation for deleting recordings
  const deleteRecordingMutation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest("DELETE", `/api/voice-recordings/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/voice-recordings'] });
      toast({
        title: "Recording Deleted",
        description: "Voice recording has been deleted.",
      });
    }
  });

  // Initialize speech recognition
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;

      recognitionRef.current.onresult = (event: any) => {
        let transcript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          transcript += event.results[i][0].transcript;
        }
        setLiveTranscript(transcript);
      };

      recognitionRef.current.onerror = (event: any) => {
        console.error('Speech recognition error:', event.error);
      };
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  // Start recording
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          channelCount: 1,
          sampleRate: 16000,
          echoCancellation: true,
          noiseSuppression: true
        }
      });

      // Use WebM format with Opus codec for better compression, fallback to other formats
      let options: MediaRecorderOptions = {};

      if (MediaRecorder.isTypeSupported('audio/webm;codecs=opus')) {
        options = {
          mimeType: 'audio/webm;codecs=opus',
          audioBitsPerSecond: 32000
        };
      } else if (MediaRecorder.isTypeSupported('audio/mp4')) {
        options = {
          mimeType: 'audio/mp4',
          audioBitsPerSecond: 32000
        };
      } else if (MediaRecorder.isTypeSupported('audio/webm')) {
        options = {
          mimeType: 'audio/webm',
          audioBitsPerSecond: 32000
        };
      }

      mediaRecorderRef.current = new MediaRecorder(stream, options);
      const chunks: BlobPart[] = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = () => {
        const mimeType = mediaRecorderRef.current?.mimeType || 'audio/webm';
        const blob = new Blob(chunks, { type: mimeType });
        setCurrentRecording(blob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      setDuration(0);

      // Start live transcription
      if (recognitionRef.current) {
        recognitionRef.current.lang = selectedLanguage;
        recognitionRef.current.start();
      }

      // Start timer
      intervalRef.current = setInterval(() => {
        setDuration(prev => prev + 1);
      }, 1000);

      toast({
        title: "Recording Started",
        description: "Your voice recording has begun with live transcription.",
      });
    } catch (error) {
      toast({
        title: "Microphone Error",
        description: "Could not access microphone. Please check permissions.",
        variant: "destructive",
      });
    }
  };

  // Stop recording
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setIsPaused(false);

      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }

      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }

      setIsDialogOpen(true);
    }
  };

  // Pause/Resume recording
  const togglePause = () => {
    if (mediaRecorderRef.current) {
      if (isPaused) {
        mediaRecorderRef.current.resume();
        if (recognitionRef.current) {
          recognitionRef.current.start();
        }
      } else {
        mediaRecorderRef.current.pause();
        if (recognitionRef.current) {
          recognitionRef.current.stop();
        }
      }
      setIsPaused(!isPaused);
    }
  };

  // Play audio
  const playAudio = (recording: VoiceRecording) => {
    if (audioRef.current) {
      audioRef.current.pause();
    }

    try {
      // Try to detect the correct MIME type from the blob data or use a fallback
      const mimeType = recording.blob.startsWith('UklGR') ? 'audio/wav' : 'audio/webm';
      const audio = new Audio(`data:${mimeType};base64,${recording.blob}`);
      audioRef.current = audio;

      audio.onended = () => setPlayingId(null);
      audio.onerror = () => {
        setPlayingId(null);
        toast({
          title: "Playback Error",
          description: "Could not play this recording. The audio data may be corrupted.",
          variant: "destructive",
        });
      };

      audio.play().catch(() => {
        setPlayingId(null);
        toast({
          title: "Playback Error",
          description: "Could not play this recording. Please try again.",
          variant: "destructive",
        });
      });
      setPlayingId(recording.id);
    } catch (error) {
      toast({
        title: "Playback Error",
        description: "Could not play this recording. The audio format may not be supported.",
        variant: "destructive",
      });
    }
  };

  // Stop audio
  const stopAudio = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
    setPlayingId(null);
  };

  // Generate transcript
  const generateTranscript = async (recording: VoiceRecording) => {
    setIsTranscribing(true);
    try {
      // Using Web Speech API for transcript generation
      const audioBlob = new Blob([Uint8Array.from(atob(recording.blob), c => c.charCodeAt(0))], { type: 'audio/wav' });

      // For now, we'll use a placeholder transcript since we need audio processing
      // In a real implementation, you'd send the audio to a transcription service
      const mockTranscript = `This is a sample transcript for the recording "${recording.title}". The actual implementation would use a speech-to-text service to generate the real transcript from the audio data.`;

      const mockSummary = `Summary: This recording discusses ${recording.title} and contains important information that was captured during the session.`;

      await updateTranscriptMutation.mutateAsync({
        id: recording.id,
        transcript: mockTranscript,
        summary: mockSummary
      });
    } catch (error) {
      toast({
        title: "Transcription Failed",
        description: "Could not generate transcript. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsTranscribing(false);
    }
  };

  // Share recording
  const shareRecording = (recording: VoiceRecording) => {
    setShareDialog({ open: true, recording });
  };

  // Copy transcript to clipboard
  const copyTranscript = async (transcript: string) => {
    try {
      await navigator.clipboard.writeText(transcript);
      toast({
        title: "Copied",
        description: "Transcript copied to clipboard.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Could not copy to clipboard.",
        variant: "destructive",
      });
    }
  };

  // Share via email
  const shareViaEmail = (recording: VoiceRecording) => {
    const subject = encodeURIComponent(`Voice Recording: ${recording.title}`);
    const body = encodeURIComponent(`
Voice Recording: ${recording.title}
Duration: ${Math.floor(recording.duration / 60)}:${(recording.duration % 60).toString().padStart(2, '0')}
Recorded: ${format(new Date(recording.createdAt), 'PPP')}

${recording.transcript ? `Transcript:\n${recording.transcript}` : 'No transcript available.'}

${recording.summary ? `\nSummary:\n${recording.summary}` : ''}
    `);
    window.open(`mailto:?subject=${subject}&body=${body}`);
  };

  // Share via WhatsApp
  const shareViaWhatsApp = (recording: VoiceRecording) => {
    const text = encodeURIComponent(`Voice Recording: ${recording.title}\n${recording.transcript || 'Audio recording shared'}`);
    window.open(`https://wa.me/?text=${text}`);
  };

  // Share via social media
  const shareViaSocial = (platform: string, recording: VoiceRecording) => {
    const text = encodeURIComponent(`Check out this voice recording: ${recording.title}`);
    const url = encodeURIComponent(window.location.href);

    switch (platform) {
      case 'twitter':
        window.open(`https://twitter.com/intent/tweet?text=${text}&url=${url}`);
        break;
      case 'facebook':
        window.open(`https://www.facebook.com/sharer/sharer.php?u=${url}`);
        break;
      case 'linkedin':
        window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${url}`);
        break;
    }
  };

  // Format duration
  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Handle form submission
  const onSubmit = async (data: RecordingFormData) => {
    if (!currentRecording) return;

    const reader = new FileReader();
    reader.onload = () => {
      const base64 = (reader.result as string).split(',')[1];
      createRecordingMutation.mutate({
        title: data.title,
        duration,
        blob: base64,
        transcript: liveTranscript || "",
        transcriptLanguage: selectedLanguage,
        summary: "",
        tags: [],
      });
    };
    reader.readAsDataURL(currentRecording);
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Recording Controls */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Mic className="h-5 w-5" />
            <span>Enhanced Voice Recorder</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Language Selection */}
          <div className="flex items-center space-x-4">
            <Label className="flex items-center space-x-2">
              <Languages className="h-4 w-4" />
              <span>Transcription Language:</span>
            </Label>
            <Select value={selectedLanguage} onValueChange={setSelectedLanguage}>
              <SelectTrigger className="w-48">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {SUPPORTED_LANGUAGES.map((lang) => (
                  <SelectItem key={lang.code} value={lang.code}>
                    {lang.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Recording Status */}
          <div className="flex items-center justify-between p-4 bg-slate-50 dark:bg-slate-800 rounded-lg">
            <div className="flex items-center space-x-4">
              <div className={`w-3 h-3 rounded-full ${isRecording ? 'bg-red-500 animate-pulse' : 'bg-gray-400'}`} />
              <span className="font-medium">
                {isRecording ? (isPaused ? 'Paused' : 'Recording') : 'Ready'}
              </span>
              <Badge variant="outline" className="flex items-center space-x-1">
                <Clock className="h-3 w-3" />
                <span>{formatDuration(duration)}</span>
              </Badge>
            </div>

            <div className="flex items-center space-x-2">
              {!isRecording ? (
                <Button onClick={startRecording} className="bg-red-500 hover:bg-red-600">
                  <Mic className="h-4 w-4 mr-2" />
                  Start Recording
                </Button>
              ) : (
                <>
                  <Button onClick={togglePause} variant="outline">
                    {isPaused ? <Play className="h-4 w-4" /> : <Pause className="h-4 w-4" />}
                  </Button>
                  <Button onClick={stopRecording} variant="destructive">
                    <Square className="h-4 w-4" />
                  </Button>
                </>
              )}
            </div>
          </div>

          {/* Live Transcript */}
          {isRecording && liveTranscript && (
            <div className="p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
              <div className="flex items-center space-x-2 mb-2">
                <FileText className="h-4 w-4 text-blue-600" />
                <span className="text-sm font-medium text-blue-600 dark:text-blue-400">Live Transcript</span>
              </div>
              <p className="text-sm text-slate-700 dark:text-slate-300">{liveTranscript}</p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Recordings List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <FileAudio className="h-5 w-5" />
            <span>Your Recordings ({recordings.length})</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {recordings.length === 0 ? (
            <div className="text-center py-8 text-slate-500">
              <FileAudio className="h-12 w-12 mx-auto mb-4 text-slate-300" />
              <p>No recordings yet. Start your first recording above!</p>
            </div>
          ) : (
            <div className="space-y-3">
              {recordings.map((recording: VoiceRecording) => (
                <div key={recording.id} className="p-4 border rounded-lg">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex-1">
                      <h3 className="font-medium text-slate-900 dark:text-slate-100">{recording.title}</h3>
                      <div className="flex items-center space-x-4 text-sm text-slate-500">
                        <span className="flex items-center space-x-1">
                          <Clock className="h-3 w-3" />
                          <span>{formatDuration(recording.duration)}</span>
                        </span>
                        <span>{format(new Date(recording.createdAt), 'PPp')}</span>
                        {recording.transcriptLanguage && (
                          <Badge variant="outline" className="text-xs">
                            {SUPPORTED_LANGUAGES.find(l => l.code === recording.transcriptLanguage)?.name}
                          </Badge>
                        )}
                      </div>
                    </div>

                    <div className="flex items-center space-x-2">
                      {playingId === recording.id ? (
                        <Button onClick={stopAudio} size="sm" variant="outline">
                          <Square className="h-4 w-4" />
                        </Button>
                      ) : (
                        <Button onClick={() => playAudio(recording)} size="sm" variant="outline">
                          <Play className="h-4 w-4" />
                        </Button>
                      )}

                      {recording.transcript ? (
                        <Button
                          onClick={() => setShowTranscript(prev => ({
                            ...prev,
                            [recording.id]: !prev[recording.id]
                          }))}
                          size="sm"
                          variant="outline"
                        >
                          {showTranscript[recording.id] ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                        </Button>
                      ) : (
                        <Button
                          onClick={() => generateTranscript(recording)}
                          size="sm"
                          variant="outline"
                          disabled={isTranscribing}
                        >
                          <FileText className="h-4 w-4" />
                        </Button>
                      )}

                      <Button onClick={() => shareRecording(recording)} size="sm" variant="outline">
                        <Share2 className="h-4 w-4" />
                      </Button>

                      <Button
                        onClick={() => deleteRecordingMutation.mutate(recording.id)}
                        size="sm"
                        variant="outline"
                        className="text-red-600 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>

                  {/* Transcript Display */}
                  {showTranscript[recording.id] && recording.transcript && (
                    <div className="mt-4 p-4 bg-slate-50 dark:bg-slate-800 rounded-lg">
                      <div className="flex items-center justify-between mb-2">
                        <span className="text-sm font-medium text-slate-700 dark:text-slate-300">Transcript</span>
                        <Button
                          onClick={() => copyTranscript(recording.transcript!)}
                          size="sm"
                          variant="ghost"
                        >
                          <Copy className="h-3 w-3" />
                        </Button>
                      </div>
                      <p className="text-sm text-slate-600 dark:text-slate-400 whitespace-pre-wrap">
                        {recording.transcript}
                      </p>
                      {recording.summary && (
                        <div className="mt-3 pt-3 border-t">
                          <span className="text-sm font-medium text-slate-700 dark:text-slate-300">Summary:</span>
                          <p className="text-sm text-slate-600 dark:text-slate-400 mt-1">{recording.summary}</p>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Save Recording Dialog */}
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Save Recording</DialogTitle>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="title"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Recording Title</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter a title for your recording" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {liveTranscript && (
                <div className="space-y-2">
                  <Label>Live Transcript Preview</Label>
                  <Textarea
                    value={liveTranscript}
                    onChange={(e) => setLiveTranscript(e.target.value)}
                    placeholder="Transcript will appear here..."
                    rows={4}
                  />
                </div>
              )}

              <div className="flex justify-end space-x-2">
                <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)}>
                  Cancel
                </Button>
                <Button type="submit" disabled={createRecordingMutation.isPending}>
                  {createRecordingMutation.isPending ? "Saving..." : "Save Recording"}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Share Dialog */}
      <Dialog open={shareDialog.open} onOpenChange={(open) => setShareDialog({ open, recording: null })}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Share Recording</DialogTitle>
          </DialogHeader>
          {shareDialog.recording && (
            <div className="space-y-4">
              <div className="text-center">
                <h3 className="font-medium">{shareDialog.recording.title}</h3>
                <p className="text-sm text-slate-500">
                  {formatDuration(shareDialog.recording.duration)} ‚Ä¢ {format(new Date(shareDialog.recording.createdAt), 'PPP')}
                </p>
              </div>

              <Separator />

              <div className="grid grid-cols-2 gap-3">
                <Button
                  onClick={() => shareViaEmail(shareDialog.recording!)}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <Mail className="h-4 w-4" />
                  <span>Email</span>
                </Button>

                <Button
                  onClick={() => shareViaWhatsApp(shareDialog.recording!)}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <MessageSquare className="h-4 w-4" />
                  <span>WhatsApp</span>
                </Button>

                <Button
                  onClick={() => shareViaSocial('twitter', shareDialog.recording!)}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <Twitter className="h-4 w-4" />
                  <span>Twitter</span>
                </Button>

                <Button
                  onClick={() => shareViaSocial('facebook', shareDialog.recording!)}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <Facebook className="h-4 w-4" />
                  <span>Facebook</span>
                </Button>

                <Button
                  onClick={() => shareViaSocial('linkedin', shareDialog.recording!)}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <Linkedin className="h-4 w-4" />
                  <span>LinkedIn</span>
                </Button>

                {shareDialog.recording.transcript && (
                  <Button
                    onClick={() => copyTranscript(shareDialog.recording!.transcript!)}
                    variant="outline"
                    className="flex items-center space-x-2"
                  >
                    <Copy className="h-4 w-4" />
                    <span>Copy Text</span>
                  </Button>
                )}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/tools/file-convert.content:
import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { 
  Upload, Download, FileText, Image, FileAudio, FileVideo, 
  File, Trash2, RefreshCw, CheckCircle, AlertCircle 
} from "lucide-react";

type FileType = "image" | "document" | "audio" | "video";
type ConversionStatus = "pending" | "converting" | "completed" | "error";

interface FileItem {
  id: string;
  file: File;
  originalFormat: string;
  targetFormat: string;
  status: ConversionStatus;
  progress: number;
  downloadUrl?: string;
  error?: string;
}

const supportedFormats = {
  image: {
    input: ["jpg", "jpeg", "png", "gif", "bmp", "webp", "tiff"],
    output: ["jpg", "png", "webp", "gif", "bmp", "pdf"]
  },
  document: {
    input: ["txt", "md", "html", "csv", "json"],
    output: ["txt", "pdf", "html", "md", "docx"]
  },
  audio: {
    input: ["mp3", "wav", "ogg", "m4a", "flac"],
    output: ["mp3", "wav", "ogg", "m4a"]
  },
  video: {
    input: ["mp4", "avi", "mov", "mkv", "webm"],
    output: ["mp4", "webm", "gif", "avi"]
  }
};

export default function FileConverterTool() {
  const [files, setFiles] = useState<FileItem[]>([]);
  const [dragActive, setDragActive] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const getFileType = (fileName: string): FileType => {
    const ext = fileName.split('.').pop()?.toLowerCase() || "";

    if (supportedFormats.image.input.includes(ext)) return "image";
    if (supportedFormats.document.input.includes(ext)) return "document";
    if (supportedFormats.audio.input.includes(ext)) return "audio";
    if (supportedFormats.video.input.includes(ext)) return "video";

    return "document"; // default
  };

  const getFileIcon = (type: FileType) => {
    switch (type) {
      case "image": return Image;
      case "document": return FileText;
      case "audio": return FileAudio;
      case "video": return FileVideo;
      default: return File;
    }
  };

  const handleFiles = (newFiles: FileList) => {
    Array.from(newFiles).forEach(file => {
      const ext = file.name.split('.').pop()?.toLowerCase() || "";
      const fileType = getFileType(file.name);

      if (!supportedFormats[fileType].input.includes(ext)) {
        toast({
          title: "Unsupported File",
          description: `${file.name} is not a supported file type`,
          variant: "destructive",
        });
        return;
      }

      const newFile: FileItem = {
        id: Date.now().toString() + Math.random().toString(),
        file,
        originalFormat: ext,
        targetFormat: supportedFormats[fileType].output[0],
        status: "pending",
        progress: 0
      };

      setFiles(prev => [...prev, newFile]);
    });
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFiles(e.dataTransfer.files);
    }
  };

  const convertFile = async (fileItem: FileItem) => {
    setFiles(prev => prev.map(f => 
      f.id === fileItem.id 
        ? { ...f, status: "converting" as ConversionStatus, progress: 0 }
        : f
    ));

    try {
      // Simulate conversion progress
      for (let i = 0; i <= 100; i += 10) {
        await new Promise(resolve => setTimeout(resolve, 200));
        setFiles(prev => prev.map(f => 
          f.id === fileItem.id 
            ? { ...f, progress: i }
            : f
        ));
      }

      // Simulate file conversion (in a real app, you'd use actual conversion libraries)
      const convertedBlob = await simulateConversion(fileItem);
      const downloadUrl = URL.createObjectURL(convertedBlob);

      setFiles(prev => prev.map(f => 
        f.id === fileItem.id 
          ? { ...f, status: "completed" as ConversionStatus, downloadUrl, progress: 100 }
          : f
      ));

      toast({
        title: "Conversion Complete",
        description: `${fileItem.file.name} converted successfully`,
      });

    } catch (error) {
      setFiles(prev => prev.map(f => 
        f.id === fileItem.id 
          ? { ...f, status: "error" as ConversionStatus, error: "Conversion failed" }
          : f
      ));

      toast({
        title: "Conversion Failed",
        description: `Failed to convert ${fileItem.file.name}`,
        variant: "destructive",
      });
    }
  };

  const simulateConversion = async (fileItem: FileItem): Promise<Blob> => {
    // This is a simulation - in a real app you'd use proper conversion libraries
    // For now, we'll just return the original file with a different name
    const reader = new FileReader();

    return new Promise((resolve) => {
      reader.onload = () => {
        // Create a new blob with the target format
        const blob = new Blob([fileItem.file], { 
          type: getMimeType(fileItem.targetFormat) 
        });
        resolve(blob);
      };
      reader.readAsArrayBuffer(fileItem.file);
    });
  };

  const getMimeType = (format: string): string => {
    const mimeTypes: { [key: string]: string } = {
      jpg: "image/jpeg",
      jpeg: "image/jpeg",
      png: "image/png",
      webp: "image/webp",
      gif: "image/gif",
      pdf: "application/pdf",
      txt: "text/plain",
      html: "text/html",
      mp3: "audio/mpeg",
      wav: "audio/wav",
      mp4: "video/mp4",
      webm: "video/webm"
    };
    return mimeTypes[format] || "application/octet-stream";
  };

  const updateTargetFormat = (fileId: string, format: string) => {
    setFiles(prev => prev.map(f => 
      f.id === fileId ? { ...f, targetFormat: format } : f
    ));
  };

  const removeFile = (fileId: string) => {
    setFiles(prev => {
      const file = prev.find(f => f.id === fileId);
      if (file?.downloadUrl) {
        URL.revokeObjectURL(file.downloadUrl);
      }
      return prev.filter(f => f.id !== fileId);
    });
  };

  const downloadFile = (fileItem: FileItem) => {
    if (fileItem.downloadUrl) {
      const link = document.createElement('a');
      link.href = fileItem.downloadUrl;
      link.download = `${fileItem.file.name.split('.')[0]}.${fileItem.targetFormat}`;
      link.click();
    }
  };

  const convertAllPending = () => {
    files
      .filter(f => f.status === "pending")
      .forEach(file => convertFile(file));
  };

  const clearCompleted = () => {
    setFiles(prev => {
      const completed = prev.filter(f => f.status === "completed");
      completed.forEach(f => {
        if (f.downloadUrl) URL.revokeObjectURL(f.downloadUrl);
      });
      return prev.filter(f => f.status !== "completed");
    });
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">File Converter</h2>
          <p className="text-slate-600">Convert files between different formats</p>
        </div>
      </div>

      {/* Upload Area */}
      <Card>
        <CardContent className="p-6">
          <div
            className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
              dragActive 
                ? "border-blue-500 bg-blue-50" 
                : "border-slate-300 hover:border-slate-400"
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            <Upload className="w-12 h-12 mx-auto mb-4 text-slate-400" />
            <p className="text-lg font-medium text-slate-900 mb-2">
              Drop files here or click to upload
            </p>
            <p className="text-sm text-slate-500 mb-4">
              Supports images, documents, audio, and video files
            </p>
            <Button onClick={() => fileInputRef.current?.click()}>
              Choose Files
            </Button>
            <input
              ref={fileInputRef}
              type="file"
              multiple
              className="hidden"
              onChange={(e) => e.target.files && handleFiles(e.target.files)}
            />
          </div>
        </CardContent>
      </Card>

      {/* Supported Formats */}
      <Card>
        <CardHeader>
          <CardTitle>Supported Formats</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {Object.entries(supportedFormats).map(([type, formats]) => {
              const IconComponent = getFileIcon(type as FileType);
              return (
                <div key={type} className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <IconComponent className="w-5 h-5" />
                    <h4 className="font-medium capitalize">{type}</h4>
                  </div>
                  <div className="text-sm text-slate-600">
                    <p>Input: {formats.input.join(", ")}</p>
                    <p>Output: {formats.output.join(", ")}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* File List */}
      {files.length > 0 && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Files ({files.length})</CardTitle>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={convertAllPending}
                  disabled={!files.some(f => f.status === "pending")}
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Convert All
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={clearCompleted}
                  disabled={!files.some(f => f.status === "completed")}
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Clear Completed
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {files.map((fileItem) => {
                const IconComponent = getFileIcon(getFileType(fileItem.file.name));
                const fileType = getFileType(fileItem.file.name);

                return (
                  <div key={fileItem.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <div className="flex items-center space-x-3">
                        <IconComponent className="w-6 h-6 text-slate-500" />
                        <div>
                          <p className="font-medium text-slate-900">{fileItem.file.name}</p>
                          <p className="text-sm text-slate-500">
                            {(fileItem.file.size / 1024 / 1024).toFixed(2)} MB
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center space-x-2">
                        {fileItem.status === "pending" && (
                          <Badge variant="secondary">Pending</Badge>
                        )}
                        {fileItem.status === "converting" && (
                          <Badge className="bg-blue-100 text-blue-800">Converting</Badge>
                        )}
                        {fileItem.status === "completed" && (
                          <Badge className="bg-green-100 text-green-800">
                            <CheckCircle className="w-3 h-3 mr-1" />
                            Completed
                          </Badge>
                        )}
                        {fileItem.status === "error" && (
                          <Badge variant="destructive">
                            <AlertCircle className="w-3 h-3 mr-1" />
                            Error
                          </Badge>
                        )}

                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => removeFile(fileItem.id)}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>

                    <div className="flex items-center space-x-4 mb-3">
                      <div className="flex items-center space-x-2">
                        <span className="text-sm text-slate-600">Convert to:</span>
                        <Select
                          value={fileItem.targetFormat}
                          onValueChange={(value) => updateTargetFormat(fileItem.id, value)}
                          disabled={fileItem.status === "converting"}
                        >
                          <SelectTrigger className="w-24">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {supportedFormats[fileType].output.map(format => (
                              <SelectItem key={format} value={format}>
                                {format.toUpperCase()}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="flex space-x-2">
                        {fileItem.status === "pending" && (
                          <Button
                            size="sm"
                            onClick={() => convertFile(fileItem)}
                          >
                            Convert
                          </Button>
                        )}

                        {fileItem.status === "completed" && (
                          <Button
                            size="sm"
                            onClick={() => downloadFile(fileItem)}
                          >
                            <Download className="w-4 h-4 mr-2" />
                            Download
                          </Button>
                        )}

                        {fileItem.status === "error" && (
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => convertFile(fileItem)}
                          >
                            Retry
                          </Button>
                        )}
                      </div>
                    </div>

                    {fileItem.status === "converting" && (
                      <Progress value={fileItem.progress} className="w-full" />
                    )}

                    {fileItem.error && (
                      <p className="text-sm text-red-600 mt-2">{fileItem.error}</p>
                    )}
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/tools/flashcards.t.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Plus, Trash2, Edit, RotateCcw, Eye, BookOpen, Brain } from "lucide-react";
import { format } from "date-fns";
import type { FlashcardDeck, Flashcard } from "@shared/schema";

const deckSchema = z.object({
  title: z.string().min(1, "Title is required"),
  description: z.string().optional(),
});

const flashcardSchema = z.object({
  front: z.string().min(1, "Front side is required"),
  back: z.string().min(1, "Back side is required"),
  deckId: z.number(),
});

type DeckFormData = z.infer<typeof deckSchema>;
type FlashcardFormData = z.infer<typeof flashcardSchema>;

export default function FlashcardsTool() {
  const [selectedDeck, setSelectedDeck] = useState<FlashcardDeck | null>(null);
  const [studyMode, setStudyMode] = useState(false);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [isDeckDialogOpen, setIsDeckDialogOpen] = useState(false);
  const [isCardDialogOpen, setIsCardDialogOpen] = useState(false);
  const [editingCard, setEditingCard] = useState<Flashcard | null>(null);

  const deckForm = useForm<DeckFormData>({
    resolver: zodResolver(deckSchema),
    defaultValues: {
      title: "",
      description: "",
    },
  });

  const cardForm = useForm<FlashcardFormData>({
    resolver: zodResolver(flashcardSchema),
    defaultValues: {
      front: "",
      back: "",
      deckId: 0,
    },
  });

  const { data: decks = [], isLoading: decksLoading } = useQuery<FlashcardDeck[]>({
    queryKey: ["/api/flashcard-decks"],
  });

  const { data: flashcards = [], isLoading: cardsLoading } = useQuery<Flashcard[]>({
    queryKey: ["/api/flashcards", selectedDeck?.id],
    enabled: !!selectedDeck,
  });

  const createDeckMutation = useMutation({
    mutationFn: (data: DeckFormData) => apiRequest("POST", "/api/flashcard-decks", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcard-decks"] });
      setIsDeckDialogOpen(false);
      deckForm.reset();
    },
  });

  const deleteDeckMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/flashcard-decks/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcard-decks"] });
      setSelectedDeck(null);
    },
  });

  const createCardMutation = useMutation({
    mutationFn: (data: FlashcardFormData) => apiRequest("POST", "/api/flashcards", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcards", selectedDeck?.id] });
      setIsCardDialogOpen(false);
      cardForm.reset();
      setEditingCard(null);
    },
  });

  const updateCardMutation = useMutation({
    mutationFn: ({ id, ...data }: { id: number } & Partial<Flashcard>) =>
      apiRequest("PATCH", `/api/flashcards/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcards", selectedDeck?.id] });
      setIsCardDialogOpen(false);
      cardForm.reset();
      setEditingCard(null);
    },
  });

  const deleteCardMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/flashcards/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcards", selectedDeck?.id] });
    },
  });

  const onDeckSubmit = (data: DeckFormData) => {
    createDeckMutation.mutate(data);
  };

  const onCardSubmit = (data: FlashcardFormData) => {
    if (editingCard) {
      updateCardMutation.mutate({ id: editingCard.id, ...data });
    } else {
      createCardMutation.mutate(data);
    }
  };

  const startStudy = () => {
    if (flashcards.length > 0) {
      setStudyMode(true);
      setCurrentCardIndex(0);
      setShowAnswer(false);
    }
  };

  const nextCard = () => {
    setCurrentCardIndex((prev) => (prev + 1) % flashcards.length);
    setShowAnswer(false);
  };

  const previousCard = () => {
    setCurrentCardIndex((prev) => (prev - 1 + flashcards.length) % flashcards.length);
    setShowAnswer(false);
  };

  const openCardDialog = (card?: Flashcard) => {
    if (card) {
      setEditingCard(card);
      cardForm.setValue("front", card.front);
      cardForm.setValue("back", card.back);
      cardForm.setValue("deckId", card.deckId);
    } else {
      setEditingCard(null);
      cardForm.reset();
      cardForm.setValue("deckId", selectedDeck?.id || 0);
    }
    setIsCardDialogOpen(true);
  };

  if (decksLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  // Study Mode
  if (studyMode && selectedDeck && flashcards.length > 0) {
    const currentCard = flashcards[currentCardIndex];

    return (
      <div className="max-w-2xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-slate-900">Studying: {selectedDeck.title}</h2>
            <p className="text-slate-600">Card {currentCardIndex + 1} of {flashcards.length}</p>
          </div>
          <Button variant="outline" onClick={() => setStudyMode(false)}>
            Exit Study Mode
          </Button>
        </div>

        <Card className="min-h-[400px] flex flex-col">
          <CardContent className="flex-1 flex flex-col justify-center items-center p-8">
            <div className="text-center space-y-6 w-full">
              <Badge variant="outline">{showAnswer ? "Answer" : "Question"}</Badge>

              <div className="text-xl md:text-2xl text-slate-900 leading-relaxed">
                {showAnswer ? currentCard.back : currentCard.front}
              </div>

              {!showAnswer ? (
                <Button onClick={() => setShowAnswer(true)} size="lg">
                  <Eye className="w-4 h-4 mr-2" />
                  Show Answer
                </Button>
              ) : (
                <div className="flex space-x-4">
                  <Button onClick={previousCard} variant="outline">
                    Previous
                  </Button>
                  <Button onClick={nextCard}>
                    Next Card
                  </Button>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        <div className="flex justify-center">
          <div className="bg-slate-100 rounded-full px-4 py-2">
            <div className="flex space-x-1">
              {flashcards.map((_, index) => (
                <div
                  key={index}
                  className={`w-2 h-2 rounded-full ${
                    index === currentCardIndex ? "bg-primary" : "bg-slate-300"
                  }`}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Deck View
  if (selectedDeck) {
    return (
      <div className="max-w-6xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <div className="flex items-center space-x-2 mb-2">
              <Button variant="ghost" size="sm" onClick={() => setSelectedDeck(null)}>
                ‚Üê Back to Decks
              </Button>
            </div>
            <h2 className="text-2xl font-bold text-slate-900">{selectedDeck.title}</h2>
            {selectedDeck.description && (
              <p className="text-slate-600">{selectedDeck.description}</p>
            )}
          </div>
          <div className="flex space-x-2">
            {flashcards.length > 0 && (
              <Button onClick={startStudy}>
                <Brain className="w-4 h-4 mr-2" />
                Study
              </Button>
            )}
            <Dialog open={isCardDialogOpen} onOpenChange={setIsCardDialogOpen}>
              <DialogTrigger asChild>
                <Button onClick={() => openCardDialog()}>
                  <Plus className="w-4 h-4 mr-2" />
                  Add Card
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>
                    {editingCard ? "Edit Flashcard" : "Add New Flashcard"}
                  </DialogTitle>
                </DialogHeader>
                <Form {...cardForm}>
                  <form onSubmit={cardForm.handleSubmit(onCardSubmit)} className="space-y-4">
                    <FormField
                      control={cardForm.control}
                      name="front"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Front (Question)</FormLabel>
                          <FormControl>
                            <Textarea placeholder="Enter the question..." {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={cardForm.control}
                      name="back"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Back (Answer)</FormLabel>
                          <FormControl>
                            <Textarea placeholder="Enter the answer..." {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <div className="flex justify-end space-x-2">
                      <Button 
                        type="button" 
                        variant="outline" 
                        onClick={() => setIsCardDialogOpen(false)}
                      >
                        Cancel
                      </Button>
                      <Button 
                        type="submit" 
                        disabled={createCardMutation.isPending || updateCardMutation.isPending}
                      >
                        {createCardMutation.isPending || updateCardMutation.isPending
                          ? "Saving..." 
                          : editingCard ? "Update Card" : "Add Card"
                        }
                      </Button>
                    </div>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {cardsLoading ? (
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        ) : flashcards.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center">
              <BookOpen className="w-12 h-12 text-slate-400 mx-auto mb-4" />
              <div className="text-slate-400 mb-4">
                No flashcards in this deck yet. Add your first flashcard to get started!
              </div>
            </CardContent>
          </Card>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
            {flashcards.map((card) => (
              <Card key={card.id} className="hover:shadow-lg transition-shadow">
                <CardContent className="p-4">
                  <div className="space-y-3">
                    <div>
                      <Badge variant="outline" className="mb-2">Question</Badge>
                      <p className="text-sm text-slate-900">{card.front}</p>
                    </div>
                    <div>
                      <Badge variant="outline" className="mb-2">Answer</Badge>
                      <p className="text-sm text-slate-600">{card.back}</p>
                    </div>
                    <div className="flex justify-end space-x-1 pt-2 border-t">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => openCardDialog(card)}
                        className="text-slate-500 hover:text-slate-700"
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => deleteCardMutation.mutate(card.id)}
                        className="text-red-500 hover:text-red-700 hover:bg-red-50"
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    );
  }

  // Decks Overview
  return (
    <div className="max-w-6xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Flashcards</h2>
          <p className="text-slate-600">Study with spaced repetition algorithm</p>
        </div>
        <Dialog open={isDeckDialogOpen} onOpenChange={setIsDeckDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              New Deck
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Deck</DialogTitle>
            </DialogHeader>
            <Form {...deckForm}>
              <form onSubmit={deckForm.handleSubmit(onDeckSubmit)} className="space-y-4">
                <FormField
                  control={deckForm.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Title</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter deck title..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={deckForm.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Enter deck description..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setIsDeckDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createDeckMutation.isPending}>
                    {createDeckMutation.isPending ? "Creating..." : "Create Deck"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {decks.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <BookOpen className="w-12 h-12 text-slate-400 mx-auto mb-4" />
            <div className="text-slate-400 mb-4">
              No flashcard decks yet. Create your first deck to start studying!
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {decks.map((deck) => (
            <Card 
              key={deck.id} 
              className="hover:shadow-lg transition-all cursor-pointer"
              onClick={() => setSelectedDeck(deck)}
            >
              <CardHeader>
                <div className="flex items-start justify-between">
                  <CardTitle className="text-lg">{deck.title}</CardTitle>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      deleteDeckMutation.mutate(deck.id);
                    }}
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
                {deck.description && (
                  <p className="text-sm text-slate-600 line-clamp-2">{deck.description}</p>
                )}
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between text-sm text-slate-500">
                  <span>Created {format(new Date(deck.createdAt), "MMM dd, yyyy")}</span>
                  <Badge variant="secondary">
                    0 cards {/* This would be populated from a separate query in a real app */}
                  </Badge>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

