import React, { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Progress } from "@/components/ui/progress";
import { 
  Play, 
  Pause, 
  Square, 
  Target, 
  Volume2, 
  VolumeX, 
  Settings,
  Coffee,
  BookOpen,
  Lightbulb,
  CheckCircle
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface FocusSession {
  id: string;
  goal: string;
  duration: number;
  completed: boolean;
  startTime: Date;
  endTime?: Date;
}

interface FocusSettings {
  soundEnabled: boolean;
  breakReminders: boolean;
  blockingMode: boolean;
  sessionGoal: string;
}

export default function FocusMode() {
  const [timeLeft, setTimeLeft] = useState(25 * 60); // 25 minutes default
  const [isRunning, setIsRunning] = useState(false);
  const [duration, setDuration] = useState([25]);
  const [currentGoal, setCurrentGoal] = useState('');
  const [sessions, setSessions] = useState<FocusSession[]>([]);
  const [settings, setSettings] = useState<FocusSettings>({
    soundEnabled: true,
    breakReminders: true,
    blockingMode: false,
    sessionGoal: ''
  });
  const [sessionStart, setSessionStart] = useState<Date | null>(null);
  const [showSettings, setShowSettings] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    const savedSessions = localStorage.getItem('focusMode_sessions');
    if (savedSessions) {
      setSessions(JSON.parse(savedSessions).map((s: any) => ({
        ...s,
        startTime: new Date(s.startTime),
        endTime: s.endTime ? new Date(s.endTime) : undefined
      })));
    }

    const savedSettings = localStorage.getItem('focusMode_settings');
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }
  }, []);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isRunning && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            completeSession();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isRunning, timeLeft]);

  useEffect(() => {
    localStorage.setItem('focusMode_sessions', JSON.stringify(sessions));
  }, [sessions]);

  useEffect(() => {
    localStorage.setItem('focusMode_settings', JSON.stringify(settings));
  }, [settings]);

  const formatTime = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startSession = () => {
    if (!currentGoal.trim()) {
      toast({
        title: "Set a Goal",
        description: "Please set a goal for your focus session.",
        variant: "destructive"
      });
      return;
    }

    setIsRunning(true);
    setSessionStart(new Date());
    setTimeLeft(duration[0] * 60);

    if (settings.soundEnabled) {
      // Play start sound (would implement with audio API)
      toast({
        title: "Focus Session Started",
        description: `${duration[0]} minute session for: ${currentGoal}`
      });
    }
  };

  const pauseSession = () => {
    setIsRunning(false);
  };

  const stopSession = () => {
    if (sessionStart) {
      const session: FocusSession = {
        id: Date.now().toString(),
        goal: currentGoal,
        duration: duration[0] * 60 - timeLeft,
        completed: false,
        startTime: sessionStart,
        endTime: new Date()
      };

      setSessions(prev => [session, ...prev]);
      toast({
        title: "Session Stopped",
        description: "Your focus session has been saved."
      });
    }

    resetSession();
  };

  const completeSession = () => {
    if (sessionStart) {
      const session: FocusSession = {
        id: Date.now().toString(),
        goal: currentGoal,
        duration: duration[0] * 60,
        completed: true,
        startTime: sessionStart,
        endTime: new Date()
      };

      setSessions(prev => [session, ...prev]);

      if (settings.soundEnabled) {
        // Play completion sound
      }

      toast({
        title: "Session Complete!",
        description: `Great job! You completed your ${duration[0]}-minute focus session.`
      });

      if (settings.breakReminders) {
        toast({
          title: "Take a Break",
          description: "Consider taking a 5-minute break before your next session."
        });
      }
    }

    resetSession();
  };

  const resetSession = () => {
    setIsRunning(false);
    setTimeLeft(duration[0] * 60);
    setSessionStart(null);
    setCurrentGoal('');
  };

  const progress = ((duration[0] * 60 - timeLeft) / (duration[0] * 60)) * 100;

  const focusTips = [
    "Turn off notifications on your devices",
    "Keep a glass of water nearby",
    "Use the two-minute rule for small tasks",
    "Break large tasks into smaller chunks",
    "Remove distractions from your workspace"
  ];

  const todaysStats = sessions.filter(s => {
    const today = new Date();
    return s.startTime.toDateString() === today.toDateString();
  });

  return (
    <div className="space-y-6">
      {/* Timer Card */}
      <Card className={isRunning ? "border-primary ring-2 ring-primary/20" : ""}>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span className="flex items-center gap-2">
              <Target className="w-5 h-5" />
              Focus Session
            </span>
            <div className="flex gap-2">
              <Badge variant={isRunning ? "default" : "secondary"}>
                {isRunning ? "Active" : "Ready"}
              </Badge>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowSettings(!showSettings)}
              >
                <Settings className="w-4 h-4" />
              </Button>
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Timer Display */}
          <div className="text-center space-y-4">
            <div className="text-6xl font-mono font-bold">
              {formatTime(timeLeft)}
            </div>
            <Progress value={progress} className="h-2" />
          </div>

          {/* Goal Input */}
          <div className="space-y-2">
            <Label htmlFor="goal">Session Goal</Label>
            <Textarea
              id="goal"
              value={currentGoal}
              onChange={(e) => setCurrentGoal(e.target.value)}
              placeholder="What do you want to focus on?"
              disabled={isRunning}
              rows={2}
            />
          </div>

          {/* Duration Slider */}
          {!isRunning && (
            <div className="space-y-2">
              <Label>Duration: {duration[0]} minutes</Label>
              <Slider
                value={duration}
                onValueChange={setDuration}
                max={120}
                min={5}
                step={5}
                className="w-full"
              />
            </div>
          )}

          {/* Controls */}
          <div className="flex gap-2 justify-center">
            {!isRunning ? (
              <Button onClick={startSession} className="flex-1">
                <Play className="w-4 h-4 mr-2" />
                Start Focus Session
              </Button>
            ) : (
              <>
                <Button onClick={pauseSession} variant="outline" className="flex-1">
                  <Pause className="w-4 h-4 mr-2" />
                  Pause
                </Button>
                <Button onClick={stopSession} variant="destructive" className="flex-1">
                  <Square className="w-4 h-4 mr-2" />
                  Stop
                </Button>
              </>
            )}
          </div>

          {/* Settings Panel */}
          {showSettings && (
            <Card className="bg-muted/50">
              <CardContent className="pt-6 space-y-4">
                <div className="flex items-center justify-between">
                  <Label htmlFor="sound">Sound Notifications</Label>
                  <Switch
                    id="sound"
                    checked={settings.soundEnabled}
                    onCheckedChange={(checked) => 
                      setSettings(prev => ({ ...prev, soundEnabled: checked }))
                    }
                  />
                </div>
                <div className="flex items-center justify-between">
                  <Label htmlFor="breaks">Break Reminders</Label>
                  <Switch
                    id="breaks"
                    checked={settings.breakReminders}
                    onCheckedChange={(checked) => 
                      setSettings(prev => ({ ...prev, breakReminders: checked }))
                    }
                  />
                </div>
                <div className="flex items-center justify-between">
                  <Label htmlFor="blocking">Distraction Blocking</Label>
                  <Switch
                    id="blocking"
                    checked={settings.blockingMode}
                    onCheckedChange={(checked) => 
                      setSettings(prev => ({ ...prev, blockingMode: checked }))
                    }
                  />
                </div>
              </CardContent>
            </Card>
          )}
        </CardContent>
      </Card>

      {/* Today's Stats */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <CheckCircle className="w-5 h-5" />
            Today's Progress
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <div className="text-2xl font-bold text-primary">
                {todaysStats.length}
              </div>
              <p className="text-sm text-muted-foreground">Sessions</p>
            </div>
            <div>
              <div className="text-2xl font-bold text-primary">
                {todaysStats.filter(s => s.completed).length}
              </div>
              <p className="text-sm text-muted-foreground">Completed</p>
            </div>
            <div>
              <div className="text-2xl font-bold text-primary">
                {Math.round(todaysStats.reduce((acc, s) => acc + s.duration, 0) / 60)}
              </div>
              <p className="text-sm text-muted-foreground">Minutes</p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Focus Tips */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lightbulb className="w-5 h-5" />
            Focus Tips
          </CardTitle>
        </CardHeader>
        <CardContent>
          <ul className="space-y-2">
            {focusTips.map((tip, index) => (
              <li key={index} className="flex items-start gap-2">
                <div className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0" />
                <span className="text-sm">{tip}</span>
              </li>
            ))}
          </ul>
        </CardContent>
      </Card>

      {/* Recent Sessions */}
      {sessions.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BookOpen className="w-5 h-5" />
              Recent Sessions
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {sessions.slice(0, 5).map(session => (
                <div key={session.id} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                  <div>
                    <p className="font-medium">{session.goal}</p>
                    <p className="text-sm text-muted-foreground">
                      {session.startTime.toLocaleDateString()} ‚Ä¢ {Math.round(session.duration / 60)} min
                    </p>
                  </div>
                  <Badge variant={session.completed ? "default" : "secondary"}>
                    {session.completed ? "Completed" : "Stopped"}
                  </Badge>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/tools/habit-tracke.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Plus, Trash2, Edit, Target, Calendar, TrendingUp, CheckCircle, Flame, Award } from "lucide-react";
import { format, startOfWeek, addDays, isSameDay, subDays, differenceInDays } from "date-fns";
import type { Habit, HabitLog } from "@shared/schema";

const habitSchema = z.object({
  title: z.string().min(1, "Title is required"),
  description: z.string().optional(),
  frequency: z.enum(["daily", "weekly", "monthly"]).default("daily"),
  targetDays: z.array(z.number()).default([1, 2, 3, 4, 5, 6, 7]),
  color: z.string().default("#2563EB"),
});

type HabitFormData = z.infer<typeof habitSchema>;

const colors = [
  "#2563EB", // Blue
  "#059669", // Emerald
  "#DC2626", // Red
  "#7C3AED", // Purple
  "#EA580C", // Orange
  "#0891B2", // Cyan
  "#CA8A04", // Yellow
  "#BE185D", // Pink
  "#059669", // Green
  "#6B7280", // Gray
];

const weekDays = [
  { value: 1, label: "Mon" },
  { value: 2, label: "Tue" },
  { value: 3, label: "Wed" },
  { value: 4, label: "Thu" },
  { value: 5, label: "Fri" },
  { value: 6, label: "Sat" },
  { value: 7, label: "Sun" },
];

export default function HabitTrackerTool() {
  const [selectedHabit, setSelectedHabit] = useState<Habit | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingHabit, setEditingHabit] = useState<Habit | null>(null);
  const [currentWeekStart, setCurrentWeekStart] = useState(startOfWeek(new Date(), { weekStartsOn: 1 }));

  const form = useForm<HabitFormData>({
    resolver: zodResolver(habitSchema),
    defaultValues: {
      title: "",
      description: "",
      frequency: "daily",
      targetDays: [1, 2, 3, 4, 5, 6, 7],
      color: "#2563EB",
    },
  });

  const { data: habits = [], isLoading: habitsLoading } = useQuery<Habit[]>({
    queryKey: ["/api/habits"],
  });

  const { data: habitLogs = [], isLoading: logsLoading } = useQuery<HabitLog[]>({
    queryKey: ["/api/habit-logs", selectedHabit?.id],
    enabled: !!selectedHabit,
  });

  const createHabitMutation = useMutation({
    mutationFn: (data: HabitFormData) => apiRequest("POST", "/api/habits", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/habits"] });
      setIsDialogOpen(false);
      form.reset();
      setEditingHabit(null);
    },
  });

  const updateHabitMutation = useMutation({
    mutationFn: ({ id, ...data }: { id: number } & Partial<Habit>) =>
      apiRequest("PATCH", `/api/habits/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/habits"] });
      setIsDialogOpen(false);
      form.reset();
      setEditingHabit(null);
    },
  });

  const deleteHabitMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/habits/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/habits"] });
      setSelectedHabit(null);
    },
  });

  const createLogMutation = useMutation({
    mutationFn: (data: { habitId: number; date: string; completed: boolean }) =>
      apiRequest("POST", "/api/habit-logs", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/habit-logs", selectedHabit?.id] });
      queryClient.invalidateQueries({ queryKey: ["/api/habits"] });
    },
  });

  const deleteLogMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/habit-logs/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/habit-logs", selectedHabit?.id] });
      queryClient.invalidateQueries({ queryKey: ["/api/habits"] });
    },
  });

  const onSubmit = (data: HabitFormData) => {
    if (editingHabit) {
      updateHabitMutation.mutate({ id: editingHabit.id, ...data });
    } else {
      createHabitMutation.mutate(data);
    }
  };

  const openHabitDialog = (habit?: Habit) => {
    if (habit) {
      setEditingHabit(habit);
      form.setValue("title", habit.title);
      form.setValue("description", habit.description || "");
      form.setValue("frequency", habit.frequency as "daily" | "weekly" | "monthly");
      form.setValue("targetDays", habit.targetDays as number[] || [1, 2, 3, 4, 5, 6, 7]);
      form.setValue("color", habit.color || "#2563EB");
    } else {
      setEditingHabit(null);
      form.reset();
    }
    setIsDialogOpen(true);
  };

  const toggleHabitCompletion = (habit: Habit, date: Date) => {
    const dateStr = format(date, "yyyy-MM-dd");
    const existingLog = habitLogs.find(log => 
      log.habitId === habit.id && 
      format(new Date(log.date), "yyyy-MM-dd") === dateStr
    );

    if (existingLog) {
      deleteLogMutation.mutate(existingLog.id);
    } else {
      createLogMutation.mutate({
        habitId: habit.id,
        date: date.toISOString(),
        completed: true,
      });
    }
  };

  const getHabitCompletion = (habit: Habit, date: Date) => {
    const dateStr = format(date, "yyyy-MM-dd");
    return habitLogs.some(log => 
      log.habitId === habit.id && 
      format(new Date(log.date), "yyyy-MM-dd") === dateStr
    );
  };

  const calculateStreak = (habit: Habit) => {
    if (habitLogs.length === 0) return 0;

    const sortedLogs = habitLogs
      .filter(log => log.habitId === habit.id)
      .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

    if (sortedLogs.length === 0) return 0;

    let streak = 0;
    let currentDate = new Date();
    currentDate.setHours(0, 0, 0, 0);

    for (let i = 0; i < 30; i++) { // Check last 30 days
      const checkDate = subDays(currentDate, i);
      const hasLog = sortedLogs.some(log => 
        isSameDay(new Date(log.date), checkDate)
      );

      if (hasLog) {
        streak++;
      } else if (i > 0) { // Allow for today to not break streak
        break;
      }
    }

    return streak;
  };

  const getWeekDays = () => {
    const days = [];
    for (let i = 0; i < 7; i++) {
      days.push(addDays(currentWeekStart, i));
    }
    return days;
  };

  const getCompletionRate = (habit: Habit, days: number = 30) => {
    const startDate = subDays(new Date(), days);
    const relevantLogs = habitLogs.filter(log => 
      log.habitId === habit.id && 
      new Date(log.date) >= startDate
    );

    return Math.round((relevantLogs.length / days) * 100);
  };

  if (habitsLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Habit Tracker</h2>
          <p className="text-slate-600">
            {habits.length} habits ‚Ä¢ Total streaks: {habits.reduce((sum, h) => sum + calculateStreak(h), 0)} days
          </p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={() => openHabitDialog()}>
              <Plus className="w-4 h-4 mr-2" />
              Add Habit
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                {editingHabit ? "Edit Habit" : "Create New Habit"}
              </DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Title</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter habit title..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Enter habit description..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="frequency"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Frequency</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="daily">Daily</SelectItem>
                            <SelectItem value="weekly">Weekly</SelectItem>
                            <SelectItem value="monthly">Monthly</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="color"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Color</FormLabel>
                        <div className="grid grid-cols-5 gap-2">
                          {colors.map((color) => (
                            <button
                              key={color}
                              type="button"
                              className={`w-8 h-8 rounded-full border-2 ${
                                field.value === color ? "border-slate-400" : "border-slate-200"
                              }`}
                              style={{ backgroundColor: color }}
                              onClick={() => field.onChange(color)}
                            />
                          ))}
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
                <FormField
                  control={form.control}
                  name="targetDays"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Target Days</FormLabel>
                      <div className="flex space-x-2">
                        {weekDays.map((day) => (
                          <div key={day.value} className="flex items-center space-x-1">
                            <Checkbox
                              checked={field.value.includes(day.value)}
                              onCheckedChange={(checked) => {
                                if (checked) {
                                  field.onChange([...field.value, day.value]);
                                } else {
                                  field.onChange(field.value.filter(d => d !== day.value));
                                }
                              }}
                            />
                            <label className="text-sm">{day.label}</label>
                          </div>
                        ))}
                      </div>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setIsDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createHabitMutation.isPending || updateHabitMutation.isPending}
                  >
                    {createHabitMutation.isPending || updateHabitMutation.isPending
                      ? "Saving..." 
                      : editingHabit ? "Update Habit" : "Create Habit"
                    }
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {habits.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <Target className="w-12 h-12 text-slate-400 mx-auto mb-4" />
            <div className="text-slate-400 mb-4">
              No habits yet. Create your first habit to start building positive routines!
            </div>
          </CardContent>
        </Card>
      ) : (
        <>
          {/* Habits Overview */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {habits.map((habit) => {
              const streak = calculateStreak(habit);
              const completionRate = getCompletionRate(habit);
              const todayCompleted = getHabitCompletion(habit, new Date());

              return (
                <Card 
                  key={habit.id} 
                  className="hover:shadow-lg transition-all cursor-pointer"
                  onClick={() => setSelectedHabit(habit)}
                >
                  <CardHeader className="pb-2">
                    <div className="flex items-start justify-between">
                      <div className="flex items-center space-x-2">
                        <div 
                          className="w-4 h-4 rounded-full" 
                          style={{ backgroundColor: habit.color }}
                        />
                        <CardTitle className="text-lg">{habit.title}</CardTitle>
                      </div>
                      <div className="flex space-x-1">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            openHabitDialog(habit);
                          }}
                          className="text-slate-500 hover:text-slate-700"
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            deleteHabitMutation.mutate(habit.id);
                          }}
                          className="text-red-600 hover:text-red-700 hover:bg-red-50"
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                    {habit.description && (
                      <p className="text-sm text-slate-600 line-clamp-2">{habit.description}</p>
                    )}
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      {/* Today's Status */}
                      <div className="flex items-center justify-between">
                        <span className="text-sm text-slate-600">Today</span>
                        <Button
                          size="sm"
                          variant={todayCompleted ? "default" : "outline"}
                          onClick={(e) => {
                            e.stopPropagation();
                            toggleHabitCompletion(habit, new Date());
                          }}
                          className="h-8"
                        >
                          {todayCompleted ? (
                            <CheckCircle className="w-4 h-4 mr-1" />
                          ) : (
                            <Target className="w-4 h-4 mr-1" />
                          )}
                          {todayCompleted ? "Done" : "Mark Done"}
                        </Button>
                      </div>

                      {/* Stats */}
                      <div className="grid grid-cols-2 gap-4 text-center">
                        <div>
                          <div className="flex items-center justify-center space-x-1">
                            <Flame className="w-4 h-4 text-orange-500" />
                            <span className="text-lg font-bold text-slate-900">{streak}</span>
                          </div>
                          <p className="text-xs text-slate-600">Day Streak</p>
                        </div>
                        <div>
                          <div className="flex items-center justify-center space-x-1">
                            <TrendingUp className="w-4 h-4 text-green-500" />
                            <span className="text-lg font-bold text-slate-900">{completionRate}%</span>
                          </div>
                          <p className="text-xs text-slate-600">30-Day Rate</p>
                        </div>
                      </div>

                      {/* Progress Bar */}
                      <div>
                        <Progress value={completionRate} className="h-2" />
                      </div>

                      {/* Frequency Badge */}
                      <div className="flex justify-between items-center">
                        <Badge variant="outline" className="text-xs">
                          {habit.frequency}
                        </Badge>
                        {streak >= 7 && (
                          <Badge variant="secondary" className="text-xs">
                            <Award className="w-3 h-3 mr-1" />
                            On Fire!
                          </Badge>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          {/* Weekly View */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center space-x-2">
                  <Calendar className="w-5 h-5" />
                  <span>Weekly Progress</span>
                </CardTitle>
                <div className="flex space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setCurrentWeekStart(subDays(currentWeekStart, 7))}
                  >
                    Previous Week
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setCurrentWeekStart(addDays(currentWeekStart, 7))}
                  >
                    Next Week
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr>
                      <th className="text-left p-2 font-medium text-slate-700">Habit</th>
                      {getWeekDays().map((day) => (
                        <th key={day.toString()} className="text-center p-2 font-medium text-slate-700">
                          <div>{format(day, "EEE")}</div>
                          <div className="text-xs text-slate-500">{format(day, "d")}</div>
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {habits.map((habit) => (
                      <tr key={habit.id} className="border-t">
                        <td className="p-2">
                          <div className="flex items-center space-x-2">
                            <div 
                              className="w-3 h-3 rounded-full" 
                              style={{ backgroundColor: habit.color }}
                            />
                            <span className="font-medium text-slate-900">{habit.title}</span>
                          </div>
                        </td>
                        {getWeekDays().map((day) => {
                          const isCompleted = getHabitCompletion(habit, day);
                          const isToday = isSameDay(day, new Date());

                          return (
                            <td key={day.toString()} className="p-2 text-center">
                              <Button
                                variant="ghost"
                                size="sm"
                                className={`w-8 h-8 p-0 rounded-full ${
                                  isCompleted 
                                    ? 'bg-green-100 text-green-700 hover:bg-green-200' 
                                    : isToday
                                      ? 'border-2 border-primary text-primary hover:bg-primary/10'
                                      : 'text-slate-400 hover:text-slate-600 hover:bg-slate-100'
                                }`}
                                onClick={() => toggleHabitCompletion(habit, day)}
                              >
                                {isCompleted ? (
                                  <CheckCircle className="w-4 h-4" />
                                ) : (
                                  <div className="w-4 h-4 border-2 border-current rounded-full" />
                                )}
                              </Button>
                            </td>
                          );
                        })}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </CardContent>
          </Card>
        </>
      )}
    </div>
  );
}

