import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Brain, Clock, CheckCircle, XCircle, RotateCcw } from "lucide-react";

interface Question {
  id: number;
  type: "pattern" | "logic" | "math" | "memory";
  question: string;
  options: string[];
  correct: number;
  explanation?: string;
}

const IQ_QUESTIONS: Question[] = [
  {
    id: 1,
    type: "pattern",
    question: "What comes next in the sequence: 2, 4, 8, 16, ?",
    options: ["24", "32", "20", "28"],
    correct: 1,
    explanation: "Each number is doubled: 2√ó2=4, 4√ó2=8, 8√ó2=16, 16√ó2=32"
  },
  {
    id: 2,
    type: "logic",
    question: "If all roses are flowers and some flowers are red, which statement is definitely true?",
    options: ["All roses are red", "Some roses are red", "All flowers are roses", "Some roses may be red"],
    correct: 3,
    explanation: "We can only conclude that some roses may be red, not that they definitely are."
  },
  {
    id: 3,
    type: "math",
    question: "If a train travels 120 miles in 2 hours, how fast is it going in mph?",
    options: ["50 mph", "60 mph", "70 mph", "80 mph"],
    correct: 1,
    explanation: "Speed = Distance √∑ Time = 120 miles √∑ 2 hours = 60 mph"
  },
  {
    id: 4,
    type: "pattern",
    question: "Complete the pattern: A1, B4, C9, D16, ?",
    options: ["E20", "E25", "F25", "E24"],
    correct: 1,
    explanation: "Each letter is paired with its position squared: A(1)¬≤=1, B(2)¬≤=4, C(3)¬≤=9, D(4)¬≤=16, E(5)¬≤=25"
  },
  {
    id: 5,
    type: "logic",
    question: "Which word doesn't belong: Dog, Cat, Bird, Fish, Car",
    options: ["Dog", "Cat", "Bird", "Car"],
    correct: 3,
    explanation: "Car is the only non-living thing, while the others are all animals."
  },
  {
    id: 6,
    type: "math",
    question: "What is 15% of 200?",
    options: ["25", "30", "35", "40"],
    correct: 1,
    explanation: "15% of 200 = 0.15 √ó 200 = 30"
  },
  {
    id: 7,
    type: "pattern",
    question: "What number should replace the question mark: 3, 6, 12, 24, ?",
    options: ["36", "48", "42", "50"],
    correct: 1,
    explanation: "Each number is doubled: 3√ó2=6, 6√ó2=12, 12√ó2=24, 24√ó2=48"
  },
  {
    id: 8,
    type: "logic",
    question: "If today is Wednesday, what day will it be in 10 days?",
    options: ["Thursday", "Friday", "Saturday", "Sunday"],
    correct: 2,
    explanation: "10 days from Wednesday: Thu(1), Fri(2), Sat(3), Sun(4), Mon(5), Tue(6), Wed(7), Thu(8), Fri(9), Sat(10)"
  },
  {
    id: 9,
    type: "math",
    question: "If x + 5 = 12, what is x?",
    options: ["5", "6", "7", "8"],
    correct: 2,
    explanation: "x + 5 = 12, so x = 12 - 5 = 7"
  },
  {
    id: 10,
    type: "pattern",
    question: "Complete the sequence: 1, 1, 2, 3, 5, 8, ?",
    options: ["11", "12", "13", "14"],
    correct: 2,
    explanation: "This is the Fibonacci sequence: each number is the sum of the two before it. 5 + 8 = 13"
  }
];

export default function IQTester() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState<number | null>(null);
  const [userAnswers, setUserAnswers] = useState<number[]>([]);
  const [showResult, setShowResult] = useState(false);
  const [timeLeft, setTimeLeft] = useState(600); // 10 minutes
  const [testStarted, setTestStarted] = useState(false);
  const [testCompleted, setTestCompleted] = useState(false);

  useEffect(() => {
    let timer: NodeJS.Timeout;
    if (testStarted && !testCompleted && timeLeft > 0) {
      timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    } else if (timeLeft === 0 && !testCompleted) {
      finishTest();
    }
    return () => clearTimeout(timer);
  }, [timeLeft, testStarted, testCompleted]);

  const startTest = () => {
    setTestStarted(true);
    setCurrentQuestion(0);
    setUserAnswers([]);
    setTestCompleted(false);
    setTimeLeft(600);
    setShowResult(false);
  };

  const handleAnswerSelect = (answerIndex: number) => {
    setSelectedAnswer(answerIndex);
  };

  const nextQuestion = () => {
    if (selectedAnswer !== null) {
      const newAnswers = [...userAnswers];
      newAnswers[currentQuestion] = selectedAnswer;
      setUserAnswers(newAnswers);
      setSelectedAnswer(null);

      if (currentQuestion < IQ_QUESTIONS.length - 1) {
        setCurrentQuestion(currentQuestion + 1);
      } else {
        finishTest();
      }
    }
  };

  const finishTest = () => {
    const finalAnswers = [...userAnswers];
    if (selectedAnswer !== null) {
      finalAnswers[currentQuestion] = selectedAnswer;
    }
    setUserAnswers(finalAnswers);
    setTestCompleted(true);
    setShowResult(true);
  };

  const calculateScore = () => {
    let correct = 0;
    userAnswers.forEach((answer, index) => {
      if (answer === IQ_QUESTIONS[index]?.correct) {
        correct++;
      }
    });
    return correct;
  };

  const getIQScore = (correctAnswers: number) => {
    const percentage = (correctAnswers / IQ_QUESTIONS.length) * 100;
    if (percentage >= 90) return 140;
    if (percentage >= 80) return 130;
    if (percentage >= 70) return 120;
    if (percentage >= 60) return 110;
    if (percentage >= 50) return 100;
    if (percentage >= 40) return 90;
    if (percentage >= 30) return 80;
    return 70;
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const resetTest = () => {
    setTestStarted(false);
    setTestCompleted(false);
    setCurrentQuestion(0);
    setSelectedAnswer(null);
    setUserAnswers([]);
    setShowResult(false);
    setTimeLeft(600);
  };

  if (!testStarted) {
    return (
      <div className="max-w-2xl mx-auto p-6">
        <Card>
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <Brain className="h-16 w-16 text-blue-500" />
            </div>
            <CardTitle className="text-3xl font-bold">IQ Test</CardTitle>
            <p className="text-gray-600 mt-2">
              Test your cognitive abilities with 10 challenging questions
            </p>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
              <h3 className="font-semibold mb-2">Test Instructions:</h3>
              <ul className="text-sm space-y-1 text-gray-600 dark:text-gray-300">
                <li>‚Ä¢ 10 questions covering patterns, logic, and math</li>
                <li>‚Ä¢ 10 minutes time limit</li>
                <li>‚Ä¢ Choose the best answer for each question</li>
                <li>‚Ä¢ Your IQ score will be calculated at the end</li>
              </ul>
            </div>
            <Button onClick={startTest} className="w-full" size="lg">
              <Brain className="w-4 h-4 mr-2" />
              Start IQ Test
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (showResult) {
    const score = calculateScore();
    const iqScore = getIQScore(score);
    const percentage = (score / IQ_QUESTIONS.length) * 100;

    return (
      <div className="max-w-2xl mx-auto p-6">
        <Card>
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-bold">Test Results</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="text-center">
              <div className="text-4xl font-bold text-blue-600 mb-2">
                IQ: {iqScore}
              </div>
              <div className="text-lg text-gray-600">
                {score}/{IQ_QUESTIONS.length} correct ({percentage.toFixed(1)}%)
              </div>
            </div>

            <div className="space-y-4">
              <h3 className="font-semibold">Detailed Results:</h3>
              {IQ_QUESTIONS.map((question, index) => {
                const userAnswer = userAnswers[index];
                const isCorrect = userAnswer === question.correct;
                return (
                  <div key={question.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <Badge variant="secondary" className="text-xs">
                        {question.type.toUpperCase()}
                      </Badge>
                      {isCorrect ? (
                        <CheckCircle className="h-5 w-5 text-green-500" />
                      ) : (
                        <XCircle className="h-5 w-5 text-red-500" />
                      )}
                    </div>
                    <p className="text-sm font-medium mb-2">{question.question}</p>
                    <div className="text-xs text-gray-600">
                      <div>Your answer: {userAnswer !== undefined ? question.options[userAnswer] : "No answer"}</div>
                      <div>Correct answer: {question.options[question.correct]}</div>
                      {question.explanation && (
                        <div className="mt-1 text-blue-600">{question.explanation}</div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>

            <Button onClick={resetTest} className="w-full">
              <RotateCcw className="w-4 h-4 mr-2" />
              Take Test Again
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const progress = ((currentQuestion + 1) / IQ_QUESTIONS.length) * 100;
  const question = IQ_QUESTIONS[currentQuestion];

  return (
    <div className="max-w-2xl mx-auto p-6">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <div>
              <CardTitle className="text-xl">
                Question {currentQuestion + 1} of {IQ_QUESTIONS.length}
              </CardTitle>
              <Badge variant="secondary" className="mt-1">
                {question.type.toUpperCase()}
              </Badge>
            </div>
            <div className="flex items-center space-x-2 text-sm">
              <Clock className="h-4 w-4" />
              <span className={timeLeft < 60 ? "text-red-500 font-bold" : ""}>
                {formatTime(timeLeft)}
              </span>
            </div>
          </div>
          <Progress value={progress} className="mt-4" />
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="text-lg font-medium">
            {question.question}
          </div>

          <div className="space-y-3">
            {question.options.map((option, index) => (
              <Button
                key={index}
                variant={selectedAnswer === index ? "default" : "outline"}
                className="w-full text-left justify-start h-auto p-4"
                onClick={() => handleAnswerSelect(index)}
              >
                <span className="font-medium mr-3">
                  {String.fromCharCode(65 + index)}.
                </span>
                {option}
              </Button>
            ))}
          </div>

          <div className="flex justify-between">
            <Button
              variant="outline"
              onClick={finishTest}
              disabled={userAnswers.length === 0}
            >
              Finish Test
            </Button>
            <Button
              onClick={nextQuestion}
              disabled={selectedAnswer === null}
            >
              {currentQuestion === IQ_QUESTIONS.length - 1 ? "Finish" : "Next Question"}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/tools/notes.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Plus, Trash2, Edit, Search, Folder } from "lucide-react";
import { format } from "date-fns";
import type { Note } from "@shared/schema";

const noteSchema = z.object({
  title: z.string().min(1, "Title is required"),
  content: z.string().min(1, "Content is required"),
  folder: z.string().default("General"),
});

type NoteFormData = z.infer<typeof noteSchema>;

export default function NotesTool() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingNote, setEditingNote] = useState<Note | null>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedFolder, setSelectedFolder] = useState("all");

  const form = useForm<NoteFormData>({
    resolver: zodResolver(noteSchema),
    defaultValues: {
      title: "",
      content: "",
      folder: "General",
    },
  });

  const { data: notes = [], isLoading } = useQuery<Note[]>({
    queryKey: ["/api/notes"],
  });



  const createMutation = useMutation({
    mutationFn: (data: NoteFormData) => apiRequest("POST", "/api/notes", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
      setIsDialogOpen(false);
      form.reset();
    },
  });

  const updateMutation = useMutation({
    mutationFn: ({ id, ...data }: { id: number } & Partial<Note>) =>
      apiRequest("PATCH", `/api/notes/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
      setIsDialogOpen(false);
      setEditingNote(null);
      form.reset();
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/notes/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
    },
  });

  const onSubmit = (data: NoteFormData) => {
    if (editingNote) {
      updateMutation.mutate({ id: editingNote.id, ...data });
    } else {
      createMutation.mutate(data);
    }
  };

  const startEdit = (note: Note) => {
    setEditingNote(note);
    form.setValue("title", note.title);
    form.setValue("content", note.content);
    form.setValue("folder", note.folder || "General");
    setIsDialogOpen(true);
  };

  const closeDialog = () => {
    setIsDialogOpen(false);
    setEditingNote(null);
    form.reset();
  };

  // Get unique folders and filter notes
  const availableFolders = ["all", ...Array.from(new Set(notes.map(note => note.folder || "General")))];

  const displayNotes = notes.filter(note => {
    const matchesSearch = searchQuery === "" || 
      note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      note.content.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesFolder = selectedFolder === "all" || note.folder === selectedFolder;

    return matchesSearch && matchesFolder;
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Notes</h2>
          <p className="text-slate-600">Take and organize your notes</p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              New Note
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>
                {editingNote ? "Edit Note" : "Create New Note"}
              </DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Title</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter note title..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="folder"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Folder</FormLabel>
                      <FormControl>
                        <Input placeholder="General, Work, Personal..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="content"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Content</FormLabel>
                      <FormControl>
                        <Textarea 
                          placeholder="Write your note here..." 
                          rows={10}
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button type="button" variant="outline" onClick={closeDialog}>
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createMutation.isPending || updateMutation.isPending}
                  >
                    {createMutation.isPending || updateMutation.isPending 
                      ? "Saving..." 
                      : editingNote ? "Update Note" : "Create Note"
                    }
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Search and Filter */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
          <Input
            placeholder="Search notes..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
          />
        </div>
        <Select value={selectedFolder} onValueChange={setSelectedFolder}>
          <SelectTrigger className="w-full sm:w-48">
            <Folder className="w-4 h-4 mr-2" />
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            {availableFolders.map(folder => (
              <SelectItem key={folder} value={folder}>
                {folder === "all" ? "All Folders" : folder}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Notes Grid */}
      {displayNotes.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <div className="text-slate-400 mb-4">
              {searchQuery || selectedFolder !== "all" ? (
                <>No notes found matching your criteria</>
              ) : (
                <>No notes yet. Create your first note to get started!</>
              )}
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {displayNotes.map((note) => (
            <Card 
              key={note.id} 
              className="hover:shadow-lg transition-shadow cursor-pointer"
              onClick={() => startEdit(note)}
            >
              <CardHeader className="pb-2">
                <div className="flex items-start justify-between">
                  <CardTitle className="text-lg line-clamp-2">{note.title}</CardTitle>
                  <div className="flex space-x-1 ml-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        startEdit(note);
                      }}
                      className="text-slate-500 hover:text-slate-700"
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteMutation.mutate(note.id);
                      }}
                      className="text-red-500 hover:text-red-700 hover:bg-red-50"
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <Badge variant="outline" className="text-xs">
                    {note.folder || "General"}
                  </Badge>
                  <span className="text-xs text-slate-500">
                    {note.updatedAt ? format(new Date(note.updatedAt), "MMM dd, yyyy") : "No date"}
                  </span>
                </div>
              </CardHeader>
              <CardContent>
                <p className="text-sm text-slate-600 line-clamp-4">
                  {note.content}
                </p>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/components/tools/password-gen.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Copy, RefreshCw, Key, Shield, Eye, EyeOff } from "lucide-react";

export default function PasswordGeneratorTool() {
  const [password, setPassword] = useState("");
  const [length, setLength] = useState([12]);
  const [includeUppercase, setIncludeUppercase] = useState(true);
  const [includeLowercase, setIncludeLowercase] = useState(true);
  const [includeNumbers, setIncludeNumbers] = useState(true);
  const [includeSymbols, setIncludeSymbols] = useState(true);
  const [excludeSimilar, setExcludeSimilar] = useState(false);
  const [showPassword, setShowPassword] = useState(true);
  const [generatedPasswords, setGeneratedPasswords] = useState<string[]>([]);

  const { toast } = useToast();

  const generatePassword = () => {
    let charset = "";

    if (includeLowercase) charset += "abcdefghijklmnopqrstuvwxyz";
    if (includeUppercase) charset += "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    if (includeNumbers) charset += "0123456789";
    if (includeSymbols) charset += "!@#$%^&*()_+-=[]{}|;:,.<>?";

    if (excludeSimilar) {
      charset = charset.replace(/[il1Lo0O]/g, "");
    }

    if (!charset) {
      toast({
        title: "Error",
        description: "Please select at least one character type",
        variant: "destructive",
      });
      return;
    }

    let generatedPassword = "";
    for (let i = 0; i < length[0]; i++) {
      generatedPassword += charset.charAt(Math.floor(Math.random() * charset.length));
    }

    setPassword(generatedPassword);

    // Add to history (keep last 5)
    setGeneratedPasswords(prev => [generatedPassword, ...prev.slice(0, 4)]);
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied!",
      description: "Password copied to clipboard",
    });
  };

  const getPasswordStrength = (pwd: string) => {
    let score = 0;
    if (pwd.length >= 8) score++;
    if (pwd.length >= 12) score++;
    if (/[a-z]/.test(pwd)) score++;
    if (/[A-Z]/.test(pwd)) score++;
    if (/[0-9]/.test(pwd)) score++;
    if (/[^A-Za-z0-9]/.test(pwd)) score++;

    if (score <= 2) return { level: "Weak", color: "red" };
    if (score <= 4) return { level: "Medium", color: "yellow" };
    return { level: "Strong", color: "green" };
  };

  const strength = password ? getPasswordStrength(password) : null;

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Password Generator</h2>
          <p className="text-slate-600">Generate secure passwords with custom options</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Generator Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Key className="w-5 h-5" />
              <span>Generator Settings</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Length Slider */}
            <div className="space-y-2">
              <Label>Password Length: {length[0]} characters</Label>
              <Slider
                value={length}
                onValueChange={setLength}
                max={50}
                min={4}
                step={1}
                className="w-full"
              />
            </div>

            {/* Character Options */}
            <div className="space-y-4">
              <Label className="text-base font-medium">Include Characters</Label>

              <div className="space-y-3">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="uppercase"
                    checked={includeUppercase}
                    onCheckedChange={(checked) => setIncludeUppercase(checked === true)}
                  />
                  <Label htmlFor="uppercase">Uppercase letters (A-Z)</Label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="lowercase"
                    checked={includeLowercase}
                    onCheckedChange={(checked) => setIncludeLowercase(checked === true)}
                  />
                  <Label htmlFor="lowercase">Lowercase letters (a-z)</Label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="numbers"
                    checked={includeNumbers}
                    onCheckedChange={(checked) => setIncludeNumbers(checked === true)}
                  />
                  <Label htmlFor="numbers">Numbers (0-9)</Label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="symbols"
                    checked={includeSymbols}
                    onCheckedChange={(checked) => setIncludeSymbols(checked === true)}
                  />
                  <Label htmlFor="symbols">Symbols (!@#$%...)</Label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="exclude-similar"
                    checked={excludeSimilar}
                    onCheckedChange={(checked) => setExcludeSimilar(checked === true)}
                  />
                  <Label htmlFor="exclude-similar">Exclude similar characters (i, l, 1, L, o, 0, O)</Label>
                </div>
              </div>
            </div>

            <Button onClick={generatePassword} className="w-full" size="lg">
              <RefreshCw className="w-5 h-5 mr-2" />
              Generate Password
            </Button>
          </CardContent>
        </Card>

        {/* Generated Password */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Shield className="w-5 h-5" />
              <span>Generated Password</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {password ? (
              <>
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <Label>Your Password</Label>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setShowPassword(!showPassword)}
                    >
                      {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                    </Button>
                  </div>
                  <div className="relative">
                    <Input
                      type={showPassword ? "text" : "password"}
                      value={password}
                      readOnly
                      className="font-mono pr-10"
                    />
                    <Button
                      variant="ghost"
                      size="sm"
                      className="absolute right-2 top-1/2 transform -translate-y-1/2"
                      onClick={() => copyToClipboard(password)}
                    >
                      <Copy className="w-4 h-4" />
                    </Button>
                  </div>
                </div>

                {/* Password Strength */}
                {strength && (
                  <div className="flex items-center space-x-2">
                    <span className="text-sm text-slate-600">Strength:</span>
                    <Badge 
                      variant={strength.color === "green" ? "default" : "secondary"}
                      className={
                        strength.color === "red" ? "bg-red-100 text-red-800" :
                        strength.color === "yellow" ? "bg-yellow-100 text-yellow-800" :
                        "bg-green-100 text-green-800"
                      }
                    >
                      {strength.level}
                    </Badge>
                  </div>
                )}
              </>
            ) : (
              <div className="text-center py-8 text-slate-500">
                Click "Generate Password" to create a secure password
              </div>
            )}

            {/* Password History */}
            {generatedPasswords.length > 0 && (
              <div className="space-y-2">
                <Label>Recent Passwords</Label>
                <div className="space-y-2 max-h-40 overflow-y-auto">
                  {generatedPasswords.map((pwd, index) => (
                    <div key={index} className="flex items-center justify-between p-2 bg-slate-50 rounded border">
                      <code className="text-sm font-mono truncate flex-1 mr-2">
                        {showPassword ? pwd : "‚Ä¢".repeat(pwd.length)}
                      </code>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => copyToClipboard(pwd)}
                      >
                        <Copy className="w-3 h-3" />
                      </Button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Security Tips */}
      <Card>
        <CardHeader>
          <CardTitle>Password Security Tips</CardTitle>
        </CardHeader>
        <CardContent>
