import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Play, Pause, Square, RotateCcw, Settings, Coffee, Target } from "lucide-react";

type PomodoroPhase = "work" | "short-break" | "long-break";

type PomodoroSettings = {
  workDuration: number; // in minutes
  shortBreakDuration: number;
  longBreakDuration: number;
  sessionsUntilLongBreak: number;
};

export default function PomodoroTool() {
  const [isRunning, setIsRunning] = useState(false);
  const [phase, setPhase] = useState<PomodoroPhase>("work");
  const [timeRemaining, setTimeRemaining] = useState(25 * 60); // 25 minutes in seconds
  const [completedSessions, setCompletedSessions] = useState(0);
  const [totalSessions, setTotalSessions] = useState(0);
  const [showSettings, setShowSettings] = useState(false);
  const [dailySessions, setDailySessions] = useState(0);
  const [focusTime, setFocusTime] = useState(0); // total minutes focused today

  const [settings, setSettings] = useState<PomodoroSettings>({
    workDuration: 25,
    shortBreakDuration: 5,
    longBreakDuration: 15,
    sessionsUntilLongBreak: 4,
  });

  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setTimeRemaining((prev) => {
          if (prev <= 1) {
            handlePhaseComplete();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isRunning]);

  const handlePhaseComplete = () => {
    setIsRunning(false);
    playNotificationSound();
    showNotification();

    if (phase === "work") {
      const newCompletedSessions = completedSessions + 1;
      setCompletedSessions(newCompletedSessions);

      // Determine next phase
      if (newCompletedSessions % settings.sessionsUntilLongBreak === 0) {
        setPhase("long-break");
        setTimeRemaining(settings.longBreakDuration * 60);
      } else {
        setPhase("short-break");
        setTimeRemaining(settings.shortBreakDuration * 60);
      }
    } else {
      // Break completed, back to work
      setPhase("work");
      setTimeRemaining(settings.workDuration * 60);
    }
  };

  const playNotificationSound = () => {
    try {
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();

      // Create a pleasant notification sound
      const frequencies = phase === "work" ? [523, 659, 784] : [392, 494, 587]; // Different tones for work/break

      frequencies.forEach((freq, index) => {
        setTimeout(() => {
          const oscillator = audioContext.createOscillator();
          const gainNode = audioContext.createGain();

          oscillator.connect(gainNode);
          gainNode.connect(audioContext.destination);

          oscillator.frequency.value = freq;
          oscillator.type = 'sine';

          gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
          gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.3);

          oscillator.start(audioContext.currentTime);
          oscillator.stop(audioContext.currentTime + 0.3);
        }, index * 200);
      });
    } catch (error) {
      console.log('Audio not available');
    }
  };

  const showNotification = () => {
    if ('Notification' in window && Notification.permission === 'granted') {
      const title = phase === "work" ? "Work session complete!" : "Break time over!";
      const body = phase === "work" 
        ? "Time for a break. You've earned it!" 
        : "Ready to get back to work?";

      new Notification(title, {
        body,
        icon: '/favicon.ico'
      });
    }
  };

  const requestNotificationPermission = () => {
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  };

  const formatTime = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const startTimer = () => {
    setIsRunning(true);
    requestNotificationPermission();
  };

  const pauseTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setPhase("work");
    setTimeRemaining(settings.workDuration * 60);
  };

  const skipPhase = () => {
    handlePhaseComplete();
  };

  const updateSettings = (newSettings: PomodoroSettings) => {
    setSettings(newSettings);
    setShowSettings(false);

    // Reset timer with new settings if not running
    if (!isRunning) {
      setPhase("work");
      setTimeRemaining(newSettings.workDuration * 60);
    }
  };

  const getCurrentPhaseDuration = () => {
    switch (phase) {
      case "work":
        return settings.workDuration * 60;
      case "short-break":
        return settings.shortBreakDuration * 60;
      case "long-break":
        return settings.longBreakDuration * 60;
      default:
        return settings.workDuration * 60;
    }
  };

  const progress = ((getCurrentPhaseDuration() - timeRemaining) / getCurrentPhaseDuration()) * 100;

  const phaseConfig = {
    work: {
      title: "Focus Time",
      description: "Time to concentrate and get work done",
      color: "bg-red-500",
      bgColor: "bg-red-50",
      borderColor: "border-red-200",
      icon: Target,
    },
    "short-break": {
      title: "Short Break",
      description: "Take a quick break to recharge",
      color: "bg-green-500",
      bgColor: "bg-green-50",
      borderColor: "border-green-200",
      icon: Coffee,
    },
    "long-break": {
      title: "Long Break",
      description: "Take a longer break to fully recharge",
      color: "bg-blue-500",
      bgColor: "bg-blue-50",
      borderColor: "border-blue-200",
      icon: Coffee,
    },
  };

  const currentConfig = phaseConfig[phase];
  const IconComponent = currentConfig.icon;

  if (showSettings) {
    return (
      <div className="max-w-2xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold text-slate-900">Pomodoro Settings</h2>
          <Button variant="outline" onClick={() => setShowSettings(false)}>
            Back
          </Button>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Timer Settings</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Work Duration (minutes)
                </label>
                <Input
                  type="number"
                  value={settings.workDuration}
                  onChange={(e) => setSettings({
                    ...settings,
                    workDuration: parseInt(e.target.value) || 25
                  })}
                  min="1"
                  max="120"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Short Break Duration (minutes)
                </label>
                <Input
                  type="number"
                  value={settings.shortBreakDuration}
                  onChange={(e) => setSettings({
                    ...settings,
                    shortBreakDuration: parseInt(e.target.value) || 5
                  })}
                  min="1"
                  max="30"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Long Break Duration (minutes)
                </label>
                <Input
                  type="number"
                  value={settings.longBreakDuration}
                  onChange={(e) => setSettings({
                    ...settings,
                    longBreakDuration: parseInt(e.target.value) || 15
                  })}
                  min="1"
                  max="60"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Sessions Until Long Break
                </label>
                <Input
                  type="number"
                  value={settings.sessionsUntilLongBreak}
                  onChange={(e) => setSettings({
                    ...settings,
                    sessionsUntilLongBreak: parseInt(e.target.value) || 4
                  })}
                  min="2"
                  max="10"
                />
              </div>
            </div>

            <div className="flex justify-end">
              <Button onClick={() => updateSettings(settings)}>
                Save Settings
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Pomodoro Timer</h2>
          <p className="text-slate-600">Focus with time blocks using the Pomodoro Technique</p>
        </div>
        <Button variant="outline" onClick={() => setShowSettings(true)}>
          <Settings className="w-4 h-4 mr-2" />
          Settings
        </Button>
      </div>

      {/* Main Timer */}
      <Card className={`${currentConfig.bgColor} ${currentConfig.borderColor} border-2`}>
        <CardContent className="p-8">
          <div className="text-center space-y-6">
            <div className="flex items-center justify-center space-x-2">
              <IconComponent className="w-6 h-6 text-slate-700" />
              <Badge variant="outline" className="text-lg px-4 py-1">
                {currentConfig.title}
              </Badge>
            </div>

            <div>
              <div className="text-6xl md:text-8xl font-mono text-slate-900 mb-2">
                {formatTime(timeRemaining)}
              </div>
              <p className="text-slate-600">{currentConfig.description}</p>
            </div>

            <Progress value={progress} className="h-3" />

            <div className="flex justify-center space-x-4">
              {!isRunning ? (
                <Button size="lg" onClick={startTimer} disabled={timeRemaining === 0}>
                  <Play className="w-6 h-6 mr-2" />
                  Start
                </Button>
              ) : (
                <Button size="lg" variant="outline" onClick={pauseTimer}>
                  <Pause className="w-6 h-6 mr-2" />
                  Pause
                </Button>
              )}

              <Button size="lg" variant="outline" onClick={resetTimer}>
                <RotateCcw className="w-6 h-6 mr-2" />
                Reset
              </Button>

              <Button size="lg" variant="outline" onClick={skipPhase}>
                <Square className="w-6 h-6 mr-2" />
                Skip
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Session Stats */}
      <div className="grid md:grid-cols-3 gap-6">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-lg">Today's Sessions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold text-slate-900">{completedSessions}</div>
            <p className="text-sm text-slate-600">Completed work sessions</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-lg">Current Cycle</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold text-slate-900">
              {(completedSessions % settings.sessionsUntilLongBreak) + 1}
            </div>
            <p className="text-sm text-slate-600">
              of {settings.sessionsUntilLongBreak} sessions
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-lg">Next Phase</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-lg font-semibold text-slate-900">
              {phase === "work" 
                ? (completedSessions + 1) % settings.sessionsUntilLongBreak === 0 
                  ? "Long Break" 
                  : "Short Break"
                : "Work Session"
              }
            </div>
            <p className="text-sm text-slate-600">
              {phase === "work" 
                ? (completedSessions + 1) % settings.sessionsUntilLongBreak === 0 
                  ? `${settings.longBreakDuration} minutes` 
                  : `${settings.shortBreakDuration} minutes`
                : `${settings.workDuration} minutes`
              }
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Session Progress */}
      <Card>
        <CardHeader>
          <CardTitle>Session Progress</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center space-x-2">
            {Array.from({ length: settings.sessionsUntilLongBreak }, (_, index) => (
              <div
                key={index}
                className={`flex-1 h-3 rounded-full ${
                  index < (completedSessions % settings.sessionsUntilLongBreak)
                    ? "bg-green-500"
                    : index === (completedSessions % settings.sessionsUntilLongBreak) && phase === "work" && isRunning
                    ? "bg-yellow-500"
                    : "bg-slate-200"
                }`}
              />
            ))}
          </div>
          <div className="flex justify-between text-sm text-slate-500 mt-2">
            <span>Cycle Progress</span>
            <span>
              {completedSessions % settings.sessionsUntilLongBreak} / {settings.sessionsUntilLongBreak} sessions
            </span>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/tools/project-time.content:
import React, { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Play, Pause, Square, Plus, Trash2, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Project {
  id: string;
  name: string;
  totalTime: number;
  sessions: Session[];
}

interface Session {
  id: string;
  startTime: Date;
  endTime?: Date;
  duration: number;
}

export default function ProjectTimer() {
  const [projects, setProjects] = useState<Project[]>([]);
  const [activeProject, setActiveProject] = useState<string | null>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [newProjectName, setNewProjectName] = useState('');
  const [sessionStart, setSessionStart] = useState<Date | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    const savedProjects = localStorage.getItem('projectTimer_projects');
    if (savedProjects) {
      const parsed = JSON.parse(savedProjects);
      setProjects(parsed.map((p: any) => ({
        ...p,
        sessions: p.sessions.map((s: any) => ({
          ...s,
          startTime: new Date(s.startTime),
          endTime: s.endTime ? new Date(s.endTime) : undefined
        }))
      })));
    }
  }, []);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isRunning) {
      interval = setInterval(() => {
        setCurrentTime(prev => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isRunning]);

  useEffect(() => {
    localStorage.setItem('projectTimer_projects', JSON.stringify(projects));
  }, [projects]);

  const formatTime = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const createProject = () => {
    if (!newProjectName.trim()) return;

    const newProject: Project = {
      id: Date.now().toString(),
      name: newProjectName,
      totalTime: 0,
      sessions: []
    };

    setProjects(prev => [...prev, newProject]);
    setNewProjectName('');
    toast({
      title: "Project Created",
      description: `${newProjectName} project has been created successfully.`
    });
  };

  const deleteProject = (projectId: string) => {
    if (activeProject === projectId) {
      stopTimer();
    }
    setProjects(prev => prev.filter(p => p.id !== projectId));
    toast({
      title: "Project Deleted",
      description: "Project has been removed successfully."
    });
  };

  const startTimer = (projectId: string) => {
    if (isRunning && activeProject !== projectId) {
      stopTimer();
    }

    setActiveProject(projectId);
    setIsRunning(true);
    setCurrentTime(0);
    setSessionStart(new Date());
  };

  const pauseTimer = () => {
    setIsRunning(false);
  };

  const stopTimer = () => {
    if (activeProject && sessionStart) {
      const endTime = new Date();
      const session: Session = {
        id: Date.now().toString(),
        startTime: sessionStart,
        endTime,
        duration: currentTime
      };

      setProjects(prev => prev.map(project => 
        project.id === activeProject 
          ? {
              ...project,
              totalTime: project.totalTime + currentTime,
              sessions: [...project.sessions, session]
            }
          : project
      ));

      toast({
        title: "Session Saved",
        description: `Logged ${formatTime(currentTime)} to your project.`
      });
    }

    setIsRunning(false);
    setActiveProject(null);
    setCurrentTime(0);
    setSessionStart(null);
  };

  return (
    <div className="space-y-6">
      {/* Create New Project */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Plus className="w-5 h-5" />
            Create New Project
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex gap-2">
            <Input
              value={newProjectName}
              onChange={(e) => setNewProjectName(e.target.value)}
              placeholder="Enter project name"
              onKeyPress={(e) => e.key === 'Enter' && createProject()}
            />
            <Button onClick={createProject} disabled={!newProjectName.trim()}>
              <Plus className="w-4 h-4" />
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Active Timer */}
      {activeProject && (
        <Card className="border-primary">
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span className="flex items-center gap-2">
                <Clock className="w-5 h-5" />
                Current Session
              </span>
              <Badge variant={isRunning ? "default" : "secondary"}>
                {isRunning ? "Running" : "Paused"}
              </Badge>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="text-center">
              <div className="text-4xl font-mono font-bold mb-2">
                {formatTime(currentTime)}
              </div>
              <p className="text-muted-foreground">
                {projects.find(p => p.id === activeProject)?.name}
              </p>
            </div>
            <div className="flex gap-2 justify-center">
              {!isRunning ? (
                <Button onClick={() => setIsRunning(true)}>
                  <Play className="w-4 h-4 mr-2" />
                  Resume
                </Button>
              ) : (
                <Button onClick={pauseTimer} variant="outline">
                  <Pause className="w-4 h-4 mr-2" />
                  Pause
                </Button>
              )}
              <Button onClick={stopTimer} variant="destructive">
                <Square className="w-4 h-4 mr-2" />
                Stop & Save
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Projects List */}
      <div className="space-y-4">
        <h3 className="text-lg font-semibold">Your Projects</h3>
        {projects.length === 0 ? (
          <Card>
            <CardContent className="text-center py-8">
              <Clock className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
              <p className="text-muted-foreground">No projects yet. Create your first project to start tracking time!</p>
            </CardContent>
          </Card>
        ) : (
          projects.map(project => (
            <Card key={project.id} className={project.id === activeProject ? "ring-2 ring-primary" : ""}>
              <CardContent className="flex items-center justify-between p-4">
                <div>
                  <h4 className="font-semibold">{project.name}</h4>
                  <p className="text-sm text-muted-foreground">
                    Total: {formatTime(project.totalTime)} ‚Ä¢ {project.sessions.length} sessions
                  </p>
                </div>
                <div className="flex gap-2">
                  <Button
                    onClick={() => startTimer(project.id)}
                    disabled={isRunning && activeProject === project.id}
                    size="sm"
                  >
                    <Play className="w-4 h-4" />
                  </Button>
                  <Button
                    onClick={() => deleteProject(project.id)}
                    variant="outline"
                    size="sm"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

