import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { 
  QrCode, Camera, Upload, Download, Copy, ExternalLink, 
  Wifi, Phone, Mail, MessageSquare, Calendar, MapPin 
} from "lucide-react";

type QRCodeType = "url" | "text" | "email" | "phone" | "sms" | "wifi" | "vcard" | "event" | "location";

export default function QRScannerTool() {
  const [activeTab, setActiveTab] = useState<"scan" | "generate">("scan");
  const [scannedData, setScannedData] = useState("");
  const [qrType, setQrType] = useState<QRCodeType>("text");
  const [generateData, setGenerateData] = useState("");
  const [qrCodeUrl, setQrCodeUrl] = useState("");
  const [isScanning, setIsScanning] = useState(false);
  const [history, setHistory] = useState<Array<{id: string, data: string, type: string, timestamp: Date}>>([]);

  const videoRef = useRef<HTMLVideoElement>(null);
  const qrScannerRef = useRef<QrScanner | null>(null);
  const { toast } = useToast();

  const detectQRType = (data: string): QRCodeType => {
    if (data.startsWith("http://") || data.startsWith("https://")) return "url";
    if (data.startsWith("mailto:")) return "email";
    if (data.startsWith("tel:")) return "phone";
    if (data.startsWith("sms:")) return "sms";
    if (data.startsWith("WIFI:")) return "wifi";
    if (data.startsWith("BEGIN:VCARD")) return "vcard";
    if (data.startsWith("BEGIN:VEVENT")) return "event";
    if (data.startsWith("geo:")) return "location";
    return "text";
  };

  const startCamera = async () => {
    try {
      if (videoRef.current && !qrScannerRef.current) {
        qrScannerRef.current = new QrScanner(
          videoRef.current,
          (result) => handleQRDetection(result.data),
          {
            preferredCamera: 'environment',
            highlightScanRegion: true,
            highlightCodeOutline: true,
          }
        );
      }

      if (qrScannerRef.current) {
        await qrScannerRef.current.start();
        setIsScanning(true);
        toast({
          title: "Camera Started",
          description: "Point your camera at a QR code to scan",
        });
      }
    } catch (error) {
      console.error('Camera error:', error);
      toast({
        title: "Camera Error",
        description: "Unable to access camera. Please check permissions and ensure you're using HTTPS.",
        variant: "destructive",
      });
    }
  };

  const stopCamera = () => {
    if (qrScannerRef.current) {
      qrScannerRef.current.stop();
      qrScannerRef.current.destroy();
      qrScannerRef.current = null;
    }
    setIsScanning(false);
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      try {
        const result = await QrScanner.scanImage(file, {
          returnDetailedScanResult: true,
        });
        handleQRDetection(result.data);
        toast({
          title: "QR Code Found",
          description: "Successfully scanned QR code from image",
        });
      } catch (error) {
        toast({
          title: "No QR Code Found",
          description: "Could not detect a QR code in the uploaded image",
          variant: "destructive",
        });
      }
    }
  };

  const handleQRDetection = (data: string) => {
    setScannedData(data);
    const type = detectQRType(data);

    // Add to history
    const newEntry = {
      id: Date.now().toString(),
      data,
      type,
      timestamp: new Date()
    };
    setHistory(prev => [newEntry, ...prev.slice(0, 9)]);

    toast({
      title: "QR Code Detected",
      description: `${type.toUpperCase()}: ${data.substring(0, 50)}${data.length > 50 ? "..." : ""}`,
    });
  };

  const generateQRCode = () => {
    const trimmedData = generateData.trim();
    if (!trimmedData) {
      toast({
        title: "Error",
        description: "Please enter data to generate QR code",
        variant: "destructive",
      });
      return;
    }

    try {
      // Use a free QR code API service
      const encodedData = encodeURIComponent(trimmedData);
      const qrUrl = `https://api.qrserver.com/v1/create-qr-code/?size=400x400&data=${encodedData}`;
      setQrCodeUrl(qrUrl);

      toast({
        title: "QR Code Generated",
        description: "QR code created successfully",
      });
    } catch (error) {
      toast({
        title: "Generation Error",
        description: "Failed to generate QR code",
        variant: "destructive",
      });
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied!",
      description: "Content copied to clipboard",
    });
  };

  const openLink = (url: string) => {
    window.open(url, "_blank");
  };

  const formatData = (data: string, type: string) => {
    switch (type) {
      case "wifi":
        const wifiMatch = data.match(/WIFI:T:(.*?);S:(.*?);P:(.*?);H:(.*?);?/);
        if (wifiMatch) {
          return `Network: ${wifiMatch[2]}\nSecurity: ${wifiMatch[1]}\nPassword: ${wifiMatch[3]}`;
        }
        break;
      case "vcard":
        return data.replace(/;/g, "\n").replace(/BEGIN:VCARD|END:VCARD|VERSION:.*?\n/g, "");
      default:
        return data;
    }
    return data;
  };

  const getIcon = (type: string) => {
    switch (type) {
      case "url": return ExternalLink;
      case "email": return Mail;
      case "phone": return Phone;
      case "sms": return MessageSquare;
      case "wifi": return Wifi;
      case "event": return Calendar;
      case "location": return MapPin;
      default: return QrCode;
    }
  };

  useEffect(() => {
    // Check if QR Scanner is supported
    QrScanner.hasCamera().then(hasCamera => {
      if (!hasCamera) {
        toast({
          title: "No Camera",
          description: "No camera found on this device",
          variant: "destructive",
        });
      }
    });

    return () => {
      stopCamera();
    };
  }, []);

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">QR Code Scanner</h2>
          <p className="text-slate-600">Scan and generate QR codes</p>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="flex space-x-1 bg-slate-100 p-1 rounded-lg">
        <Button
          variant={activeTab === "scan" ? "default" : "ghost"}
          onClick={() => setActiveTab("scan")}
          className="flex-1"
        >
          <Camera className="w-4 h-4 mr-2" />
          Scan QR Code
        </Button>
        <Button
          variant={activeTab === "generate" ? "default" : "ghost"}
          onClick={() => setActiveTab("generate")}
          className="flex-1"
        >
          <QrCode className="w-4 h-4 mr-2" />
          Generate QR Code
        </Button>
      </div>

      {activeTab === "scan" && (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Scanner */}
          <Card>
            <CardHeader>
              <CardTitle>QR Code Scanner</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="aspect-square bg-slate-100 rounded-lg overflow-hidden relative">
                {isScanning ? (
                  <video
                    ref={videoRef}
                    autoPlay
                    playsInline
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center">
                    <div className="text-center">
                      <Camera className="w-16 h-16 mx-auto mb-4 text-slate-400" />
                      <p className="text-slate-500">Camera preview will appear here</p>
                    </div>
                  </div>
                )}

              </div>

              <div className="space-y-2">
                {!isScanning ? (
                  <Button onClick={startCamera} className="w-full">
                    <Camera className="w-4 h-4 mr-2" />
                    Start Camera
                  </Button>
                ) : (
                  <Button onClick={stopCamera} variant="outline" className="w-full">
                    Stop Camera
                  </Button>
                )}

                <div className="relative">
                  <Input
                    type="file"
                    accept="image/*"
                    onChange={handleFileUpload}
                    className="hidden"
                    id="qr-upload"
                  />
                  <Label htmlFor="qr-upload" className="cursor-pointer">
                    <Button variant="outline" className="w-full" asChild>
                      <span>
                        <Upload className="w-4 h-4 mr-2" />
                        Upload Image
                      </span>
                    </Button>
                  </Label>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Results */}
          <Card>
            <CardHeader>
              <CardTitle>Scan Results</CardTitle>
            </CardHeader>
            <CardContent>
              {scannedData ? (
                <div className="space-y-4">
                  <div className="p-4 bg-slate-50 rounded-lg">
                    <div className="flex items-center justify-between mb-2">
                      <Badge variant="secondary">
                        {detectQRType(scannedData).toUpperCase()}
                      </Badge>
                      <div className="flex space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => copyToClipboard(scannedData)}
                        >
                          <Copy className="w-4 h-4" />
                        </Button>
                        {detectQRType(scannedData) === "url" && (
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => openLink(scannedData)}
                          >
                            <ExternalLink className="w-4 h-4" />
                          </Button>
                        )}
                      </div>
                    </div>
                    <pre className="text-sm whitespace-pre-wrap break-all">
                      {formatData(scannedData, detectQRType(scannedData))}
                    </pre>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8 text-slate-500">
                  Scan a QR code to see results here
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      )}

      {activeTab === "generate" && (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Generator */}
          <Card>
            <CardHeader>
              <CardTitle>Generate QR Code</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="qr-data">Data to encode</Label>
                <Textarea
                  id="qr-data"
                  placeholder="Enter text, URL, or other data..."
                  value={generateData}
                  onChange={(e) => setGenerateData(e.target.value)}
                  rows={4}
                />
              </div>

              <Button 
                onClick={generateQRCode} 
                className="w-full"
                disabled={!generateData.trim()}
              >
                <QrCode className="w-4 h-4 mr-2" />
                Generate QR Code
              </Button>

              {generateData.trim() && (
                <div className="text-sm text-gray-600">
                  Ready to generate QR code for: {generateData.substring(0, 50)}
                  {generateData.length > 50 ? "..." : ""}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Generated QR Code */}
          <Card>
            <CardHeader>
              <CardTitle>Generated QR Code</CardTitle>
            </CardHeader>
            <CardContent>
              {qrCodeUrl ? (
                <div className="space-y-4">
                  <div className="flex justify-center">
                    <img 
                      src={qrCodeUrl} 
                      alt="Generated QR Code"
                      className="max-w-full h-auto border rounded-lg"
                    />
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      onClick={() => copyToClipboard(generateData)}
                      className="flex-1"
                    >
                      <Copy className="w-4 h-4 mr-2" />
                      Copy Data
                    </Button>
                    <Button
                      variant="outline"
                      onClick={() => {
                        const link = document.createElement('a');
                        link.href = qrCodeUrl;
                        link.download = 'qrcode.png';
                        link.click();
                      }}
                      className="flex-1"
                    >
                      <Download className="w-4 h-4 mr-2" />
                      Download
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8 text-slate-500">
                  Enter data and click generate to create QR code
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      )}

      {/* History */}
      {history.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Scan History</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 max-h-60 overflow-y-auto">
              {history.map((entry) => {
                const IconComponent = getIcon(entry.type);
                return (
                  <div key={entry.id} className="flex items-center justify-between p-3 bg-slate-50 rounded-lg">
                    <div className="flex items-center space-x-3 flex-1 min-w-0">
                      <IconComponent className="w-5 h-5 text-slate-500 flex-shrink-0" />
                      <div className="min-w-0 flex-1">
                        <p className="text-sm font-medium text-slate-900 truncate">
                          {entry.data.substring(0, 60)}
                          {entry.data.length > 60 ? "..." : ""}
                        </p>
                        <p className="text-xs text-slate-500">
                          {entry.type.toUpperCase()} ‚Ä¢ {entry.timestamp.toLocaleTimeString()}
                        </p>
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => copyToClipboard(entry.data)}
                    >
                      <Copy className="w-4 h-4" />
                    </Button>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

