import { useState, useRef } from "react";
import { useMutation } from "@tanstack/react-query";
import { Zap, Type, Mic, Camera, FileText } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface QuickCaptureProps {
  onNoteCaptured?: (note: any) => void;
}

export default function QuickCapture({ onNoteCaptured }: QuickCaptureProps) {
  const { toast } = useToast();
  const [quickText, setQuickText] = useState("");
  const [isCapturing, setIsCapturing] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const createQuickNoteMutation = useMutation({
    mutationFn: (data: any) => apiRequest("POST", "/api/notes", data),
    onSuccess: (note) => {
      queryClient.invalidateQueries({ queryKey: ["/api/notes"] });
      toast({ title: "Quick note captured!" });
      setQuickText("");
      onNoteCaptured?.(note);
    },
  });

  const handleQuickCapture = () => {
    if (!quickText.trim()) return;

    const timestamp = new Date().toLocaleString();
    createQuickNoteMutation.mutate({
      title: `Quick Note - ${timestamp}`,
      content: `<p>${quickText}</p>`,
      contentType: "rich",
      folder: "Quick Captures",
      tags: ["quick-capture"],
      smartTags: ["quick"],
      wordCount: quickText.split(/\s+/).length,
      readingTime: 1,
    });
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsCapturing(true);

    if (file.type.startsWith('image/')) {
      // Handle image upload
      const reader = new FileReader();
      reader.onload = (e) => {
        const base64 = e.target?.result as string;
        const timestamp = new Date().toLocaleString();

        createQuickNoteMutation.mutate({
          title: `Image Capture - ${timestamp}`,
          content: `<img src="${base64}" alt="Captured image" style="max-width: 100%;" />`,
          contentType: "rich",
          folder: "Quick Captures",
          tags: ["image-capture", "quick-capture"],
          smartTags: ["image", "quick"],
          attachments: {
            images: [{
              name: file.name,
              type: file.type,
              size: file.size,
              data: base64
            }]
          },
          wordCount: 0,
          readingTime: 0,
        });
        setIsCapturing(false);
      };
      reader.readAsDataURL(file);
    } else if (file.type === 'text/plain') {
      // Handle text file upload
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target?.result as string;
        const timestamp = new Date().toLocaleString();

        createQuickNoteMutation.mutate({
          title: `File: ${file.name}`,
          content: `<pre>${text}</pre>`,
          contentType: "rich",
          folder: "Quick Captures",
          tags: ["file-capture", "quick-capture"],
          smartTags: ["file", "text", "quick"],
          attachments: {
            files: [{
              name: file.name,
              type: file.type,
              size: file.size
            }]
          },
          wordCount: text.split(/\s+/).length,
          readingTime: Math.ceil(text.split(/\s+/).length / 200),
        });
        setIsCapturing(false);
      };
      reader.readAsText(file);
    } else {
      toast({
        title: "Unsupported file type",
        description: "Please upload an image or text file.",
        variant: "destructive",
      });
      setIsCapturing(false);
    }

    // Reset input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <Card className="mb-4">
      <CardContent className="p-4">
        <div className="flex items-center gap-2 mb-3">
          <Zap className="h-4 w-4 text-yellow-500" />
          <span className="text-sm font-medium">Quick Capture</span>
        </div>

        <div className="flex gap-2 mb-3">
          <Input
            placeholder="Quick thought, idea, or note..."
            value={quickText}
            onChange={(e) => setQuickText(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleQuickCapture()}
            className="flex-1"
          />
          <Button 
            size="sm" 
            onClick={handleQuickCapture}
            disabled={!quickText.trim() || createQuickNoteMutation.isPending}
          >
            <Type className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center gap-2">
          <span className="text-xs text-gray-500">Or capture:</span>

          <input
            ref={fileInputRef}
            type="file"
            accept="image/*,text/plain"
            onChange={handleFileUpload}
            className="hidden"
          />

          <Button
            variant="outline"
            size="sm"
            onClick={() => fileInputRef.current?.click()}
            disabled={isCapturing}
          >
            <Camera className="h-4 w-4 mr-1" />
            Image
          </Button>

          <Button
            variant="outline"
            size="sm"
            onClick={() => fileInputRef.current?.click()}
            disabled={isCapturing}
          >
            <FileText className="h-4 w-4 mr-1" />
            File
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/tools/theme-custom.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";

import { 
  Palette, Sun, Moon, Monitor, Eye, Download, Upload, 
  RotateCcw, Brush, Contrast, Type, Layout
} from "lucide-react";

interface ThemeConfig {
  mode: "light" | "dark" | "auto";
  primaryColor: string;
  secondaryColor: string;
  accentColor: string;
  backgroundColor: string;
  textColor: string;
  borderRadius: number;
  fontSize: number;
  compactMode: boolean;
  reducedMotion: boolean;
  highContrast: boolean;
}

const colorPresets = [
  { name: "Blue", primary: "#3b82f6", secondary: "#1e40af", accent: "#60a5fa" },
  { name: "Green", primary: "#10b981", secondary: "#047857", accent: "#34d399" },
  { name: "Purple", primary: "#8b5cf6", secondary: "#7c3aed", accent: "#a78bfa" },
  { name: "Orange", primary: "#f97316", secondary: "#ea580c", accent: "#fb923c" },
  { name: "Pink", primary: "#ec4899", secondary: "#db2777", accent: "#f472b6" },
  { name: "Teal", primary: "#14b8a6", secondary: "#0f766e", accent: "#2dd4bf" },
];

const fontSizes = [
  { name: "Small", value: 14 },
  { name: "Medium", value: 16 },
  { name: "Large", value: 18 },
  { name: "Extra Large", value: 20 },
];

export default function ThemeCustomizerTool() {
  const [activeTab, setActiveTab] = useState<"colors" | "layout" | "accessibility">("colors");
  const [theme, setTheme] = useState<ThemeConfig>({
    mode: "light",
    primaryColor: "#3b82f6",
    secondaryColor: "#1e40af",
    accentColor: "#60a5fa",
    backgroundColor: "#ffffff",
    textColor: "#0f172a",
    borderRadius: 8,
    fontSize: 16,
    compactMode: false,
    reducedMotion: false,
    highContrast: false,
  });

  const { toast } = useToast();

  // Load theme from localStorage on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem("app-theme");
    if (savedTheme) {
      try {
        setTheme(JSON.parse(savedTheme));
      } catch (error) {
        console.error("Failed to load saved theme:", error);
      }
    }
  }, []);

  // Apply theme changes to document
  useEffect(() => {
    applyTheme(theme);
    localStorage.setItem("app-theme", JSON.stringify(theme));
  }, [theme]);

  const applyTheme = (themeConfig: ThemeConfig) => {
    const root = document.documentElement;

    // Only apply theme mode, not colors to prevent visibility issues
    if (themeConfig.mode === "dark") {
      root.classList.add("dark");
    } else if (themeConfig.mode === "light") {
      root.classList.remove("dark");
    } else {
      // Auto mode based on system preference
      const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
      root.classList.toggle("dark", prefersDark);
    }

    // Apply accessibility options
    root.classList.toggle("compact-mode", themeConfig.compactMode);
    root.classList.toggle("reduced-motion", themeConfig.reducedMotion);
    root.classList.toggle("high-contrast", themeConfig.highContrast);

    // Apply font size to body
    document.body.style.fontSize = `${themeConfig.fontSize}px`;
  };

  const updateTheme = (updates: Partial<ThemeConfig>) => {
    setTheme(prev => ({ ...prev, ...updates }));
  };

  const applyColorPreset = (preset: typeof colorPresets[0]) => {
    updateTheme({
      primaryColor: preset.primary,
      secondaryColor: preset.secondary,
      accentColor: preset.accent,
    });

    toast({
      title: "Theme Applied",
      description: `${preset.name} color scheme has been applied`,
    });
  };

  const resetToDefault = () => {
    const defaultTheme: ThemeConfig = {
      mode: "light",
      primaryColor: "#3b82f6",
      secondaryColor: "#1e40af",
      accentColor: "#60a5fa",
      backgroundColor: "#ffffff",
      textColor: "#0f172a",
      borderRadius: 8,
      fontSize: 16,
      compactMode: false,
      reducedMotion: false,
      highContrast: false,
    };

    setTheme(defaultTheme);

    toast({
      title: "Theme Reset",
      description: "Theme has been reset to default settings",
    });
  };

  const exportTheme = () => {
    const themeJson = JSON.stringify(theme, null, 2);
    const blob = new Blob([themeJson], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "app-theme.json";
    link.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Theme Exported",
      description: "Theme configuration downloaded as JSON file",
    });
  };

  const importTheme = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const importedTheme = JSON.parse(e.target?.result as string);
          setTheme(importedTheme);
          toast({
            title: "Theme Imported",
            description: "Theme configuration has been imported successfully",
          });
        } catch (error) {
          toast({
            title: "Import Failed",
            description: "Invalid theme file format",
            variant: "destructive",
          });
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className="max-w-6xl mx-auto space-y-8 p-6">
      <div className="text-center">
        <h1 className="text-3xl font-bold text-slate-900 dark:text-slate-100 mb-2">Theme Customizer</h1>
        <p className="text-slate-600 dark:text-slate-400 mb-6">Personalize your app's appearance and accessibility settings</p>

        <div className="flex justify-center space-x-3">
          <Button 
            variant="outline" 
            size="sm"
            onClick={resetToDefault}
            className="bg-white dark:bg-slate-800 border-slate-200 dark:border-slate-600 text-slate-900 dark:text-slate-100 hover:bg-slate-50 dark:hover:bg-slate-700"
          >
            <RotateCcw className="w-4 h-4 mr-2" />
            Reset to Default
          </Button>
          <Button 
            variant="outline" 
            size="sm"
            onClick={exportTheme}
            className="bg-white dark:bg-slate-800 border-slate-200 dark:border-slate-600 text-slate-900 dark:text-slate-100 hover:bg-slate-50 dark:hover:bg-slate-700"
          >
            <Download className="w-4 h-4 mr-2" />
            Export Theme
          </Button>
          <div className="relative">
            <input
              type="file"
              accept=".json"
              onChange={importTheme}
              className="hidden"
              id="theme-import"
            />
            <Label htmlFor="theme-import" className="cursor-pointer">
              <Button 
                variant="outline" 
                size="sm"
                asChild
                className="bg-white dark:bg-slate-800 border-slate-200 dark:border-slate-600 text-slate-900 dark:text-slate-100 hover:bg-slate-50 dark:hover:bg-slate-700"
              >
                <span>
                  <Upload className="w-4 h-4 mr-2" />
                  Import Theme
                </span>
              </Button>
            </Label>
          </div>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="flex justify-center">
