import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Play, Pause, Square, RotateCcw, Plus } from "lucide-react";

type Timer = {
  id: string;
  name: string;
  duration: number; // in seconds
  remaining: number;
  isRunning: boolean;
  isCompleted: boolean;
};

const presetTimers = [
  { name: "1 Minute", duration: 60 },
  { name: "5 Minutes", duration: 300 },
  { name: "10 Minutes", duration: 600 },
  { name: "15 Minutes", duration: 900 },
  { name: "30 Minutes", duration: 1800 },
  { name: "1 Hour", duration: 3600 },
];

export default function TimerTool() {
  const [timers, setTimers] = useState<Timer[]>([]);
  const [newTimerName, setNewTimerName] = useState("");
  const [newTimerMinutes, setNewTimerMinutes] = useState("");
  const [newTimerSeconds, setNewTimerSeconds] = useState("");
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    intervalRef.current = setInterval(() => {
      setTimers(currentTimers => 
        currentTimers.map(timer => {
          if (timer.isRunning && timer.remaining > 0) {
            const newRemaining = timer.remaining - 1;

            if (newRemaining === 0) {
              // Timer completed
              playNotificationSound();
              showNotification(timer.name);
              return { ...timer, remaining: 0, isRunning: false, isCompleted: true };
            }

            return { ...timer, remaining: newRemaining };
          }
          return timer;
        })
      );
    }, 1000);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  const playNotificationSound = () => {
    // Create a simple beep sound
    const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);

    oscillator.frequency.value = 800;
    oscillator.type = 'sine';

    gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

    oscillator.start(audioContext.currentTime);
    oscillator.stop(audioContext.currentTime + 0.5);
  };

  const showNotification = (timerName: string) => {
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification('Timer Completed!', {
        body: `${timerName} has finished.`,
        icon: '/favicon.ico'
      });
    }
  };

  const requestNotificationPermission = () => {
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  };

  const formatTime = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    }
    return `${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const addPresetTimer = (preset: { name: string; duration: number }) => {
    const newTimer: Timer = {
      id: Date.now().toString(),
      name: preset.name,
      duration: preset.duration,
      remaining: preset.duration,
      isRunning: false,
      isCompleted: false,
    };
    setTimers([...timers, newTimer]);
  };

  const addCustomTimer = () => {
    if (!newTimerName.trim()) return;

    const minutes = parseInt(newTimerMinutes) || 0;
    const seconds = parseInt(newTimerSeconds) || 0;
    const totalSeconds = minutes * 60 + seconds;

    if (totalSeconds <= 0) return;

    const newTimer: Timer = {
      id: Date.now().toString(),
      name: newTimerName.trim(),
      duration: totalSeconds,
      remaining: totalSeconds,
      isRunning: false,
      isCompleted: false,
    };

    setTimers([...timers, newTimer]);
    setNewTimerName("");
    setNewTimerMinutes("");
    setNewTimerSeconds("");
  };

  const startTimer = (id: string) => {
    setTimers(timers.map(timer => 
      timer.id === id 
        ? { ...timer, isRunning: true, isCompleted: false }
        : timer
    ));
    requestNotificationPermission();
  };

  const pauseTimer = (id: string) => {
    setTimers(timers.map(timer => 
      timer.id === id 
        ? { ...timer, isRunning: false }
        : timer
    ));
  };

  const resetTimer = (id: string) => {
    setTimers(timers.map(timer => 
      timer.id === id 
        ? { ...timer, remaining: timer.duration, isRunning: false, isCompleted: false }
        : timer
    ));
  };

  const removeTimer = (id: string) => {
    setTimers(timers.filter(timer => timer.id !== id));
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Timer</h2>
          <p className="text-slate-600">Set countdown timers for your tasks</p>
        </div>
      </div>

      {/* Add Timer Section */}
      <div className="grid md:grid-cols-2 gap-6">
        {/* Preset Timers */}
        <Card>
          <CardHeader>
            <CardTitle>Quick Timers</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 gap-2">
              {presetTimers.map((preset) => (
                <Button
                  key={preset.name}
                  variant="outline"
                  onClick={() => addPresetTimer(preset)}
                  className="h-12"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  {preset.name}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Custom Timer */}
        <Card>
          <CardHeader>
            <CardTitle>Custom Timer</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <Input
              placeholder="Timer name..."
              value={newTimerName}
              onChange={(e) => setNewTimerName(e.target.value)}
            />
            <div className="flex space-x-2">
              <div className="flex-1">
                <Input
                  type="number"
                  placeholder="Minutes"
                  value={newTimerMinutes}
                  onChange={(e) => setNewTimerMinutes(e.target.value)}
                  min="0"
                />
              </div>
              <div className="flex-1">
                <Input
                  type="number"
                  placeholder="Seconds"
                  value={newTimerSeconds}
                  onChange={(e) => setNewTimerSeconds(e.target.value)}
                  min="0"
                  max="59"
                />
              </div>
            </div>
            <Button onClick={addCustomTimer} className="w-full">
              <Plus className="w-4 h-4 mr-2" />
              Add Timer
            </Button>
          </CardContent>
        </Card>
      </div>

      {/* Active Timers */}
      {timers.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <div className="text-slate-400 mb-4">
              No timers active. Add a timer to get started!
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          {timers.map((timer) => (
            <Card 
              key={timer.id} 
              className={`${timer.isCompleted 
                ? 'border-green-300 bg-green-50' 
                : timer.isRunning 
                  ? 'border-blue-300 bg-blue-50' 
                  : 'border-slate-200'
              }`}
            >
              <CardHeader className="pb-2">
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg truncate">{timer.name}</CardTitle>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeTimer(timer.id)}
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    √ó
                  </Button>
                </div>
              </CardHeader>
              <CardContent>
                <div className="text-center space-y-4">
                  <div className={`text-3xl font-mono ${
                    timer.isCompleted 
                      ? 'text-green-700' 
                      : timer.remaining <= 60 
                        ? 'text-red-600' 
                        : 'text-slate-900'
                  }`}>
                    {formatTime(timer.remaining)}
                  </div>

                  {timer.isCompleted && (
                    <div className="text-green-700 font-semibold">
                      ‚úÖ Completed!
                    </div>
                  )}

                  <div className="flex justify-center space-x-2">
                    {timer.isRunning ? (
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => pauseTimer(timer.id)}
                      >
                        <Pause className="w-4 h-4" />
                      </Button>
                    ) : (
                      <Button
                        size="sm"
                        onClick={() => startTimer(timer.id)}
                        disabled={timer.remaining === 0}
                      >
                        <Play className="w-4 h-4" />
                      </Button>
                    )}
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => resetTimer(timer.id)}
                    >
                      <RotateCcw className="w-4 h-4" />
                    </Button>
                  </div>

                  <div className="w-full bg-slate-200 rounded-full h-2">
                    <div
                      className={`h-2 rounded-full transition-all duration-1000 ${
                        timer.isCompleted 
                          ? 'bg-green-500' 
                          : timer.isRunning 
                            ? 'bg-blue-500' 
                            : 'bg-slate-400'
                      }`}
                      style={{
                        width: `${((timer.duration - timer.remaining) / timer.duration) * 100}%`
                      }}
                    />
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/components/tools/todo.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { format } from "date-fns";
import { Plus, Trash2, Calendar, Filter, SortAsc } from "lucide-react";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

import { apiRequest, queryClient } from "@/lib/queryClient";
import { insertTodoSchema, type Todo } from "@shared/schema";
import { useGamification } from "@/hooks/use-gamification";

type TodoFormData = z.infer<typeof insertTodoSchema>;

const priorityColors = {
  low: "bg-green-100 text-green-800",
  medium: "bg-yellow-100 text-yellow-800",
  high: "bg-red-100 text-red-800",
};

export default function TodoTool() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [filter, setFilter] = useState<"all" | "pending" | "completed">("all");
  const [sortBy, setSortBy] = useState<"created" | "priority" | "dueDate">("created");
  const { trackActivity } = useGamification();

  const { data: todos = [], isLoading } = useQuery<Todo[]>({
    queryKey: ["/api/todos"],
  });

  const createMutation = useMutation({
    mutationFn: (data: TodoFormData) => apiRequest("POST", "/api/todos", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/todos"] });
      setIsDialogOpen(false);
      form.reset();
    },
  });

  const updateMutation = useMutation({
    mutationFn: ({ id, ...data }: { id: number } & Partial<Todo>) =>
      apiRequest("PATCH", `/api/todos/${id}`, data),
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/todos"] });

      // Track activity when completing a todo
      if (variables.completed === true) {
        trackActivity("todos_completed");
      }
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/todos/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/todos"] });
    },
  });

  const form = useForm<TodoFormData>({
    resolver: zodResolver(insertTodoSchema),
    defaultValues: {
      title: "",
      description: null,
      priority: "medium",
      category: null,
      dueDate: null,
    },
  });

  const onSubmit = (data: TodoFormData) => {
    createMutation.mutate(data);
  };

  const toggleComplete = (todo: Todo) => {
    updateMutation.mutate({
      id: todo.id,
      completed: !todo.completed,
    });
  };

  // Filter and sort todos
  const filteredTodos = todos.filter(todo => {
    if (filter === "pending") return !todo.completed;
    if (filter === "completed") return todo.completed;
    return true;
  });

  const sortedTodos = [...filteredTodos].sort((a, b) => {
    if (sortBy === "priority") {
      const priorityOrder = { high: 3, medium: 2, low: 1 };
      return priorityOrder[b.priority as keyof typeof priorityOrder] - priorityOrder[a.priority as keyof typeof priorityOrder];
    }
    if (sortBy === "dueDate") {
      if (!a.dueDate && !b.dueDate) return 0;
      if (!a.dueDate) return 1;
      if (!b.dueDate) return -1;
      return new Date(a.dueDate).getTime() - new Date(b.dueDate).getTime();
    }
    return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
  });

  // Stats
  const completedTodos = todos.filter(todo => todo.completed);
  const pendingTodos = todos.filter(todo => !todo.completed);
  const todayTasks = todos.filter(todo => 
    todo.dueDate && new Date(todo.dueDate).toDateString() === new Date().toDateString()
  );

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header with Stats */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">To-Do Lists</h2>
          <p className="text-slate-600">
            {pendingTodos.length} pending ‚Ä¢ {completedTodos.length} completed ‚Ä¢ {todayTasks.length} due today
          </p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Add Task
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Task</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Title</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter task title..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Task details..." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="priority"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Priority</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select priority" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="low">Low</SelectItem>
                            <SelectItem value="medium">Medium</SelectItem>
                            <SelectItem value="high">High</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="category"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Category</FormLabel>
                        <FormControl>
                          <Input placeholder="Work, Personal..." {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
                <FormField
                  control={form.control}
                  name="dueDate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Due Date</FormLabel>
                      <FormControl>
                        <Input type="date" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setIsDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createMutation.isPending}>
                    {createMutation.isPending ? "Creating..." : "Create Task"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Filter and Sort Controls */}
      <div className="flex flex-wrap items-center gap-4 p-4 bg-slate-50 rounded-lg">
        <div className="flex items-center space-x-2">
          <Filter className="w-4 h-4 text-slate-500" />
          <span className="text-sm font-medium text-slate-700">Filter:</span>
          <Select value={filter} onValueChange={(value: any) => setFilter(value)}>
            <SelectTrigger className="w-32">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Tasks</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="completed">Completed</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div className="flex items-center space-x-2">
          <SortAsc className="w-4 h-4 text-slate-500" />
          <span className="text-sm font-medium text-slate-700">Sort by:</span>
          <Select value={sortBy} onValueChange={(value: any) => setSortBy(value)}>
            <SelectTrigger className="w-32">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="created">Created</SelectItem>
              <SelectItem value="priority">Priority</SelectItem>
              <SelectItem value="dueDate">Due Date</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Task List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <span>
              {filter === "all" && "All Tasks"}
              {filter === "pending" && "Pending Tasks"}
              {filter === "completed" && "Completed Tasks"}
            </span>
            <Badge variant="secondary">{sortedTodos.length}</Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {sortedTodos.length === 0 ? (
            <div className="text-center py-8 text-slate-500">
              {filter === "completed" && "No completed tasks yet."}
              {filter === "pending" && "No pending tasks. Great job!"}
              {filter === "all" && "No tasks yet. Create your first task!"}
            </div>
          ) : (
            <div className="space-y-3">
              {sortedTodos.map((todo) => (
                <div
                  key={todo.id}
                  className={`flex items-start space-x-3 p-3 rounded-lg border transition-all ${
                    todo.completed ? 'bg-green-50 border-green-200' : 'bg-slate-50 border-slate-200'
                  }`}
                >
                  <Checkbox
                    checked={!!todo.completed}
                    onCheckedChange={() => toggleComplete(todo)}
                    className="mt-1"
                  />
                  <div className="flex-1 space-y-1">
                    <div className="flex items-center justify-between">
                      <h3 className={`font-medium ${todo.completed ? 'line-through text-slate-500' : 'text-slate-900'}`}>
                        {todo.title}
                      </h3>
                      <div className="flex items-center space-x-2">
                        <Badge className={priorityColors[todo.priority as keyof typeof priorityColors]}>
                          {todo.priority}
                        </Badge>
                        {todo.dueDate && (
                          <Badge variant="outline" className="text-xs">
                            <Calendar className="w-3 h-3 mr-1" />
                            {format(new Date(todo.dueDate), "MMM dd")}
                          </Badge>
                        )}
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => deleteMutation.mutate(todo.id)}
                          className="text-red-600 hover:text-red-700"
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                    {todo.description && (
                      <p className={`text-sm ${todo.completed ? 'text-slate-400' : 'text-slate-600'}`}>
                        {todo.description}
                      </p>
                    )}
                    {todo.category && (
                      <Badge variant="outline" className="text-xs">
                        {todo.category}
                      </Badge>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/tools/unit-convert.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ArrowUpDown } from "lucide-react";

type ConversionCategory = "length" | "weight" | "temperature" | "volume" | "area" | "speed";

type Unit = {
  name: string;
  symbol: string;
  toBase: number; // Multiplier to convert to base unit
  fromBase: number; // Multiplier to convert from base unit
  offset?: number; // For temperature conversions
};

const conversionData: Record<ConversionCategory, { baseUnit: string; units: Unit[] }> = {
  length: {
    baseUnit: "meter",
    units: [
      { name: "Millimeter", symbol: "mm", toBase: 0.001, fromBase: 1000 },
      { name: "Centimeter", symbol: "cm", toBase: 0.01, fromBase: 100 },
      { name: "Meter", symbol: "m", toBase: 1, fromBase: 1 },
      { name: "Kilometer", symbol: "km", toBase: 1000, fromBase: 0.001 },
      { name: "Inch", symbol: "in", toBase: 0.0254, fromBase: 39.3701 },
      { name: "Foot", symbol: "ft", toBase: 0.3048, fromBase: 3.28084 },
      { name: "Yard", symbol: "yd", toBase: 0.9144, fromBase: 1.09361 },
      { name: "Mile", symbol: "mi", toBase: 1609.34, fromBase: 0.000621371 },
    ],
  },
  weight: {
    baseUnit: "kilogram",
    units: [
      { name: "Milligram", symbol: "mg", toBase: 0.000001, fromBase: 1000000 },
      { name: "Gram", symbol: "g", toBase: 0.001, fromBase: 1000 },
      { name: "Kilogram", symbol: "kg", toBase: 1, fromBase: 1 },
      { name: "Pound", symbol: "lb", toBase: 0.453592, fromBase: 2.20462 },
      { name: "Ounce", symbol: "oz", toBase: 0.0283495, fromBase: 35.274 },
      { name: "Stone", symbol: "st", toBase: 6.35029, fromBase: 0.157473 },
      { name: "Ton", symbol: "t", toBase: 1000, fromBase: 0.001 },
    ],
  },
  temperature: {
    baseUnit: "celsius",
    units: [
      { name: "Celsius", symbol: "¬∞C", toBase: 1, fromBase: 1 },
      { name: "Fahrenheit", symbol: "¬∞F", toBase: 1, fromBase: 1 },
      { name: "Kelvin", symbol: "K", toBase: 1, fromBase: 1 },
    ],
  },
  volume: {
    baseUnit: "liter",
    units: [
      { name: "Milliliter", symbol: "ml", toBase: 0.001, fromBase: 1000 },
      { name: "Liter", symbol: "l", toBase: 1, fromBase: 1 },
      { name: "Gallon (US)", symbol: "gal", toBase: 3.78541, fromBase: 0.264172 },
      { name: "Gallon (UK)", symbol: "gal (UK)", toBase: 4.54609, fromBase: 0.219969 },
      { name: "Quart", symbol: "qt", toBase: 0.946353, fromBase: 1.05669 },
      { name: "Pint", symbol: "pt", toBase: 0.473176, fromBase: 2.11338 },
      { name: "Cup", symbol: "cup", toBase: 0.236588, fromBase: 4.22675 },
      { name: "Fluid Ounce", symbol: "fl oz", toBase: 0.0295735, fromBase: 33.814 },
    ],
  },
  area: {
    baseUnit: "square meter",
    units: [
      { name: "Square Millimeter", symbol: "mm¬≤", toBase: 0.000001, fromBase: 1000000 },
      { name: "Square Centimeter", symbol: "cm¬≤", toBase: 0.0001, fromBase: 10000 },
      { name: "Square Meter", symbol: "m¬≤", toBase: 1, fromBase: 1 },
      { name: "Square Kilometer", symbol: "km¬≤", toBase: 1000000, fromBase: 0.000001 },
      { name: "Square Inch", symbol: "in¬≤", toBase: 0.00064516, fromBase: 1550 },
      { name: "Square Foot", symbol: "ft¬≤", toBase: 0.092903, fromBase: 10.7639 },
      { name: "Square Yard", symbol: "yd¬≤", toBase: 0.836127, fromBase: 1.19599 },
      { name: "Acre", symbol: "ac", toBase: 4046.86, fromBase: 0.000247105 },
    ],
  },
  speed: {
    baseUnit: "meters per second",
    units: [
      { name: "Meters per Second", symbol: "m/s", toBase: 1, fromBase: 1 },
      { name: "Kilometers per Hour", symbol: "km/h", toBase: 0.277778, fromBase: 3.6 },
      { name: "Miles per Hour", symbol: "mph", toBase: 0.44704, fromBase: 2.23694 },
      { name: "Feet per Second", symbol: "ft/s", toBase: 0.3048, fromBase: 3.28084 },
      { name: "Knots", symbol: "kt", toBase: 0.514444, fromBase: 1.94384 },
    ],
  },
};

export default function UnitConverterTool() {
  const [category, setCategory] = useState<ConversionCategory>("length");
  const [fromUnit, setFromUnit] = useState("");
  const [toUnit, setToUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [result, setResult] = useState("");

  const currentUnits = conversionData[category].units;

  const convertTemperature = (value: number, from: string, to: string): number => {
    // Convert to Celsius first
    let celsius: number;
    switch (from) {
      case "¬∞C":
        celsius = value;
        break;
      case "¬∞F":
        celsius = (value - 32) * 5/9;
        break;
      case "K":
        celsius = value - 273.15;
        break;
      default:
        celsius = value;
    }

    // Convert from Celsius to target
    switch (to) {
      case "¬∞C":
        return celsius;
      case "¬∞F":
        return celsius * 9/5 + 32;
      case "K":
        return celsius + 273.15;
      default:
        return celsius;
    }
  };

  const performConversion = () => {
    if (!inputValue || !fromUnit || !toUnit) {
      setResult("");
      return;
    }

    const value = parseFloat(inputValue);
    if (isNaN(value)) {
      setResult("Invalid input");
      return;
    }

    if (category === "temperature") {
      const convertedValue = convertTemperature(value, fromUnit, toUnit);
      setResult(convertedValue.toFixed(6).replace(/\.?0+$/, ""));
      return;
    }

    const fromUnitData = currentUnits.find(unit => unit.symbol === fromUnit);
    const toUnitData = currentUnits.find(unit => unit.symbol === toUnit);

    if (!fromUnitData || !toUnitData) {
      setResult("Unit not found");
      return;
    }

    // Convert to base unit, then to target unit
    const baseValue = value * fromUnitData.toBase;
    const convertedValue = baseValue * toUnitData.fromBase;

    setResult(convertedValue.toFixed(6).replace(/\.?0+$/, ""));
  };

  const swapUnits = () => {
    const tempUnit = fromUnit;
    setFromUnit(toUnit);
    setToUnit(tempUnit);
    setInputValue(result);
    setResult(inputValue);
  };

  const categoryOptions = [
    { value: "length", label: "Length" },
    { value: "weight", label: "Weight" },
    { value: "temperature", label: "Temperature" },
    { value: "volume", label: "Volume" },
    { value: "area", label: "Area" },
    { value: "speed", label: "Speed" },
  ];

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Unit Converter</h2>
          <p className="text-slate-600">Convert between different units of measurement</p>
        </div>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Unit Conversion</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Category Selection */}
          <div>
            <label className="block text-sm font-medium text-slate-700 mb-2">
              Category
            </label>
            <Select 
              value={category} 
              onValueChange={(value: ConversionCategory) => {
                setCategory(value);
                setFromUnit("");
                setToUnit("");
                setInputValue("");
                setResult("");
              }}
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {categoryOptions.map(option => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Conversion Interface */}
          <div className="grid md:grid-cols-3 gap-4 items-end">
            {/* From Unit */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                From
              </label>
              <Select value={fromUnit} onValueChange={setFromUnit}>
                <SelectTrigger>
                  <SelectValue placeholder="Select unit" />
                </SelectTrigger>
                <SelectContent>
                  {currentUnits.map(unit => (
                    <SelectItem key={unit.symbol} value={unit.symbol}>
                      {unit.name} ({unit.symbol})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Input
                type="number"
                placeholder="Enter value"
                value={inputValue}
                onChange={(e) => {
                  setInputValue(e.target.value);
                  setTimeout(performConversion, 100);
                }}
              />
            </div>

            {/* Swap Button */}
            <div className="flex justify-center">
              <Button
                variant="outline"
                size="icon"
                onClick={swapUnits}
                disabled={!fromUnit || !toUnit}
              >
                <ArrowUpDown className="w-4 h-4" />
              </Button>
            </div>

            {/* To Unit */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                To
              </label>
              <Select value={toUnit} onValueChange={setToUnit}>
                <SelectTrigger>
                  <SelectValue placeholder="Select unit" />
                </SelectTrigger>
                <SelectContent>
                  {currentUnits.map(unit => (
                    <SelectItem key={unit.symbol} value={unit.symbol}>
                      {unit.name} ({unit.symbol})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <div className="p-3 bg-slate-100 rounded-md">
                <div className="text-lg font-mono">
                  {result || "0"}
                </div>
              </div>
            </div>
          </div>

          {/* Quick Convert Button */}
          <Button onClick={performConversion} className="w-full">
            Convert
          </Button>

          {/* Common Conversions */}
          <div className="mt-8">
            <h3 className="text-lg font-semibold text-slate-900 mb-4">
              Common {categoryOptions.find(c => c.value === category)?.label} Conversions
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
              {category === "length" && (
                <>
                  <div className="flex justify-between p-2 bg-slate-50 rounded">
                    <span>1 meter</span><span>= 3.28 feet</span>
                  </div>
                  <div className="flex justify-between p-2 bg-slate-50 rounded">
