import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Mic, Square, Play, Pause, Trash2, Download } from "lucide-react";
import { format } from "date-fns";
import type { VoiceRecording } from "@shared/schema";

const recordingSchema = z.object({
  title: z.string().min(1, "Title is required"),
});

type RecordingFormData = z.infer<typeof recordingSchema>;

export default function VoiceRecorderTool() {
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [duration, setDuration] = useState(0);
  const [currentRecording, setCurrentRecording] = useState<Blob | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [playingId, setPlayingId] = useState<number | null>(null);

  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const audioRefs = useRef<{ [key: number]: HTMLAudioElement }>({});

  const form = useForm<RecordingFormData>({
    resolver: zodResolver(recordingSchema),
    defaultValues: {
      title: "",
    },
  });

  const { data: recordings = [], isLoading } = useQuery<VoiceRecording[]>({
    queryKey: ["/api/voice-recordings"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: { title: string; blob: string; duration: number }) => 
      apiRequest("POST", "/api/voice-recordings", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/voice-recordings"] });
      setIsDialogOpen(false);
      setCurrentRecording(null);
      setDuration(0); // Reset duration to 0
      form.reset();
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (id: number) => apiRequest("DELETE", `/api/voice-recordings/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/voice-recordings"] });
    },
  });

  useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        setCurrentRecording(audioBlob);
        setIsDialogOpen(true);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
      setDuration(0);

      timerRef.current = setInterval(() => {
        setDuration(prev => prev + 1);
      }, 1000);
    } catch (error) {
      console.error('Error accessing microphone:', error);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setIsPaused(false);
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    }
  };

  const pauseRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      if (isPaused) {
        mediaRecorderRef.current.resume();
        timerRef.current = setInterval(() => {
          setDuration(prev => prev + 1);
        }, 1000);
      } else {
        mediaRecorderRef.current.pause();
        if (timerRef.current) {
          clearInterval(timerRef.current);
        }
      }
      setIsPaused(!isPaused);
    }
  };

  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const playRecording = (recording: VoiceRecording) => {
    if (playingId === recording.id) {
      // Stop current playback
      if (audioRefs.current[recording.id]) {
        audioRefs.current[recording.id].pause();
        audioRefs.current[recording.id].currentTime = 0;
      }
      setPlayingId(null);
      return;
    }

    // Stop any other playing audio
    Object.values(audioRefs.current).forEach(audio => {
      audio.pause();
      audio.currentTime = 0;
    });

    // Create and play new audio
    if (recording.blob) {
      const audio = new Audio(`data:audio/wav;base64,${recording.blob}`);
      audioRefs.current[recording.id] = audio;

      audio.onended = () => setPlayingId(null);
      audio.play();
      setPlayingId(recording.id);
    }
  };

  const onSubmit = async (data: RecordingFormData) => {
    if (currentRecording) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const base64 = reader.result as string;
        const base64Data = base64.split(',')[1]; // Remove data:audio/wav;base64, prefix

        createMutation.mutate({
          title: data.title,
          blob: base64Data,
          duration,
        });
      };
      reader.readAsDataURL(currentRecording);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-slate-900">Voice Recorder</h2>
          <p className="text-slate-600">Record audio memos and notes</p>
        </div>
      </div>

      {/* Recording Controls */}
      <Card>
        <CardHeader>
          <CardTitle>Record New Audio</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-col items-center space-y-6">
            <div className="text-4xl font-mono text-slate-900">
              {formatDuration(duration)}
            </div>

            <div className="flex items-center space-x-4">
              {!isRecording ? (
                <Button size="lg" onClick={startRecording} className="bg-red-600 hover:bg-red-700">
                  <Mic className="w-6 h-6 mr-2" />
                  Start Recording
                </Button>
              ) : (
                <>
                  <Button 
                    size="lg" 
                    variant="outline" 
                    onClick={pauseRecording}
                    className="border-orange-600 text-orange-600 hover:bg-orange-50"
                  >
                    {isPaused ? <Play className="w-6 h-6 mr-2" /> : <Pause className="w-6 h-6 mr-2" />}
                    {isPaused ? "Resume" : "Pause"}
                  </Button>
                  <Button 
                    size="lg" 
                    onClick={stopRecording}
                    className="bg-slate-600 hover:bg-slate-700"
                  >
                    <Square className="w-6 h-6 mr-2" />
                    Stop
                  </Button>
                </>
              )}
            </div>

            {isRecording && (
              <div className="flex items-center space-x-2 text-red-600">
                <div className="w-3 h-3 bg-red-600 rounded-full animate-pulse"></div>
                <span className="text-sm font-medium">
                  {isPaused ? "Paused" : "Recording..."}
                </span>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Save Recording Dialog */}
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Save Recording</DialogTitle>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="title"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Title</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter recording title..." {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <div className="text-sm text-slate-600">
                Duration: {formatDuration(duration)}
              </div>
              <div className="flex justify-end space-x-2">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => {
                    setIsDialogOpen(false);
                    setCurrentRecording(null);
                    setDuration(0); // Reset duration when discarding
                    form.reset();
                  }}
                >
                  Discard
                </Button>
                <Button type="submit" disabled={createMutation.isPending}>
                  {createMutation.isPending ? "Saving..." : "Save Recording"}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Recordings List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <span>Your Recordings</span>
            <Badge variant="secondary">{recordings.length}</Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {recordings.length === 0 ? (
            <div className="text-center py-8 text-slate-500">
              No recordings yet. Start recording to create your first audio memo!
            </div>
          ) : (
            <div className="space-y-3">
              {recordings.map((recording) => (
                <div
                  key={recording.id}
                  className="flex items-center justify-between p-4 bg-slate-50 rounded-lg border"
                >
                  <div className="flex-1 min-w-0">
                    <h4 className="font-medium text-slate-900 truncate">
                      {recording.title}
                    </h4>
                    <div className="flex items-center space-x-4 mt-1 text-sm text-slate-500">
                      <span>Duration: {formatDuration(recording.duration || 0)}</span>
                      <span>‚Ä¢</span>
                      <span>{recording.createdAt ? format(new Date(recording.createdAt), "MMM dd, yyyy") : "No date"}</span>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => playRecording(recording)}
                      className="flex items-center space-x-1"
                    >
                      {playingId === recording.id ? (
                        <Square className="w-4 h-4" />
                      ) : (
                        <Play className="w-4 h-4" />
                      )}
                      <span>{playingId === recording.id ? "Stop" : "Play"}</span>
                    </Button>

                    {recording.blob && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          const audio = `data:audio/wav;base64,${recording.blob}`;
                          const link = document.createElement('a');
                          link.href = audio;
                          link.download = `${recording.title}.wav`;
                          link.click();
                        }}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                    )}

                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => deleteMutation.mutate(recording.id)}
                      className="text-red-600 hover:text-red-700 hover:bg-red-50"
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

